UPDATE verses SET es = "¿No has visto cómo tu Señor alarga la sombra? Si le hubiera agradado, podría haberla hecho fija. Entonces convertimos al sol en guía de la misma." WHERE ayatNumber = 46 AND chapterNumber = 25;
