UPDATE verses SET es = "Y que pasan la noche ante su Señor, postrados o de pie," WHERE ayatNumber = 65 AND chapterNumber = 25;
UPDATE verses SET es = "Y dicen: \"Señor nuestro, apar­ta de nosotros el castigo del Infierno; pues su castigo es un tormento do­loroso," WHERE ayatNumber = 66 AND chapterNumber = 25;
UPDATE verses SET es = "\"En verdad es malo como lu­gar de reposo y como morada”;" WHERE ayatNumber = 67 AND chapterNumber = 25;
