UPDATE verses SET es = "Quienes se arrepienten y hacen buenas <em>obras, </em>vuelven en verdad a Al‑lah con arrepentimiento <em>verda­dero;</em>" WHERE ayatNumber = 72 AND chapterNumber = 25;
