UPDATE verses SET es = "Éstos serán los recompensa­dos con un lugar de gloria <em>en el Paraíso, </em>porque fueron perseveran­tes, y en él serán recibidos con salu­tación y paz," WHERE ayatNumber = 76 AND chapterNumber = 25;
