UPDATE verses SET es = "“Id pues al Faraón y decidle: “Somos los Mensajeros del Señor de los mundos," WHERE ayatNumber = 17 AND chapterNumber = 26;
UPDATE verses SET es = "<em> “Para decirte que </em>envíes con nosotros a los hijos de Israel”." WHERE ayatNumber = 18 AND chapterNumber = 26;
