UPDATE verses SET es = "“Y éste es el favor por el que me reprochas; que has esclavizado a los hijos de Israel”." WHERE ayatNumber = 23 AND chapterNumber = 26;
UPDATE verses SET es = "El Faraón dijo: “¿Y quién es el Señor de los mundos?”." WHERE ayatNumber = 24 AND chapterNumber = 26;
