UPDATE verses SET es = "El<em> Faraón </em>respondió: “Si tomas a un Dios distinto de mí, cier­tamente te encerraré en prisión”." WHERE ayatNumber = 30 AND chapterNumber = 26;
UPDATE verses SET es = "<em>Moisés </em>dijo”: ¿A pesar de que te presente algo evidente?”." WHERE ayatNumber = 31 AND chapterNumber = 26;
UPDATE verses SET es = "<em>El Faraón</em> respondió: “Preséntalo, pues, si dices la verdad”." WHERE ayatNumber = 32 AND chapterNumber = 26;
UPDATE verses SET es = "Así pues, arrojó su cayado y ¡he ahí que se convirtió en una serpiente visible!" WHERE ayatNumber = 33 AND chapterNumber = 26;
UPDATE verses SET es = "Y extendió la mano, ¡y he aquí que apareció blanca para los espectadores!" WHERE ayatNumber = 34 AND chapterNumber = 26;
UPDATE verses SET es = "El<em> Faraón </em>dijo a los jefes que lo rodeaban: “En verdad es un mago hábil;" WHERE ayatNumber = 35 AND chapterNumber = 26;
UPDATE verses SET es = "“Intenta expulsaros de vuestra tierra con su magia. ¿Qué me aconsejáis ahora?”." WHERE ayatNumber = 36 AND chapterNumber = 26;
UPDATE verses SET es = "Respondieron: “Déjalos a un lado a él y a su hermano <em>durante</em> <em>algún tiempo</em> y envía pregoneros a las ciudades," WHERE ayatNumber = 37 AND chapterNumber = 26;
UPDATE verses SET es = "“Para que te traigan a los más hábiles brujos”." WHERE ayatNumber = 38 AND chapterNumber = 26;
UPDATE verses SET es = "Así pues, los magos se reunieron en la hora y el día fijados," WHERE ayatNumber = 39 AND chapterNumber = 26;
UPDATE verses SET es = "Y se dijo al pueblo: “¿Queréis reuniros <em>también,</em>" WHERE ayatNumber = 40 AND chapterNumber = 26;
UPDATE verses SET es = "“Para que sigamos a los magos si son ellos los vencedores?”." WHERE ayatNumber = 41 AND chapterNumber = 26;
UPDATE verses SET es = "<em></em>Y cuando llegaron los magos, dijeron al Faraón: “¿Tendremos una recompensa si somos los gana­dores?”." WHERE ayatNumber = 42 AND chapterNumber = 26;
UPDATE verses SET es = "Él dijo: “Sí, y en verdad es­taréis entre los favorecidos”." WHERE ayatNumber = 43 AND chapterNumber = 26;
