UPDATE verses SET es = "Entonces Moisés arrojó su cayado, y he ahí que se tragó todo lo que ellos habían fabricado." WHERE ayatNumber = 46 AND chapterNumber = 26;
UPDATE verses SET es = "Al punto los magos se sin­tieron impulsados a caer proster­nados;" WHERE ayatNumber = 47 AND chapterNumber = 26;
UPDATE verses SET es = "Dijeron: “Creemos en el Señor de los mundos," WHERE ayatNumber = 48 AND chapterNumber = 26;
UPDATE verses SET es = "“El Señor de Moisés y Aarón”." WHERE ayatNumber = 49 AND chapterNumber = 26;
UPDATE verses SET es = "<em>El Faraón </em>dijo: “¿Habéis creído en él antes de que os diera permiso? Sin duda que él es vuestro jefe y os ha enseñado la magia. Pero sabréis sus<em> consecuencias. </em>En ver­dad os cortaré las manos y los pies de lados opuestos, y ciertamente os crucificaré a todos”." WHERE ayatNumber = 50 AND chapterNumber = 26;
UPDATE verses SET es = "Dijeron: “No importa; pues retornaremos a nuestro Señor;" WHERE ayatNumber = 51 AND chapterNumber = 26;
UPDATE verses SET es = "“Esperamos que nuestro Señor nos perdone nuestros pecados, ya que somos los primeros entre los creyentes”." WHERE ayatNumber = 52 AND chapterNumber = 26;
UPDATE verses SET es = "Y revelamos a Moisés diciéndole: “Saca a Mis siervos de noche, pues ciertamente seréis perseguidos”." WHERE ayatNumber = 53 AND chapterNumber = 26;
UPDATE verses SET es = "Y el Faraón envió pregoneros a las ciudades, <em>que dijeron:</em>" WHERE ayatNumber = 54 AND chapterNumber = 26;
UPDATE verses SET es = "“Son un pequeño grupo," WHERE ayatNumber = 55 AND chapterNumber = 26;
UPDATE verses SET es = "“Y nos han ofendido;" WHERE ayatNumber = 56 AND chapterNumber = 26;
UPDATE verses SET es = "Y nosotros somos una multitud <em>perfectamente preparada y </em> vigilante”." WHERE ayatNumber = 57 AND chapterNumber = 26;
UPDATE verses SET es = "Por ello les expulsamos de jardines y manantiales," WHERE ayatNumber = 58 AND chapterNumber = 26;
UPDATE verses SET es = "De tesoros y de una morada honorable." WHERE ayatNumber = 59 AND chapterNumber = 26;
UPDATE verses SET es = "Así <em>fue en verdad</em>; y los entregamos como herencia a los hijos de Israel." WHERE ayatNumber = 60 AND chapterNumber = 26;
UPDATE verses SET es = "Y los persiguieron y los alcanzaron al salir el sol." WHERE ayatNumber = 61 AND chapterNumber = 26;
UPDATE verses SET es = "Y cuando los dos grupos se avistaron, los compañeros de Moisés dijeron: “Seremos ciertamente apresados”." WHERE ayatNumber = 62 AND chapterNumber = 26;
UPDATE verses SET es = "“¡No, <em>no habléis así</em>!”, dijo, “mi Señor está conmigo. Él me dirigirá con rectitud”." WHERE ayatNumber = 63 AND chapterNumber = 26;
UPDATE verses SET es = "Entonces revelamos a Moi­sés, <em>diciéndole:</em> Golpea el mar con tu cayado”. Y he aquí que se dividió y cada parte <em>parecía </em> una enorme montaña." WHERE ayatNumber = 64 AND chapterNumber = 26;
UPDATE verses SET es = "Y dejamos que los otros se aproximaran a ese lugar." WHERE ayatNumber = 65 AND chapterNumber = 26;
UPDATE verses SET es = "Y salvamos a Moisés y a quienes estaban con él." WHERE ayatNumber = 66 AND chapterNumber = 26;
UPDATE verses SET es = "Y luego ahogamos a los demás." WHERE ayatNumber = 67 AND chapterNumber = 26;
UPDATE verses SET es = "Aquí hay en verdad un Signo; pero la mayoría de éstos no creerán." WHERE ayatNumber = 68 AND chapterNumber = 26;
UPDATE verses SET es = "Y en verdad tu Señor: Él es el Poderoso, el Misericordioso." WHERE ayatNumber = 69 AND chapterNumber = 26;
UPDATE verses SET es = "Nárrales la historia de Abraham." WHERE ayatNumber = 70 AND chapterNumber = 26;
