UPDATE verses SET es = "Respondieron: “Adoramos a ídolos y continuamos siendo devotos de ellos”." WHERE ayatNumber = 72 AND chapterNumber = 26;
UPDATE verses SET es = "Él dijo: “¿Pueden escucharos cuando <em>los </em>llamáis?" WHERE ayatNumber = 73 AND chapterNumber = 26;
UPDATE verses SET es = "“¿U <em>os</em> benefician o perjudican?”." WHERE ayatNumber = 74 AND chapterNumber = 26;
UPDATE verses SET es = "Dijeron: “No, pero vimos a nuestros padres hacer lo mismo”." WHERE ayatNumber = 75 AND chapterNumber = 26;
UPDATE verses SET es = "Él dijo: “¿Qué pensáis de lo que habéis estado adorando" WHERE ayatNumber = 76 AND chapterNumber = 26;
UPDATE verses SET es = "“Vosotros y vuestros padres antes que <em>vosotros</em>?," WHERE ayatNumber = 77 AND chapterNumber = 26;
UPDATE verses SET es = "<em>“Todos </em>son enemigos míos, excepto el Señor de los mundos;" WHERE ayatNumber = 78 AND chapterNumber = 26;
UPDATE verses SET es = "“Quien me creó y es Quien me guía;" WHERE ayatNumber = 79 AND chapterNumber = 26;
