UPDATE verses SET es = "“Encontré a una mujer que los gobierna, a la que se le ha concedido de todo y tiene un trono poderoso;" WHERE ayatNumber = 24 AND chapterNumber = 27;
UPDATE verses SET es = "“Encontré a ella y a su pueblo adorando al sol en lugar de a Al‑lah; y Satanás hizo que sus obras <em>aparecieran </em>bellas a sus ojos, desviándolos así del camino <em>recto, </em>para que no siguieran la guía;" WHERE ayatNumber = 25 AND chapterNumber = 27;
UPDATE verses SET es = "<em>“Y Satanás les impuso </em>no adorar a Al‑lah, que es Quien saca a la luz lo que está oculto en los cielos y la tierra, y sabe lo que ocultáis y manifestáis." WHERE ayatNumber = 26 AND chapterNumber = 27;
UPDATE verses SET es = "<em></em>“¡Al‑lah! no hay ningún dios fuera de Él, el Señor del Trono Po­deroso”." WHERE ayatNumber = 27 AND chapterNumber = 27;
UPDATE verses SET es = "<em> Salomón </em>dijo: 'Veremos si has dicho la verdad o si eres uno de los que mienten;" WHERE ayatNumber = 28 AND chapterNumber = 27;
UPDATE verses SET es = "“Ve con esta carta mía y pre­séntasela; apártate luego de ellos y comprueba qué <em>respuesta </em>dan”." WHERE ayatNumber = 29 AND chapterNumber = 27;
UPDATE verses SET es = "<em>La Reina </em>dijo: “Oh, jefes, se me ha entregado una carta noble;" WHERE ayatNumber = 30 AND chapterNumber = 27;
UPDATE verses SET es = "“Es de Salomón, y dice: “En el nombre de Al‑lah, el Clemente, el Misericordioso;" WHERE ayatNumber = 31 AND chapterNumber = 27;
UPDATE verses SET es = "“No os comportéis orgullosa­mente ante mí; antes bien venid a mí sumisos”," WHERE ayatNumber = 32 AND chapterNumber = 27;
UPDATE verses SET es = "Ella dijo: “Oh, jefes, aconsejadme en el asunto que se me plantea. Nunca he decidido asunto alguno mientras no me hayáis presentado vuestras observaciones”." WHERE ayatNumber = 33 AND chapterNumber = 27;
UPDATE verses SET es = "Respondieron: “Poseemos po­der. Y somos guerreros implacables, pero a ti corresponde de­cidir; considera pues lo que has de ordenarnos”." WHERE ayatNumber = 34 AND chapterNumber = 27;
UPDATE verses SET es = "Ella respondió: “En verdad, cuando los reyes entran en un país, lo despojan y convierten en misera­bles a quienes ocupan los puestos más altos entre ellos. Y así actuarán;" WHERE ayatNumber = 35 AND chapterNumber = 27;
UPDATE verses SET es = "“Pero voy a enviarles un pre­sente y a esperar qué <em>respuesta </em>traen de nuevo los emisarios”." WHERE ayatNumber = 36 AND chapterNumber = 27;
UPDATE verses SET es = "Así, cuando llegó a Salomón ­<em>el embajador de la Reina, </em> dijo: “¿Pre­tendéis ayudarme con <em>vuestra </em>riqueza? Pero lo que Al‑lah me ha dado es mejor que lo que os ha dado a vosotros. No, pero os rego­cijáis con vuestro regalo;" WHERE ayatNumber = 37 AND chapterNumber = 27;
UPDATE verses SET es = "“Vuelve a ellos, pues en ver­dad iremos a ellos con ejércitos con­tra los que no tendrán poder al­guno y los expulsaremos de allí envilecidos, y serán humillados”." WHERE ayatNumber = 38 AND chapterNumber = 27;
UPDATE verses SET es = "Dijo: “Oh, nobles, ¿cuál de vosotros me traerá su trono antes de que me lleguen sometidos?”." WHERE ayatNumber = 39 AND chapterNumber = 27;
UPDATE verses SET es = "Uno de los más fornidos de entre los Yinn dijo: “Te lo presentaré antes de que te levantes de tu cam­pamento; Pues en verdad poseo poder para ello y <em>soy </em>digno de con­fianza”." WHERE ayatNumber = 40 AND chapterNumber = 27;
UPDATE verses SET es = "Dijo uno que tenía conocimiento del Libro: “Te lo traeré en menos de un abrir y cerrar de ojos”. Y cuando lo vio ante él, dijo: “Esto es por la gracia de mi Señor, para probarme si soy agradecido o ingrato. ues quien es agradecido lo es ciertamente para el bien de su <em>propia </em>alma; mas quien es desagra­decido, sepa que, en verdad, mi Señor es Auto‑Suficiente, Generoso”." WHERE ayatNumber = 41 AND chapterNumber = 27;
UPDATE verses SET es = "Dijo: “Haced que su trono aparezca corriente para ella, y veamos si sigue el camino recto o si es <em>una </em>de quienes no siguen el ca­mino recto”." WHERE ayatNumber = 42 AND chapterNumber = 27;
UPDATE verses SET es = "Y cuando ella hubo llegado, se le dijo: “¿Es tu trono como éste?” Ella respondió: “Es como se fuese el mismo. Nos había llegado el conocimiento antes de esto, y ya nos hemos sometido”." WHERE ayatNumber = 43 AND chapterNumber = 27;
UPDATE verses SET es = "Mas lo que ella solía adorar fuera de Al‑lah le había impedido <em>creer; </em>pues procedía de un pueblo incrédulo." WHERE ayatNumber = 44 AND chapterNumber = 27;
UPDATE verses SET es = "Se le dijo: “Entra en el pa­lacio”. Y cuando ella lo vio, creyó que ra una gran extensión de agua y se descubrió las piernas. <em>Salomón </em>dijo: “Es un palacio pavimentado con losas de cristal”. Ella dijo: “Mi Señor, en verdad he sido injusta con mi alma; ahora me someto con Sa­lomón a Al‑lah, el Señor de los mundos”." WHERE ayatNumber = 45 AND chapterNumber = 27;
UPDATE verses SET es = "Enviamos a los Zamud a su hermano Salih, <em>quien dijo: </em> “Adorad a Al‑lah”. Pero he aquí se dividieron en dos grupos que disputaban entre sí." WHERE ayatNumber = 46 AND chapterNumber = 27;
UPDATE verses SET es = "Él dijo: “Oh, pueblo mío, ¿por qué deseas apresurarte al mal en vez de hacerlo al bien?, ¿Por qué no pedís pues perdón a Al‑lah para que se os muestre misericordia?”." WHERE ayatNumber = 47 AND chapterNumber = 27;
UPDATE verses SET es = "Respondieron: “Te auguramos el mal a ti y a quienes están contigo”. Él respondió: “<em>La causa</em> <em>de </em>vuestra mala fortuna está en Al‑lah. Así es, pues no sois más que un pueblo que está a prueba”." WHERE ayatNumber = 48 AND chapterNumber = 27;
UPDATE verses SET es = "Y había en la ciudad un grupo de nueve <em>personas </em>que corrompían el país y no estaban dispuestas a reformarse." WHERE ayatNumber = 49 AND chapterNumber = 27;
UPDATE verses SET es = "Dijeron: “Juraos mutuamente por Al‑lah que lo atacaremos ciertamente a él y a su familia por la noche, y después diremos a su here­dero: “No hemos sido testigos de la destrucción de su familia, y en verdad somos veraces”." WHERE ayatNumber = 50 AND chapterNumber = 27;
UPDATE verses SET es = "Y tramaron un plan pero Nosotros preparamos nuestro plan <em>en contra</em>, aunque no se percataron de ello." WHERE ayatNumber = 51 AND chapterNumber = 27;
UPDATE verses SET es = "¡Ved pues qué <em>malo</em> fue el resultado de su plan! En verdad, los destruimos totalmente, a ellos y a su pueblo, todos juntos." WHERE ayatNumber = 52 AND chapterNumber = 27;
UPDATE verses SET es = "Y allá yacen sus casas en ruinas, debido a sus malas acciones. Ahí hay en verdad un Signo para un pueblo que posea conocimiento." WHERE ayatNumber = 53 AND chapterNumber = 27;
UPDATE verses SET es = "Y salvamos a quienes cre­yeron en Dios y lo temieron." WHERE ayatNumber = 54 AND chapterNumber = 27;
UPDATE verses SET es = "<em> Acuérdate de </em>Lot, cuando dijo a su pueblo: “¿Seguís cometiendo abominaciones aunque veis <em>su maldad?</em>" WHERE ayatNumber = 55 AND chapterNumber = 27;
UPDATE verses SET es = "“¡Cómo! ¿Os acercáis con lu­juria a los hombres en vez de a las mujeres? No, en verdad, sois un pue­blo inconsciente de las consecuencias”." WHERE ayatNumber = 56 AND chapterNumber = 27;
UPDATE verses SET es = "Mas la respuesta de su pueblo no fue otra que <em>incitar a la gente</em> y decir: “Ex­pulsad de vuestra ciudad a los seguidores de Lot. Son, en verdad, gente que pretende ser pura”." WHERE ayatNumber = 57 AND chapterNumber = 27;
UPDATE verses SET es = "Por eso lo salvamos a él y a su familia, excepto a su esposa; pues decretamos que fuese de los que que­daron detrás." WHERE ayatNumber = 58 AND chapterNumber = 27;
UPDATE verses SET es = "<em></em>Y desencadenamos sobre ellos un aguacero; y la lluvia fue muy mala para los que habían sido adver­tidos." WHERE ayatNumber = 59 AND chapterNumber = 27;
UPDATE verses SET es = "Di: “Toda alabanza corres­ponde a Al‑lah, y la paz sea con los siervos Suyos a quienes Él ha ele­gido. ¿Es mejor Al‑lah o lo que asocian <em>a</em> <em>Él?</em>”." WHERE ayatNumber = 60 AND chapterNumber = 27;
UPDATE verses SET es = "O, ¿quién creó los cielos y la tierra, y quién os envió agua desde el cielo con la que hacemos que crezcan huertos espléndidos? Vosotros no podríais hacer que sus árboles crecieran. ¿Existe acaso algún dios fuera de Al‑lah? No; son un pueblo que atribuyen partícipes a Al‑lah." WHERE ayatNumber = 61 AND chapterNumber = 27;
UPDATE verses SET es = "O, ¿Quién hizo de la tierra un lugar de descanso, colocó ríos en su mitad y dispuso en ella firmes mon­tañas, ubicando una barrera entre las dos aguas? ¿Existe algún dios fuera de Al‑lah? No, la mayoría de ellos no lo saben." WHERE ayatNumber = 62 AND chapterNumber = 27;
UPDATE verses SET es = "O, ¿Quién responde a la per­sona afligida cuando Le invoca? ¿ <em>quién os</em> libra del mal y os convierte en sucesores en la tierra? ¿Existe acaso algún dios fuera de Al‑lah? Qué poco es lo que reflexionáis." WHERE ayatNumber = 63 AND chapterNumber = 27;
UPDATE verses SET es = "O, ¿Quién os guía en toda clase de tinieblas de la tierra y del mar, y Quién envía los vientos como buena nueva antes de Su mise­ricordia? ¿Existe algún dios fuera de Al‑lah? Ensalzado sea Al‑lah por encima de lo que <em>Le </em>asocian." WHERE ayatNumber = 64 AND chapterNumber = 27;
UPDATE verses SET es = "O ¿Quién origina la creación, repitiéndola después y Quién os provee del cielo y de la tierra? ¿Existe algún dios fuera de Al‑lah? Diles: “Presentad vuestra prueba, si sois veraces”." WHERE ayatNumber = 65 AND chapterNumber = 27;
UPDATE verses SET es = "Diles: “Nadie en los cielos y en la tierra conoce lo oculto, salvo Al‑lah; e ignoran cuándo serán resu­citados”." WHERE ayatNumber = 66 AND chapterNumber = 27;
UPDATE verses SET es = "¡No! su conocimiento ha lle­gado a su fin respecto al Más Allá; ¡No! <em>en verdad, </em>dudan sobre él; ¡No! son ciegos respecto a él." WHERE ayatNumber = 67 AND chapterNumber = 27;
UPDATE verses SET es = "Mas los incrédulos dicen: “¡Cómo! ¿Cuando nosotros y nues­tros adres nos hayamos convertido en polvo seremos realmente resuci­tados <em>de nuevo?</em>" WHERE ayatNumber = 68 AND chapterNumber = 27;
UPDATE verses SET es = "“En verdad se nos prometió esto antes: a nosotros y a nuestros padres; no son sino historias de los antiguos”." WHERE ayatNumber = 69 AND chapterNumber = 27;
UPDATE verses SET es = "Diles: “¡Viajad por la tierra y ved qué <em>malo </em>fue el fin de los pe­cadores!”." WHERE ayatNumber = 70 AND chapterNumber = 27;
UPDATE verses SET es = "No te aflijas por ellos ni te angusties por lo que traman." WHERE ayatNumber = 71 AND chapterNumber = 27;
UPDATE verses SET es = "Mas dicen: “¿Cuándo <em>se cumplirá </em>esta promesa, si sois ve­races?”." WHERE ayatNumber = 72 AND chapterNumber = 27;
UPDATE verses SET es = "Diles: “Podría ocurrir que, una parte del <em>castigo prometido</em> que <em>arrogantemente</em> demandáis que os sobrevenga, ya esté tras vuestros talones”." WHERE ayatNumber = 73 AND chapterNumber = 27;
UPDATE verses SET es = "En verdad, tu Señor es Cle­mente con los humanos, pero la ma­yoría de ellos no son agradecidos." WHERE ayatNumber = 74 AND chapterNumber = 27;
UPDATE verses SET es = "En verdad, tu Señor sabe lo<strong> </strong>que ocultan sus pechos y lo que manifiestan." WHERE ayatNumber = 75 AND chapterNumber = 27;
UPDATE verses SET es = "Pues no hay nada oculto en el cielo y en la tierra que no esté <em>re­gistrado </em>en un Libro manifiesto." WHERE ayatNumber = 76 AND chapterNumber = 27;
UPDATE verses SET es = "Ciertamente este Corán ex­plica a los Hijos de Israel la mayoría de aquello en lo que disputan." WHERE ayatNumber = 77 AND chapterNumber = 27;
UPDATE verses SET es = "En verdad, es una guía y una misericordia para los creyentes." WHERE ayatNumber = 78 AND chapterNumber = 27;
UPDATE verses SET es = "Tu Señor decidirá ciertamente entre ellos con Su juicio, pues Él es el Poderoso, el Omnisciente." WHERE ayatNumber = 79 AND chapterNumber = 27;
UPDATE verses SET es = "Deposita pues tu confianza en Al‑lah; en verdad <em>estás apoyado </em>en una verdad manifiesta." WHERE ayatNumber = 80 AND chapterNumber = 27;
UPDATE verses SET es = "Por cierto que no puedes hacer que los muertos oigan, ni puedes hacer que los sordos escuchen la llamada, cuando dan la espalda en retirada." WHERE ayatNumber = 81 AND chapterNumber = 27;
UPDATE verses SET es = "Tampoco puedes guiar al ciego sacándolo de su error. Sólo puedes hacer que te escuchen quienes creen en Nuestros Signos, y se hacen obedientes." WHERE ayatNumber = 82 AND chapterNumber = 27;
UPDATE verses SET es = "Mas cuando se dicte sentencia contra ellos, haremos brotar para ellos una criatura de la tierra, que los herirá porque las gentes no cre­yeron en Nuestros Signos." WHERE ayatNumber = 83 AND chapterNumber = 27;
UPDATE verses SET es = "<em>Recuérdales </em>el día en el que reuniremos de cada uno de los pueblos a un grupo de entre aquellos que recha­zaron Nuestros Signos, y serán colo­cados en bandas <em>separadas.</em>" WHERE ayatNumber = 84 AND chapterNumber = 27;
UPDATE verses SET es = "Hasta que, cuando lleguen, Él les diga: “¿Rechazasteis <em>rápidamente</em> Mis Signos, sin haber logrado pleno conocimiento de los mismos? <em>Si no es así</em> ¿qué es lo que estabais haciendo?”." WHERE ayatNumber = 85 AND chapterNumber = 27;
UPDATE verses SET es = "Y la sentencia caerá sobre ellos porque obraron injustamente, y no serán capaces de hablar." WHERE ayatNumber = 86 AND chapterNumber = 27;
UPDATE verses SET es = "¿No han visto acaso que he­mos hecho la noche para que puedan descansar en ella, y el día que da la vista? En eso hay ciertamente Signos para los hombres que creen." WHERE ayatNumber = 87 AND chapterNumber = 27;
UPDATE verses SET es = "El día en el que suene la trompeta; quienes se encuentren en los cielos y en la tierra serán presa del terror, salvo aquel a quien Al‑lah le agrade. Y todos comparecerán ante Él, humillados." WHERE ayatNumber = 88 AND chapterNumber = 27;
UPDATE verses SET es = "Y ves las montañas imaginando que están quietas, pero flotan como flotan las nubes. Así es la obra de Al‑lah, Quien lo ha hecho todo firme y fuerte. En verdad, Él sabe muy bien lo que hacéis." WHERE ayatNumber = 89 AND chapterNumber = 27;
UPDATE verses SET es = "Quien haga una buena ac­ción tendrá una recompensa mejor que ésa, y estará libre del terror en ese día." WHERE ayatNumber = 90 AND chapterNumber = 27;
UPDATE verses SET es = "Mas quienes practiquen el mal serán arrojados de bruces al Fuego: “¿No sois acaso retribuidos por lo que habéis hecho?”." WHERE ayatNumber = 91 AND chapterNumber = 27;
UPDATE verses SET es = "<em>Diles: </em>“Se me ha ordenado únicamente servir al Señor de esta ciudad que Él ha hecho sagrada, y a Él pertenecen todas las cosas; y se me ha ordenado ser de aquellos que se someten <em>a Dios,</em>" WHERE ayatNumber = 92 AND chapterNumber = 27;
UPDATE verses SET es = "“Y que recite el Corán”. Así, quien sigue la guía la sigue nicamente por <em>el bien</em> <em>de</em> su propia alma; y <em>a quien</em> se extravía, dile: “No soy más que un amonestador”." WHERE ayatNumber = 93 AND chapterNumber = 27;
UPDATE verses SET es = "Y di: “Toda alabanza pertenece a Al-lah; Él os mostrará pronto Sus Signos, y los conoceréis”. Pues vuestro Señor no desconoce lo que hacéis." WHERE ayatNumber = 94 AND chapterNumber = 27;
UPDATE verses SET es = "En el nombre de Al‑lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 28;
UPDATE verses SET es = "Ta Sin Mim." WHERE ayatNumber = 2 AND chapterNumber = 28;
UPDATE verses SET es = "Éstos son los versículos del Libro manifiesto." WHERE ayatNumber = 3 AND chapterNumber = 28;
UPDATE verses SET es = "Te narramos <em>una parte </em>de la historia de Moisés y el Faraón con veracidad, <em>a beneficio de </em>un pueblo creyente." WHERE ayatNumber = 4 AND chapterNumber = 28;
UPDATE verses SET es = "En verdad, el Faraón se com­portó con arrogancia en la tierra, y dividió a sus gentes en grupos: intentó debilitar a un grupo de ellos, matando a sus hijos y dejando vivir a sus mujeres. En verdad, fue uno de los corruptores." WHERE ayatNumber = 5 AND chapterNumber = 28;
UPDATE verses SET es = "Y deseamos mostrar Nuestro favor a quienes habían sido considerados ébiles en la tierra, convirtiéndolos en jefes y hacién­dolos herederos <em>de Nuestros favores,</em>" WHERE ayatNumber = 6 AND chapterNumber = 28;
UPDATE verses SET es = "Y establecerlos en la tierra, y mostrar al Faraón y a Haman y a sus ejércitos lo que temían de ellos." WHERE ayatNumber = 7 AND chapterNumber = 28;
UPDATE verses SET es = "Enviamos una revelación a la madre de Moisés <em>diciéndole: </em> “Ama­mántalo; y cuando temas por él, arrójalo al río y no temas ni te ngusties; pues te lo devolveremos y lo convertiremos en <em>uno de </em>los Men­sajeros”." WHERE ayatNumber = 8 AND chapterNumber = 28;
UPDATE verses SET es = "La familia del Faraón lo re­cogió <em>sin conocer</em><em>apenas </em>que se convertiría en enemigo suyo y en <em>fuente de </em>des­dicha. En verdad, el Faraón y Ha­man y sus ejércitos fueron inicuos." WHERE ayatNumber = 9 AND chapterNumber = 28;
UPDATE verses SET es = "Y la mujer del Faraón dijo: <em>“Será </em>para mí y para ti la alegría de nuestros ojos. No lo mates. Es posible que nos sea útil o ue podamos adoptarlo como hijo”. Y no se dieron cuenta de <em>las consecuencias de todo ello.</em>" WHERE ayatNumber = 10 AND chapterNumber = 28;
UPDATE verses SET es = "Y el corazón de la madre de Moisés quedó libre <em>de ansiedad. </em>Hubiera revelado <em>su</em> <em>identidad</em> de no ser porque forta­lecimos su corazón para que fuese de los creyentes <em>firmes.</em>" WHERE ayatNumber = 11 AND chapterNumber = 28;
UPDATE verses SET es = "Y dijo a su hermana: “Sí­guelo”. Lo vigiló pues desde lejos; y ellos no lo advirtieron." WHERE ayatNumber = 12 AND chapterNumber = 28;
UPDATE verses SET es = "De antemano hicimos que rechazara a las nodrizas; por lo que dijo: “¿Queréis que os indique una familia que lo criará para vosotros y serán sus custodios sinceros?”." WHERE ayatNumber = 13 AND chapterNumber = 28;
UPDATE verses SET es = "Así lo devolvimos a su madre para delicia de su vista, para que no se afligiera y supiera que la promesa de Al‑lah es verdadera. Pero la ma­yoría de ellos lo desconocen." WHERE ayatNumber = 14 AND chapterNumber = 28;
UPDATE verses SET es = "Cuando alcanzó la plena madurez y se hizo fuerte, le concedimos sabiduría y conocimiento; así recom­pensamos a quienes hacen el bien." WHERE ayatNumber = 15 AND chapterNumber = 28;
UPDATE verses SET es = "Entró en la ciudad cuando la gente aún dormía; encontrando allí a dos hombres que peleaban: uno de su propio pueblo y otro de sus enemigos. El miembro de su propio pueblo le pidió ayuda contra el que perte­necía a sus enemigos. Moisés le golpeó entonces con su puño, causán­dole <em>así </em>la muerte. Dijo: “Esto es obra de Satanás; él es en verdad un ene­migo, un engañador manifiesto”." WHERE ayatNumber = 16 AND chapterNumber = 28;
UPDATE verses SET es = "Dijo: “Mi Señor, he obrado mal, perdóname pues”. Y Él lo perdonó; Él es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 17 AND chapterNumber = 28;
UPDATE verses SET es = "Dijo: “Mi Señor, por haberme concedido Tu favor, nunca seré so­corredor de los culpables”." WHERE ayatNumber = 18 AND chapterNumber = 28;
UPDATE verses SET es = "Y comenzó el día en la ciudad caminando con temor y aprensión; y he hí que aquel que pidió su ayuda el día anterior le gritó <em>de nuevo </em>pidiéndole ayuda. Moisés le dijo: “Eres en verdad un transgresor evidente”." WHERE ayatNumber = 19 AND chapterNumber = 28;
UPDATE verses SET es = "Y cuando se decidió a castigar al hombre que era enemigo de ambos, dijo: “Oh, Moisés, ¿piensas matarme como mataste al hombre de ayer? Sólo aspiras a convertirte en un tirano en el país, y no deseas ser un pacificador”." WHERE ayatNumber = 20 AND chapterNumber = 28;
UPDATE verses SET es = "Y acudió apresuradamente un hombre de la parte más alejada de la ciudad, y dijo: “Oh, Moisés, en ver­dad los jefes conspiran contra ti para matarte. Aléjate pues; en verdad de­seo lo mejor para ti”." WHERE ayatNumber = 21 AND chapterNumber = 28;
UPDATE verses SET es = "Salió pues de ella, temeroso y receloso. Dijo: “Señor mío, líbrame de los hombres injustos”." WHERE ayatNumber = 22 AND chapterNumber = 28;
UPDATE verses SET es = "Y cuando dirigió su rostro a Madián, dijo: “Espero que mi Señor me guíe por el camino recto”." WHERE ayatNumber = 23 AND chapterNumber = 28;
UPDATE verses SET es = "Cuando llegó al agua de Madián, encontró allí a un grupo de hombres, que abrevaban <em>a su ga­nado. </em>Y tras ellos vio a dos mujeres que guardaban <em>su</em> <em>ganado. </em>Les dijo:”¿Qué os ocurre?”. Ellas respondieron: “No podemos abrevar a nuestros animales mientras que los pastores no se lleven <em>los suyos</em>, y nuestro padre es un hombre muy anciano”." WHERE ayatNumber = 24 AND chapterNumber = 28;
UPDATE verses SET es = "Así pues abrevó a <em>su</em> <em>ganado </em>en lugar de ellas. A continuación se retiró a la sombra y dijo: “En verdad, Señor mío, soy un mendigo de todo lo bueno que me concedes”." WHERE ayatNumber = 25 AND chapterNumber = 28;
UPDATE verses SET es = "Una de dos <em>mujeres </em>se acercó a él, andando recatadamente. ijo: “Mi padre te llama para recom­pensarte por habernos abrevado <em>nuestro ganado”. </em>Así, cuando llegó a él y le contó la historia, dijo: “No temas; te has librado de un pueblo injusto”." WHERE ayatNumber = 26 AND chapterNumber = 28;
UPDATE verses SET es = "Una de las dos <em>mujeres </em>dijo: “Oh, padre mío, contrátalo; pues el mejor hombre al que puedes dar empleo es el que es fuerte y digno de confianza”." WHERE ayatNumber = 27 AND chapterNumber = 28;
UPDATE verses SET es = "Le dijo: “Pienso casar contigo a una de estas dos hijas mías a ondición de que me sirvas durante ocho años. Pero si completas diez <em>años,</em> será por tu propia voluntad. No quisiera imponerte nada exce­sivo. Comprobarás, si lo quiere Al-­lah, que soy de los justos”." WHERE ayatNumber = 28 AND chapterNumber = 28;
UPDATE verses SET es = "Respondió: “Esto queda <em>convenido </em>entre tú y yo. En cualquiera de los dos plazos que cumpla, no habrá injusticia para mí; pues Al‑lah es Testigo de lo que decimos”." WHERE ayatNumber = 29 AND chapterNumber = 28;
UPDATE verses SET es = "Y cuando Moisés hubo cum­plido el plazo y viajaba con su fa­milia, percibió un fuego en la direc­ción del Monte. Dijo a su familia: Aguardad, veo un fuego; tal vez os traiga alguna información <em>útil </em>de él, o un tizón ardiendo del fuego con el que calentaros”." WHERE ayatNumber = 30 AND chapterNumber = 28;
UPDATE verses SET es = "Cuando hubo llegado a él, fue llamado <em>por una voz</em> del lado de­recho del Valle, en el lugar ben­dito, desde un árbol: “Oh, Moisés, en verdad Yo <em>y sólo</em> Yo, soy Al‑lah, el Señor de los mundos”;" WHERE ayatNumber = 31 AND chapterNumber = 28;
UPDATE verses SET es = "<em></em>Y<em> dijo también: </em>“Arroja tu cayado”. Y cuando lo vio moverse como si fuera una serpiente, huyó dándose la vuelta y no esperó. “Oh, Moisés, acércate y no temas; en ver­dad eres uno de los que están se­guros." WHERE ayatNumber = 32 AND chapterNumber = 28;
UPDATE verses SET es = "“Mete tu mano en tu manto, cerca del pecho; saldrá blanca in ningún <em>efecto </em>maligno, y vuelve tu brazo hacia ti mismo <em>para que estés libre </em>del temor. Éstas <em>serán </em>dos prue­bas de tu Señor ante el Faraón y sus jefes. En verdad son un pueblo re­belde”." WHERE ayatNumber = 33 AND chapterNumber = 28;
UPDATE verses SET es = "Él dijo: Mi Señor, he ma­tado a uno de ellos, y temo que me maten;" WHERE ayatNumber = 34 AND chapterNumber = 28;
UPDATE verses SET es = "“Y mi hermano Aarón es más elocuente que yo hablando; envíalo por tanto conmigo como ayudante para que sea testigo de mi verdad. Temo que me acusen de falsedad”." WHERE ayatNumber = 35 AND chapterNumber = 28;
UPDATE verses SET es = "<em>Dios</em> dijo: “Fortaleceremos tu brazo con tu hermano, y os da­remos poder a los dos para que nunca puedan alcanzaros. <em>Id </em> con Nuestros Signos. Vosotros dos y quienes os sigan serán los vencedores”." WHERE ayatNumber = 36 AND chapterNumber = 28;
UPDATE verses SET es = "Mas cuando Moisés llegó a ellos con Nuestros Signos evidentes, dijeron: “Esto no es más que brujería inventada, ya que nunca hemos oído <em>nada similar a </em>esto entre nuestros antepasados”." WHERE ayatNumber = 37 AND chapterNumber = 28;
UPDATE verses SET es = "Moisés dijo: “Mi Señor sabe mejor que nadie quién ha traído Su guía y de quienes será la recompensa de la morada <em>final. </em>En verdad, los injustos nunca prosperarán”." WHERE ayatNumber = 38 AND chapterNumber = 28;
UPDATE verses SET es = "El Faraón dijo: “Oh, jefes, no conozco para vosotros otro dios dis­tinto de mí; cuéceme pues <em>ladrillos de </em>arcilla, Oh, Hamán, y constrúyeme una torre, para que alcance a ver al Dios de Moisés, aunque creo que él es uno de los embusteros”." WHERE ayatNumber = 39 AND chapterNumber = 28;
UPDATE verses SET es = "Él y sus ejércitos se compor­taron arrogantemente en la tierra, sin justificación alguna. Y pensaron que nunca serían devueltos a Nosotros." WHERE ayatNumber = 40 AND chapterNumber = 28;
UPDATE verses SET es = "Así pues, los atrapamos a él y a sus huestes, lanzándolos al mar. ¡Ved, pues, qué <em>desgraciado </em>fue el fin de los inicuos!" WHERE ayatNumber = 41 AND chapterNumber = 28;
UPDATE verses SET es = "Los convertimos en dirigentes que invitaron <em>al pueblo </em>al Fuego; y en el Día de la Resurrección no recibirán ayuda." WHERE ayatNumber = 42 AND chapterNumber = 28;
UPDATE verses SET es = "Hicimos que la maldición los siguiera en este mundo; y en el Día de la Resurrección, estarán entre los privados de <em>todo </em>bien." WHERE ayatNumber = 43 AND chapterNumber = 28;
UPDATE verses SET es = "Y entregamos a Moisés el Libro, después de que destruyéramos las generaciones anteriores, como <em>fuente de </em>discernimiento para los hombres, y como guía y misericordia, para que reflexionen." WHERE ayatNumber = 44 AND chapterNumber = 28;
UPDATE verses SET es = "Tú no estabas en la ladera occidental <em>del Monte </em>cuando reve­lamos la orden a Moisés, ni fuiste uno de los testigos." WHERE ayatNumber = 45 AND chapterNumber = 28;
UPDATE verses SET es = "Pero hicimos surgir varias ge­neraciones <em>después de Moisés, </em>y la vida se prolongó para ellos. Y tú no eras habitante del pueblo de Madián, para recitarles Nuestros Signos; sino que fuimos Nosotros Quienes enviamos a los Mensajeros." WHERE ayatNumber = 46 AND chapterNumber = 28;
UPDATE verses SET es = "Tampoco estabas en la ladera del Monte cuando convocamos. Pero <em>te hemos enviado </em>como miseri­cordia de tu Señor para que ad­viertas a un pueblo al que no había llegado Amonestador ninguno antes de ti, a fin de que reflexionen." WHERE ayatNumber = 47 AND chapterNumber = 28;
UPDATE verses SET es = "Y por qué no dicen, cuando la desgracia cae sobre ellos como resultado de sus propias acciones: “Señor Nuestro, ¿por qué no nos enviaste un Mensajero para que hubiéramos podido seguir Tus signos y ser de los creyentes?”." WHERE ayatNumber = 48 AND chapterNumber = 28;
UPDATE verses SET es = "Pero cuando les llegó Nuestra verdad, dijeron: “¿Por qué no se le ha dado lo mismo que se le dio a Moisés?”. ¿Acaso no rechazaron lo que se le dio a Moisés antes? Dicen: “<em>Sólo</em> dos Magos que se ayudan mutuamente”. Y afirman: “Les rechazamos por completo”." WHERE ayatNumber = 49 AND chapterNumber = 28;
UPDATE verses SET es = "Diles: Traed pues un Libro de Al‑lah que sea mejor guía que <em>estos </em>dos -<em>la Torah y el Corán</em>- para que lo siga, si sois verídicos”." WHERE ayatNumber = 50 AND chapterNumber = 28;
UPDATE verses SET es = "Pero si te responden que no, sabe entonces que sólo siguen sus malas inclinaciones. ¿Hay alguien que yerre más que quien sigue sus inclinaciones perversas sin guía al­guna de Al‑lah? en verdad, Al‑lah no guía a los injustos." WHERE ayatNumber = 51 AND chapterNumber = 28;
UPDATE verses SET es = "En verdad, les hemos transmitido cuidadosamente la Palabra, para que sean advertidos." WHERE ayatNumber = 52 AND chapterNumber = 28;
UPDATE verses SET es = "Aquellos a quienes dimos el libro antes de éste: ésos creen en él;" WHERE ayatNumber = 53 AND chapterNumber = 28;
UPDATE verses SET es = "Y cuando se les recita, dicen: “Creemos en él. Ciertamente es la verdad de nuestro Señor. En verdad, incluso antes de él nos habíamos sometido a <em>Dios</em>”." WHERE ayatNumber = 54 AND chapterNumber = 28;
UPDATE verses SET es = "Éstos recibirán su recompensa dos veces, por haber sido perseve­rantes y haber combatido el mal con el bien, y gastado de lo que les dimos." WHERE ayatNumber = 55 AND chapterNumber = 28;
UPDATE verses SET es = "Cuando escuchan palabras vanas, se apartan de ellas y dicen: “Para nosotros nuestras obras y para vosotros las vuestras. La paz sea con vosotros. No buscamos a los igno­rantes”." WHERE ayatNumber = 56 AND chapterNumber = 28;
UPDATE verses SET es = "En verdad, no conseguirás guiar a quien amas; pero Al‑lah guía quien le place; pues Él conoce mejor aquellos que aceptarían la guía." WHERE ayatNumber = 57 AND chapterNumber = 28;
UPDATE verses SET es = "Ellos dicen: “Si siguiéramos la guía contigo, seríamos expulsa­dos de nuestro país”. ¿Acaso no hemos establecido para ellos un san­tuario eguro al que son llevados los frutos de todas las cosas, <em>como </em>pro­visión Nuestra? Mas la mayoría de ellos lo desconocen." WHERE ayatNumber = 58 AND chapterNumber = 28;
UPDATE verses SET es = "¡Cuántas viviendas que vi­vían en la exultación, por su <em> estilo de </em>vida, hemos destruido! Y éstos son sus hogares que no han vuelto a ser habi­tados tras ellos salvo por escaso tiem­po. Somos Nosotros Quienes nos convertimos en los Herederos." WHERE ayatNumber = 59 AND chapterNumber = 28;
UPDATE verses SET es = "Mas tu Señor nunca destruiría las ciudades mientras no susci­tara <em>en la ciudad </em>madre de las mis­mas un Mensajero que les reci­tara Nuestros Signos; ni destruiría­mos las ciudades a menos que sus gen­tes fuesen malvadas." WHERE ayatNumber = 60 AND chapterNumber = 28;
UPDATE verses SET es = "Y cualquiera de las cosas <em>de</em> <em>este mundo </em>que se os dé no es más que un goce temporal de la vida presente y un ornamento suyo; pero lo que está junto a Al‑lah es mejor y más duradero. ¿Acaso no entende­réis?" WHERE ayatNumber = 61 AND chapterNumber = 28;
UPDATE verses SET es = "¿Acaso será aquel a quien hemos hecho una promesa divina cuyo cumplimiento alcanzará, igual a aquel al que hemos proporcionado las cosas buenas de esta vida y más tarde, en el Día de la Resurrección, será de los que sean traídos <em>empla­zados ante Dios</em>?" WHERE ayatNumber = 62 AND chapterNumber = 28;
UPDATE verses SET es = "En ese día los llamará y dirá: “¿Dónde están los “partícipes” que así imaginasteis?”." WHERE ayatNumber = 63 AND chapterNumber = 28;
UPDATE verses SET es = "Aquellos contra quienes se vaya a dictar sentencia dirán: “Se­ñor nuestro, éstos son aquellos a quienes extraviamos. Los extravia­mos al igual que nos extraviamos <em>nosotros mismos. Ahora </em>nos sepa­ramos <em>de ellos y</em> <em>volvemos </em>a Ti. No era a nosotros a quienes adoraban”." WHERE ayatNumber = 64 AND chapterNumber = 28;
UPDATE verses SET es = "Mas se dirá: “Llamad a vuestros compañeros”. Los llamarán pero no les contestarán. Y verán el cas­tigo. ¡Ojalá hubiesen seguido la guía!" WHERE ayatNumber = 65 AND chapterNumber = 28;
UPDATE verses SET es = "Y en ese día, Él se dirigirá a ellos y les dirá: “¿Qué respuesta disteis a los Mensajeros?”." WHERE ayatNumber = 66 AND chapterNumber = 28;
UPDATE verses SET es = "En ese día, todos los asuntos quedarán oscurecidos para ellos, y ni <em>siquiera </em>se preguntarán mutuamente." WHERE ayatNumber = 67 AND chapterNumber = 28;
UPDATE verses SET es = "Pero quien se arrepienta, crea y realice buenas acciones, tal vez se cuente entre los prósperos." WHERE ayatNumber = 68 AND chapterNumber = 28;
UPDATE verses SET es = "Y tu Señor crea todo lo que Le agrada y elige <em>a quien Le place. </em>No les corresponde a ellos elegir. lori­ficado sea Al-lah, pues Él está muy por encima de todo lo que <em>Le </em>asocian." WHERE ayatNumber = 69 AND chapterNumber = 28;
UPDATE verses SET es = "Pues tu Señor conoce lo que ocultan sus corazones y lo que revelan." WHERE ayatNumber = 70 AND chapterNumber = 28;
UPDATE verses SET es = "Él es Al‑lah; no hay más dios que Él. A Él corresponde toda la alabanza en el comienzo y en el Más Allá. Suyo es el juicio, y a Él seréis devueltos." WHERE ayatNumber = 71 AND chapterNumber = 28;
UPDATE verses SET es = "Diles: “Decidme, si Al‑lah hi­ciera la noche continua sobre voso­tros hasta el Día de la Resurrección; ¿qué dios existe fuera de Al‑lah que pudiera devolveros la luz? ¿No escu­charéis pues?" WHERE ayatNumber = 72 AND chapterNumber = 28;
UPDATE verses SET es = "Diles “Decidme, si Al-lah hiciera el día continuo sobre vosotros hasta el Día de la Resurrección, ¿qué dios existe fuera de Al‑lah que pu­diera devolveros la noche en la que podáis descansar? ¿No veréis pues?”." WHERE ayatNumber = 73 AND chapterNumber = 28;
UPDATE verses SET es = "Y con Su misericordia ha hecho para vosotros el día y la noche, para que descanséis en ellos, y po­dáis buscar de Su gracia, y seáis agradecidos." WHERE ayatNumber = 74 AND chapterNumber = 28;
UPDATE verses SET es = "Y en ese día, los convocará y les dirá: “¿En dónde están Mis “par­tícipes”, a quienes imaginasteis de ese modo?”." WHERE ayatNumber = 75 AND chapterNumber = 28;
UPDATE verses SET es = "Y sacaremos un testigo de cada pueblo y les diremos: “Pre­sentad vuestra prueba”. Entonces sabrán que la verdad pertenece a Al-­lah. Y que lo que solían tramar se les esfumará." WHERE ayatNumber = 76 AND chapterNumber = 28;
UPDATE verses SET es = "En verdad, Korah pertenecía al pueblo de Moisés, pero se comportó como un tirano respecto a ellos. Y le habíamos dado de los tesoros en cantidad tal que sus llaves no hubieran podido ser trans­portadas por un grupo de hombres fuertes. Cuando su pueblo le dijo: “No te exultes, en verdad Al‑lah no ama a quienes se enorgullecen”." WHERE ayatNumber = 77 AND chapterNumber = 28;
UPDATE verses SET es = "“Y busca, en lo que Al‑lah te ha dado, la morada del Más Allá; no olvides tu parte en este mundo; y haz el bien <em>a los demás </em>como Al‑lah lo ha hecho contigo; no pretendas hacer el mal en la tierra pues en verdad Al­-lah no ama a quienes hacen el mal”." WHERE ayatNumber = 78 AND chapterNumber = 28;
UPDATE verses SET es = "Él respondió: “Esto se me ha dado por los conocimientos que po­seo”. ¿No sabía acaso que Al‑lah destruyó antes que él a generaciones más poderosas que él y de mayores riquezas? Mas al culpable no se le pedirá <em>que ofrezca una explicación </em>de sus pecados." WHERE ayatNumber = 79 AND chapterNumber = 28;
UPDATE verses SET es = "Así se presentó ante su pueblo con su pompa. Los que ambiciona­ban la vida de este mundo, dijeron: “¡Oh, ojalá tuviésemos algo similar a lo que se ha dado a Korah! En verdad, es el dueño de una gran fortuna”." WHERE ayatNumber = 80 AND chapterNumber = 28;
UPDATE verses SET es = "Pero aquellos que recibieron el conocimiento dijeron: “Ay de voso­tros, la recompensa de Al‑lah es mejor para quienes creen y hacen buenas obras; y no se concederá a ninguno salvo a quienes sean per­severantes”." WHERE ayatNumber = 81 AND chapterNumber = 28;
UPDATE verses SET es = "Entonces hicimos que la tierra se lo tragara, a él y a su morada; y no obtuvo grupo alguno que lo ayudara contra Al‑lah, ni era de los que pudiera dominar <em>al decreto de Al-lah</em>." WHERE ayatNumber = 82 AND chapterNumber = 28;
UPDATE verses SET es = "Y quienes codiciaron su posi­ción el día anterior comenzaron a decir: “¡Ah! en verdad es Al‑lah quien amplía la provisión para quellos de Sus siervos que Le place y la es­trecha <em>para quien Él quiere. </em>De no haber sido Al‑lah Clemente con o­sotros, habría hecho que <em>la tierra </em>nos tragara <em>también. </em>¡Ah! los ingratos nunca prosperan”." WHERE ayatNumber = 83 AND chapterNumber = 28;
UPDATE verses SET es = "¡Ésta es la Morada del Más Allá! Nosotros la damos a quienes no desean la propia exaltación en la tierra ni la corrupción. Y el final es para el justo." WHERE ayatNumber = 84 AND chapterNumber = 28;
UPDATE verses SET es = "Quien hace una buena obra tendrá una recompensa mejor que ella; y quien cometa una mala acción <em>sepa que</em> quienes cometen malas cciones no serán retribuidos salvo <em>de acuerdo con </em>lo que hicieron." WHERE ayatNumber = 85 AND chapterNumber = 28;
UPDATE verses SET es = "Con certeza que Quien te ha impuesto <em>las enseñanzas del </em> Corán te devolverá a <em>tu</em> lugar de retorno. Diles: “Mi Señor sabe mejor quién trae la guía y quién está en error evidente”." WHERE ayatNumber = 86 AND chapterNumber = 28;
UPDATE verses SET es = "Y nunca esperaste que el Libro te fuese revelado; sino que se trata de una misericordia de tu Señor; no seas, pues, ayudante de quienes no creen." WHERE ayatNumber = 87 AND chapterNumber = 28;
UPDATE verses SET es = "Y no dejes que te aparten de los Signos de Al‑lah, después de que te han sido revelados; llama <em>a la humanidad </em>ante tu Señor; y no seas de aquellos que <em>Le </em>atribuyen par­tícipes." WHERE ayatNumber = 88 AND chapterNumber = 28;
UPDATE verses SET es = "No invoques a ningún otro dios fuera de Al‑lah. No hay más Dios que Él. Todo perecerá excepto Él. Suyo es el juicio, y a Él seréis devueltos." WHERE ayatNumber = 89 AND chapterNumber = 28;
UPDATE verses SET es = "En el nombre de Al‑lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 29;
UPDATE verses SET es = "Alif Lam Mim." WHERE ayatNumber = 2 AND chapterNumber = 29;
UPDATE verses SET es = "¿Piensan los hombre que se­rán dejados solos porque dicen: “Creemos” y que no serán puestos a prueba?" WHERE ayatNumber = 3 AND chapterNumber = 29;
UPDATE verses SET es = "Y pusimos a prueba a quie­nes existieron antes que ellos. Al‑lah distinguirá pues ciertamente a quienes son veraces, y distinguirá en verdad a los mentirosos <em>de los ve­races.</em>" WHERE ayatNumber = 4 AND chapterNumber = 29;
UPDATE verses SET es = "¿O acaso piensan quienes cometen malas acciones que escaparán a Nosotros? ¡Qué malo es lo que juzgan!" WHERE ayatNumber = 5 AND chapterNumber = 29;
UPDATE verses SET es = "Quien espere encontrar a Al-lah, <em>que se prepare para ello,</em> pues la hora fijada por Al‑lah vendrá ciertamente. Pues Él es Quien todo lo oye, el Omnisciente." WHERE ayatNumber = 6 AND chapterNumber = 29;
UPDATE verses SET es = "Y quien se esfuerza, se esfuerza únicamente por su propia alma; en verdad, Al‑lah es Indepen­diente de todas las criaturas." WHERE ayatNumber = 7 AND chapterNumber = 29;
UPDATE verses SET es = "Y en cuanto a quienes creen y hacen buenas obras, ciertamente les retiraremos sus malas acciones y les da­remos ciertamente la mejor recom­pensa por sus obras." WHERE ayatNumber = 8 AND chapterNumber = 29;
UPDATE verses SET es = "Hemos ordenado al hombre la benevolencia con sus padres; pero si disputan contigo para que tu también asocies partícipes conmigo; de lo cual no tienes ningún conocimiento, no les obedezcas. A Mí es a quien retornaréis, y os informaré de lo que hicisteis." WHERE ayatNumber = 9 AND chapterNumber = 29;
UPDATE verses SET es = "Mas quienes creen y hacen buenas obras: a ellos ciertamente los admitiré en <em>la compañía</em> <em>de </em>los justos." WHERE ayatNumber = 10 AND chapterNumber = 29;
UPDATE verses SET es = "Entre los hombres hay <em>algu­nos </em>que dicen: “Creemos en Al‑lah” ; pero cuando se les hace sufrir por la causa de Al‑lah, consideran que la persecución de los hombres es como el castigo de Al‑lah. Y si les llega la ayuda de tu Señor, dirán ciertamen­te, “En verdad estábamos con voso­tros.” ¿Acaso Al‑lah no sabe me­jor que nadie lo que hay en el corazón de toda la gente?." WHERE ayatNumber = 11 AND chapterNumber = 29;
UPDATE verses SET es = "¡Mas Al-lah distinguirá ciertamente a quienes creen y distin­guirá ciertamente a los hipócritas <em>de los creyentes!</em>" WHERE ayatNumber = 12 AND chapterNumber = 29;
UPDATE verses SET es = "Los incrédulos dicen a los cre­yentes: “Seguid nuestro camino, y ciertamente cargaremos vuestros pecados.” Pero no pueden llevar na­da de sus pecados. En verdad son embusteros." WHERE ayatNumber = 13 AND chapterNumber = 29;
UPDATE verses SET es = "Pero sí que llevarán su propias cargas junto con<em> otras</em> cargas además de las suyas. Y ciertamente serán in­terrogados en el Día de la Resurrec­ción en cuanto a lo que tramaron." WHERE ayatNumber = 14 AND chapterNumber = 29;
UPDATE verses SET es = "Ciertamente enviamos a Noé a su pueblo, y vivió entre ellos mil años menos cincuenta. A continuación el Diluvio cayó sobre ellos, mientras eran de los malvados." WHERE ayatNumber = 15 AND chapterNumber = 29;
UPDATE verses SET es = "Pero Nosotros los salvamos a él y a quienes estaban <em>con</em> él en el Arca; y lo convertimos en un Signo para todos los pueblos." WHERE ayatNumber = 16 AND chapterNumber = 29;
UPDATE verses SET es = "Y <em>acordaos de</em> Abraham, cuando dijo a su pueblo: “Adorad a Al‑lah y temedle. Sería mejor para vo­sotros, si supierais;" WHERE ayatNumber = 17 AND chapterNumber = 29;
UPDATE verses SET es = "“Sólo adoráis a ídolos en lu­gar de a Al‑lah, y forjáis mentiras. Aquellos a quienes adoráis fuera de Al‑lah no tienen poder para propor­cionaros el sostén. Buscad pues el sostén de Al‑lah, y adoradle, y sedle agradecidos. A Él seréis devueltos.”" WHERE ayatNumber = 18 AND chapterNumber = 29;
UPDATE verses SET es = "“Y si os oponéis, muchas generaciones antes que vosotros <em>también</em> se opusieron<em>. </em>Mas el Mensa­jero sólo es responsable de la trans­misión diáfana <em>del Mensaje”.</em>" WHERE ayatNumber = 19 AND chapterNumber = 29;
UPDATE verses SET es = "¿Acaso no ven cómo Al‑lah origina la creación, y después la repite? Eso es, ciertamente, fácil para Al‑lah." WHERE ayatNumber = 20 AND chapterNumber = 29;
UPDATE verses SET es = "Diles: 'Viajad por la tierra, y ved como Él originó la creación. Luego Al‑lah hará surgir otra creación <em>posterior</em>. “En verdad, Al‑lah tiene poder sobre todas las cosas." WHERE ayatNumber = 21 AND chapterNumber = 29;
UPDATE verses SET es = "Él castiga a quien Le place y muestra misericordia a quien Le agrada; y a Él seréis devueltos;" WHERE ayatNumber = 22 AND chapterNumber = 29;
UPDATE verses SET es = "Y no podéis frustrar<em> los designios de Al‑lah </em>en la tierra ni en el cielo; ni tenéis amigo ni ayudante fuera de Al‑lah." WHERE ayatNumber = 23 AND chapterNumber = 29;
UPDATE verses SET es = "Quienes no creen en los Sig­nos de Al‑lah y en la reunión con Él: son estos quienes han desesperado de Mi misericordia. Para ellos habrá un severo castigo." WHERE ayatNumber = 24 AND chapterNumber = 29;
UPDATE verses SET es = "La única respuesta de su pue­blo fue que dijeron: “Matadle o que­madle”. Pero Al‑lah le salvó del fuego. Aquí hay ciertamente Signos para un pueblo dispuesto a creer." WHERE ayatNumber = 25 AND chapterNumber = 29;
UPDATE verses SET es = "Dijo: “En verdad, habéis adoptado ídolos para vosotros fuera de Al‑lah, por puro amor mutuo, en la presente vida. Más tarde, en el Día de la Resurrección os negaréis unos a otros y os maldeciréis mutuamente. Vuestra morada será el Infierno y no tendréis quien os ayude”." WHERE ayatNumber = 26 AND chapterNumber = 29;
UPDATE verses SET es = "Lot creyó en él; y A<em>braham </em>dijo: “Me refugio con mi Señor; en verdad, Él es el Poderoso, el Sabio”." WHERE ayatNumber = 27 AND chapterNumber = 29;
UPDATE verses SET es = "Le concedimos a Isaac y Jacob, y pusimos el <em>don </em>de la profecía y el Libro entre sus descendientes, le dimos su recompensa en esta vida, y en el Más Allá estará ciertamente entre los justos." WHERE ayatNumber = 28 AND chapterNumber = 29;
UPDATE verses SET es = "<em> Y enviamos a </em>Lot; que dijo a su pueblo: “Cometéis una abominación que nadie de la humanidad ha co­metido antes que vosotros”." WHERE ayatNumber = 29 AND chapterNumber = 29;
UPDATE verses SET es = "“¿Os acercáis en verdad <em>disi­muladamente </em>a los hombres y cortáis el camino <em>a los viajeros? </em>¡Os permitís todo lo aborrecible en vuestras reu­niones! “Pero la única respuesta de su pueblo fue que dijeron: “Trae sobre nosotros el castigo de Al‑lah si dices la verdad”." WHERE ayatNumber = 30 AND chapterNumber = 29;
UPDATE verses SET es = "Dijo: “Ayúdame, mi Señor, contra el pueblo corrompido”." WHERE ayatNumber = 31 AND chapterNumber = 29;
UPDATE verses SET es = "Y cuando Nuestros mensa­jeros llevaron a Abraham la buena nueva, dijeron: “Vamos a destruir al pueblo de esta ciudad; en verdad sus gentes son injustas”." WHERE ayatNumber = 32 AND chapterNumber = 29;
UPDATE verses SET es = "Dijo: “Pero Lot está allí”. “Respondieron: “Sabemos muy bien quién está allí. Ciertamente lo sal­varemos a él y a su familia, excepto a su mujer, que es de las que se quedan detrás”." WHERE ayatNumber = 33 AND chapterNumber = 29;
UPDATE verses SET es = "Cuando Nuestros Mensajeros llegaron a Lot, se angustió por ellos y se sintió indefenso frente a ellos. Dijeron: “No temas ni te afli­jas; ciertamente te salvaremos a ti y a tu familia, excepto a tu mujer, que es de las que se quedan detrás." WHERE ayatNumber = 34 AND chapterNumber = 29;
UPDATE verses SET es = "“En verdad, haremos caer sobre las gentes de esta ciudad un castigo de los cielos, porque han sido rebeldes”." WHERE ayatNumber = 35 AND chapterNumber = 29;
UPDATE verses SET es = "Y hemos dejado en todo ello un Signo claro para el pueblo que sea sensato." WHERE ayatNumber = 36 AND chapterNumber = 29;
UPDATE verses SET es = "<em>Enviamos </em>a Madián a su hermano Shu'aib quien dijo: “Oh, pueblo mío, servid a Al‑lah, temed el Ultimo Día y no cometáis iniquidad alguna en la tierra, creando el de­sorden”." WHERE ayatNumber = 37 AND chapterNumber = 29;
UPDATE verses SET es = "Pero ellos le llamaron menti­roso. Por lo que les sobrevino un violento terremoto y en sus casas quedaron, postrados en tierra." WHERE ayatNumber = 38 AND chapterNumber = 29;
UPDATE verses SET es = "Y <em>destruimos </em>a Ad y Zamud: os es evidente por sus moradas. Y Satanás hizo que sus acciones <em>aparecieran </em>buenas ante su vista, y los apartó así del camino, a pesar de que tenían la capacidad de discernir <em>la verdad</em>." WHERE ayatNumber = 39 AND chapterNumber = 29;
UPDATE verses SET es = "Y <em>destruimos</em> a Korah, al Faraón y a Hamán. Moisés compareció ante ellos con Signos manifiestos, pero se comportaron rgullosamente en la tierra, aunque no pudieron librarse <em>de Nosotros.</em>" WHERE ayatNumber = 40 AND chapterNumber = 29;
UPDATE verses SET es = "Así caímos sobre cada uno <em>de ellos </em>por su pecado; contra algunos de ellos enviamos una violenta tormenta de arena, otros quedaron sobrecogidos por un estallido espan­toso, otros hicimos que fueran tragados por la tierra, y hubo otros a quienes ahogamos. No fue Al-­lah quien los perjudicó, sino que fueron ellos los que arruinaron sus propias almas." WHERE ayatNumber = 41 AND chapterNumber = 29;
UPDATE verses SET es = "El ejemplo de quienes adoptan protectores distintos a Al‑lah es como el de la araña, que se prepara una casa; la casa de la araña es en verdad la más frágil de<em> todas </em>las casas, ¡si lo supieran!" WHERE ayatNumber = 42 AND chapterNumber = 29;
UPDATE verses SET es = "En verdad, Al‑lah conoce todo aquello que invocan en vez de a Él; mas Él es el Poderoso, el Sabio." WHERE ayatNumber = 43 AND chapterNumber = 29;
UPDATE verses SET es = "Estas son semejanzas; ejemplos que proponemos a los humanos, pero sólo las entienden quienes tie­nen conocimiento." WHERE ayatNumber = 44 AND chapterNumber = 29;
UPDATE verses SET es = "Al‑lah creó los cielos y la tierra con la verdad. Aquí hay cier­tamente un Signo para los creyentes." WHERE ayatNumber = 45 AND chapterNumber = 29;
UPDATE verses SET es = "Recita lo que te ha sido revelado del Libro, y cumple la Ora­ción. En verdad, la Oración preserva <em>a la persona </em>de la obscenidad y el mal manifiesto, y el recuerdo de Al‑lah es en verdad la mayor <em>virtud. </em>Pues Al‑lah sabe lo que hacéis.." WHERE ayatNumber = 46 AND chapterNumber = 29;
UPDATE verses SET es = "No disputéis con el Pueblo del libro, excepto con lo que sea mejor; pero <em>no disputéis en absoluto </em>con los que son injustos de entre ellos. De­cidles: “Creemos en lo que nos ha sido revelado y en lo que os ha sido revelado; nuestro Dios y vuestro Dios es Uno Sólo; y a Él nos sometemos”." WHERE ayatNumber = 47 AND chapterNumber = 29;
UPDATE verses SET es = "De la misma manera te hemos revelado el Libro, para que aquellos a quienes hemos dado el <em>conocimiento verdadero del </em>Libro, crean en él (el Corán). Y también entre estos <em>mequíes </em>hay algunos que creen en él. Pues nadie, excepto los desagradecidos, niegan Nuestros Signos." WHERE ayatNumber = 48 AND chapterNumber = 29;
UPDATE verses SET es = "Tú no recitaste ningún Libro antes de él, ni escribiste ninguno con tu mano derecha; en ese caso, los mentirosos habrían dudado." WHERE ayatNumber = 49 AND chapterNumber = 29;
UPDATE verses SET es = "No, ésta es <em>una colección de </em>Signos claros para los corazones de aquellos que han recibido el conoci­miento. Mas nadie, excepto los injustos, niegan Nuestros Signos." WHERE ayatNumber = 50 AND chapterNumber = 29;
UPDATE verses SET es = "Ellos dicen: “¿Por qué no te revela Signos tu Señor?” Diles: “Los Signos están en Al‑lah, y yo soy, ciertamente, un Amonestador fide­digno”." WHERE ayatNumber = 51 AND chapterNumber = 29;
UPDATE verses SET es = "¿Acaso no les basta el que te hayamos revelado el Libro que se les recita? En verdad, en él hay misericordia y un recordatorio para el pueblo que cree." WHERE ayatNumber = 52 AND chapterNumber = 29;
UPDATE verses SET es = "Diles: “Al-lah basta como testigo entre vosotros y yo. Él sabe lo que hay en los cielos y la tierra. Y <em>en cuanto</em> a quienes creen en la falsedad y no creen en Al-lah, son ellos los perdedores”." WHERE ayatNumber = 53 AND chapterNumber = 29;
UPDATE verses SET es = "Te piden que apresures el castigo; mas de no haber existido un plazo fijado, el castigo habría caído sobre ellos. Y ciertamente les alcan­zará de improviso, cuando no se den cuenta." WHERE ayatNumber = 54 AND chapterNumber = 29;
UPDATE verses SET es = "Te piden que apresures el cas­tigo; pero en verdad, el infierno <em>ya </em>rodea a los incrédulos." WHERE ayatNumber = 55 AND chapterNumber = 29;
UPDATE verses SET es = "<em>Recuerda </em>el día en que el escarmiento les sobrevendrá esde arriba y desde debajo de sus pies, y Él dirá: “Probad <em>el fruto de </em>vuestras acciones”." WHERE ayatNumber = 56 AND chapterNumber = 29;
UPDATE verses SET es = "¡Oh, siervos míos que creéis! En verdad, amplia es Mi tierra, ado­radme pues sólo a Mí." WHERE ayatNumber = 57 AND chapterNumber = 29;
UPDATE verses SET es = "Toda alma probará el gusto de la muerte; luego seréis devuel­tos a Nosotros." WHERE ayatNumber = 58 AND chapterNumber = 29;
UPDATE verses SET es = "A quienes creen y hacen buenas obras, a ellos los alojaremos ciertamente en mansiones elevadas del Paraíso, bajo las cuales corren íos. Allí morarán. Excelente es la recompensa de los que hacen <em>el bien.</em>" WHERE ayatNumber = 59 AND chapterNumber = 29;
UPDATE verses SET es = "Los que son perseverantes y ponen su confianza en su Señor." WHERE ayatNumber = 60 AND chapterNumber = 29;
UPDATE verses SET es = "¡Cuántos animales hay que no pueden procurarse su propio sus­tento! Al‑lah provee para ellos y para vosotros. Él es Quien Todo lo Oye, el Omnisciente." WHERE ayatNumber = 61 AND chapterNumber = 29;
UPDATE verses SET es = "Y si les preguntáis: “¿Quién ha creado los cielos y la tierra y ha puesto en servicio al sol y la lu­na? dirán ciertamente: “Al‑lah”. ¿Cómo se extravían, pues? " WHERE ayatNumber = 62 AND chapterNumber = 29;
UPDATE verses SET es = "Al-lah amplía <em>los medios</em> <em>de</em> sustento de aquellos e Sus siervos que Le place, y se los recorta a quienes <em>quiere. </em>En verdad Al‑lah tiene pleno conocimiento de todas las cosas." WHERE ayatNumber = 63 AND chapterNumber = 29;
UPDATE verses SET es = "Y si les preguntas: “¿Quién envía el agua desde el cielo dando así la vida a la tierra después de su muerte?” , dirán ciertamente: “Al­-lah”. Diles: “Toda alabanza corres­ponde a Al‑lah”. Pero la mayoría de ellos no entiende." WHERE ayatNumber = 64 AND chapterNumber = 29;
UPDATE verses SET es = "La vida del mundo no es sino un pasatiempo y un juego, y el Hogar del Más Allá es la Vida en verdad. ¡Si lo supieran!" WHERE ayatNumber = 65 AND chapterNumber = 29;
UPDATE verses SET es = "Cuando van a bordo de una embarcación, invocan a Al‑lah, con fe sincera y exclusiva en Él. Pero cuando Él les lleva seguros a tierra, he ahí que Le asocian partícipes." WHERE ayatNumber = 66 AND chapterNumber = 29;
UPDATE verses SET es = "Para negar lo que les hemos concedido, y disfrutar <em>durante algún tiempo. </em>Pero pronto sabrán." WHERE ayatNumber = 67 AND chapterNumber = 29;
UPDATE verses SET es = "¿No han visto que hemos he­cho el santuario seguro <em>para ellos, </em>mientras las gentes son expulsadas de su alrededor? ¿Van a creer pues en la falsedad y negar el favor de Al‑lah?" WHERE ayatNumber = 68 AND chapterNumber = 29;
UPDATE verses SET es = "¿Quién es más injusto que aquel que inventa una mentira sobre Al‑lah, o rechaza la verdad cuando le ha llegado? ¿No hay acaso una morada en el Infierno para los in­crédulos?" WHERE ayatNumber = 69 AND chapterNumber = 29;
UPDATE verses SET es = "En cuanto a los que se esfuerzan en Nuestro camino: en verdad los guiaremos por Nuestras sendas. Pues Al‑lah está en verdad con los que hacen el bien." WHERE ayatNumber = 70 AND chapterNumber = 29;
UPDATE verses SET es = "En el nombre de Al‑lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 30;
UPDATE verses SET es = "Alif Lam Mim." WHERE ayatNumber = 2 AND chapterNumber = 30;
UPDATE verses SET es = "Los romanos han sido ven­cidos," WHERE ayatNumber = 3 AND chapterNumber = 30;
UPDATE verses SET es = "En el país vecino, y, des­pués de su derrota, serán victoriosos" WHERE ayatNumber = 4 AND chapterNumber = 30;
UPDATE verses SET es = "En pocos años -de Al‑lah es el decreto antes y después <em>de</em> <em>eso-</em>­ y en ese día los creyentes se regocijarán," WHERE ayatNumber = 5 AND chapterNumber = 30;
UPDATE verses SET es = "Con la ayuda de Al‑lah. Él ayuda a quien Le place; pues Él es el Poderoso, el Misericordioso." WHERE ayatNumber = 6 AND chapterNumber = 30;
UPDATE verses SET es = "Al‑lah <em>ha hecho </em>esta prome­sa. Al‑lah no rompe Su promesa, pero la mayoría de los hombres no lo saben." WHERE ayatNumber = 7 AND chapterNumber = 30;
UPDATE verses SET es = "<em>Sólo</em> conocen el <em>aspecto </em>ex­terior de la vida de este mundo, y descuidan totalmente el Más Allá." WHERE ayatNumber = 8 AND chapterNumber = 30;
UPDATE verses SET es = "¿No reflexionan con sus pro­pias mentes? Al‑lah no ha creado los cielos y la tierra y todo lo que hay entre ambos sino de acuerdo con los requisitos de la sabiduría y por un plazo fijo. Pero muchos de entre los hombres no creen en la reunión con su Señor." WHERE ayatNumber = 9 AND chapterNumber = 30;
UPDATE verses SET es = "¿Acaso no han viajado por la tierra para poder ver qué <em>malo</em> fue el fin de los que les antecedieron? Fue­ron más uertes que éstos en poder, araron la tierra y la poblaron <em>más</em> <em>y mejor</em> de lo que la han poblado éstos. Y sus Mensajeros llegaron a ellos con Signos manifiestos. Mas Al‑lah no les perjudicó, sino que ellos mismos perjudicaron sus propias almas." WHERE ayatNumber = 10 AND chapterNumber = 30;
UPDATE verses SET es = "Malo fue después el final de los que hicieron el mal, porque re­chazaron los Signos de Al‑lah y se mofaron de ellos." WHERE ayatNumber = 11 AND chapterNumber = 30;
UPDATE verses SET es = "Al‑lah origina la creación; y después la repite; más tarde, a Él seréis devueltos." WHERE ayatNumber = 12 AND chapterNumber = 30;
UPDATE verses SET es = "Y en el día en que llegue la Hora, los culpables se hundirán en la desesperación." WHERE ayatNumber = 13 AND chapterNumber = 30;
UPDATE verses SET es = "Y no tendrán intercesores <em>de entre</em> quienes asocian a Dios; éstos ne­garán a <em>aquellos </em>que le asocian <em>a</em> <em>Él.</em>" WHERE ayatNumber = 14 AND chapterNumber = 30;
UPDATE verses SET es = "Y en el día en que llegue la Hora: en ese día quedarán sepa­rados <em>unos</em> <em>de los otros.</em>" WHERE ayatNumber = 15 AND chapterNumber = 30;
UPDATE verses SET es = "Entonces, quienes creyeron e hicieron buenas obras serán honra­dos y hechos felices en un jardín." WHERE ayatNumber = 16 AND chapterNumber = 30;
UPDATE verses SET es = "Mientras que aquellos que no creyeron y rechazaron Nuestros Sig­nos y la reunión del Más Allá, serán entregados al castigo." WHERE ayatNumber = 17 AND chapterNumber = 30;
UPDATE verses SET es = "Glorificad pues a Al‑lah cuando entréis en el anochecer y cuando entréis en el amanecer," WHERE ayatNumber = 18 AND chapterNumber = 30;
UPDATE verses SET es = "Pues a Él corresponde toda alabanza en los cielos y la tierra; <em>y glorificarle </em>por la tarde, y cuan­do entréis en el momento del ocaso del sol." WHERE ayatNumber = 19 AND chapterNumber = 30;
UPDATE verses SET es = "Él extrae lo vivo de lo muerto, y Él extrae lo muerto de lo vivo; Él da la vida a la tierra después de su muerte. Y de la misma manera vosotros seréis resucitados." WHERE ayatNumber = 20 AND chapterNumber = 30;
UPDATE verses SET es = "Y <em>uno</em> de Sus Signos <em>es éste: </em>que os ha creado del olvo; luego, ¡mirad! sois hombres que os disemi­náis <em>por la faz de la </em>tierra." WHERE ayatNumber = 21 AND chapterNumber = 30;
UPDATE verses SET es = "<em>Y uno </em>de Sus Signos <em>es éste: </em>Que os ha creado esposas de entre vosotros mismos para que encontréis la paz del espíritu en ellas, y ha puesto amor y ternura entre ambos. En eso hay, cierta­mente, Signos para los hombres que reflexionan." WHERE ayatNumber = 22 AND chapterNumber = 30;
UPDATE verses SET es = "Y entre Sus Signos está la creación de los cielos y la tierra, y la diversidad de vuestras lenguas y colores. Ahí hay ciertamente Signos para quienes poseen conocimien­to," WHERE ayatNumber = 23 AND chapterNumber = 30;
UPDATE verses SET es = "<em></em>Y entre Sus Signos está vues­tro sueño, de noche y de día, y vuestra búsqueda de Su magnanimidad. Ahí hay ciertamente Signos para un pue­blo que escucha." WHERE ayatNumber = 24 AND chapterNumber = 30;
UPDATE verses SET es = "Y<em> uno </em>de Sus Signos <em>es éste: </em>Que os muestra el relámpago como <em>fuente de </em>temor y esperanza, y envía agua desde el cielo y con ella vivifica la tierra después de su muerte. Ahí hay con seguridad Sig­nos para un pueblo que entiende." WHERE ayatNumber = 25 AND chapterNumber = 30;
UPDATE verses SET es = "Y entre Sus Signos <em>está éste: </em>Que los cielos y la tierra permanecen <em>firmes </em>por Su orden. Después, cuando Os convoque con una lla­mada <em>procedente </em>de la tierra, he ahí que emergeréis." WHERE ayatNumber = 26 AND chapterNumber = 30;
UPDATE verses SET es = "Y a Él pertenece todo lo que hay en los cielos y en la tierra. Todos Le obedecen." WHERE ayatNumber = 27 AND chapterNumber = 30;
UPDATE verses SET es = "Él es el origen de la creación, después la repite: es algo suma­mente fácil para Él. Suyo es el ejem­plo más sublime en los cielos y en la tierra; pues Él es el Poderoso, el Sabio." WHERE ayatNumber = 28 AND chapterNumber = 30;
UPDATE verses SET es = "Él os propone una parábola relativa a vosotros mismos. ¿Acaso habéis hecho que vuestros esclavos se conviertan en socios de lo que os hemos proporcionado y tengan igual <em>participación, </em>de tal forma que habéis comenzado a temerles como os teméis mutuamente? Así explica­mos los Signos a un pueblo que entiende." WHERE ayatNumber = 29 AND chapterNumber = 30;
UPDATE verses SET es = "No, pero quienes son injustos siguen sus propios bajos deseos sin conocimiento alguno. Entonces, ¿quién puede guiar a quien Al‑lah ha juzgado como extraviado? No habrá quien les ayude." WHERE ayatNumber = 30 AND chapterNumber = 30;
UPDATE verses SET es = "Dirige pues tu rostro al <em>servicio de </em>la religión, siempre inclinado <em>a la verdad</em>: <em>y sigue</em> la naturaleza de Al-lah, según la cual ha modelado la humanidad. No hay alteración alguna en la creación de Al‑lah. Ésta es ciertamente la religión firme y justa que ayuda a los demás a ser justos. Aunque la mayoría de los hombres no lo saben." WHERE ayatNumber = 31 AND chapterNumber = 30;
UPDATE verses SET es = "<em>Levantad vuestro rostro hacia Dios, </em>volviendo a Él <em>con arrepenti­miento, </em>temedle y cumplid la Ora­ción, y no seáis de aquellos que asocian partícipes <em>a Dios</em>" WHERE ayatNumber = 32 AND chapterNumber = 30;
UPDATE verses SET es = "De aquellos que fragmentan su religión y quedan divididos en sectas; ale­grándose cada grupo con lo que tienen." WHERE ayatNumber = 33 AND chapterNumber = 30;
UPDATE verses SET es = "<em></em>Cuando una aflicción cae so­bre los hombres, invocan a su Señor, dirigiéndose a Él <em>con arrepenti­miento; </em>pero luego, cuando les favo­rece para que gusten de Su miseri­cordia, ¡Ay! un grupo de ellos asocian partícipes a su Señor," WHERE ayatNumber = 34 AND chapterNumber = 30;
UPDATE verses SET es = "De modo que son desagrade­cidos con lo que les hemos dado. Regocijaos pues durante <em>algún tiempo, </em>porque muy pronto llegaréis a conocer." WHERE ayatNumber = 35 AND chapterNumber = 30;
UPDATE verses SET es = "¿Les he concedido autor­idad alguna que hable <em>a favor </em>de lo que Le asocian?" WHERE ayatNumber = 36 AND chapterNumber = 30;
UPDATE verses SET es = "Y cuando favorecemos a la hu­manidad con el sabor de la isericordia, se regocijan; pero cuando les acaece el mal por lo que <em>ellos mismos</em> han ganado con sus manos, he ahí que empiezan a desesperarse." WHERE ayatNumber = 37 AND chapterNumber = 30;
UPDATE verses SET es = "¿No han visto acaso que Al‑lah amplía la provisión a quien Le place, y la estrecha <em>a quien desea? </em>Aquí hay realmente Signos para los hombres que creen." WHERE ayatNumber = 38 AND chapterNumber = 30;
UPDATE verses SET es = "Dad pues al pariente lo que se le debe, y al necesitado y al viajero, Eso es lo mejor para los que buscan el favor de Al‑lah; y son ellos los que prosperarán." WHERE ayatNumber = 39 AND chapterNumber = 30;
UPDATE verses SET es = "Lo que pagáis como inte­reses para que aumente la riqueza de los hombres, no la aumenta a la vista de Al‑lah; sin embargo, todo lo que dais en <em>Zakat </em>buscando el favor de Al‑lah, <em>sabed que</em> éstos es a quienes se aumentará muchas veces <em>su</em> <em>riqueza.</em>" WHERE ayatNumber = 40 AND chapterNumber = 30;
UPDATE verses SET es = "Es Al‑lah Quien os ha creado, y os ha provisto <em>de dones; </em> después Él os hará morir y os devolverá de nuevo a la vida. ¿Hay alguno de vuestros “partícipes” que puedan ha­cer alguna de estas cosas? Glorifi­cado sea Él y exaltado por encima de lo que <em>Le </em> asocian." WHERE ayatNumber = 41 AND chapterNumber = 30;
UPDATE verses SET es = "La corrupción ha aparecido en la tierra y en el mar a causa de lo ue han forjado las manos de los hombres, para que Él les haga probar<em>el fruto </em>de algunas de sus acciones, y para que se aparten <em>del mal.</em>" WHERE ayatNumber = 42 AND chapterNumber = 30;
UPDATE verses SET es = "Diles: “¡Viajad por la tierra y ved qué malo fue el fin de los que <em>os</em> precedieron! La mayoría de ellos fueron idólatras”." WHERE ayatNumber = 43 AND chapterNumber = 30;
UPDATE verses SET es = "Levanta pues tu rostro hacia la religión justa que ayuda a los demás a ser justos, an­tes de que llegue el día de Al‑lah en el que no abrá escape. En ese día se dividirán en grupos, distintos entre si <em>.</em>" WHERE ayatNumber = 44 AND chapterNumber = 30;
UPDATE verses SET es = "Los incrédulos sufrirán <em>las consecuencias </em>de su ncredulidad; y los que hagan buenas obras se pre­pararán <em>el bien </em>para ellos mismos," WHERE ayatNumber = 45 AND chapterNumber = 30;
UPDATE verses SET es = "Para que Él, de Su magnani­midad, recompense a los que creen y hacen buenas obras. En verdad, Él no ama a los incrédulos." WHERE ayatNumber = 46 AND chapterNumber = 30;
UPDATE verses SET es = "Y entre Sus Signos <em>está éste: </em>Que envía los vientos como porta­dores de buena nueva, para ha­ceros probar el gusto de Su miseri­cordia, para que los buques zarpen bajo Su mando, podáis solicitar de Su generosidad y seáis agradecidos." WHERE ayatNumber = 47 AND chapterNumber = 30;
UPDATE verses SET es = "En verdad enviamos Mensa­jeros antes de ti a sus propios pue­blos, y les llevaron Signos claros. Después castigamos rotundamente a los culpables. Y ciertamente era obli­gación Nuestra ayudar a los cre­yentes." WHERE ayatNumber = 48 AND chapterNumber = 30;
UPDATE verses SET es = "Es Al‑lah Quien<strong> </strong>envía los vientos para que levanten la nube. Después la extiende por el cielo como Le place y la coloca capa sobre capa y ves la lluvia que sale de en medio de ella. Y cuando la hace caer sobre quienes Le agradan de Sus siervos, mirad cómo se regocijan;" WHERE ayatNumber = 49 AND chapterNumber = 30;
UPDATE verses SET es = "Aunque antes<strong> </strong>de eso, antes incluso de que fuese enviada sobre ellos, estaban desesperados." WHERE ayatNumber = 50 AND chapterNumber = 30;
UPDATE verses SET es = "Mira, pues, las señales de la misericordia de Al‑lah: cómo ivi­fica la tierra después de su muerte. En verdad, el mismo <em>Dios </em>resucitará a los muertos; pues Él tiene poder sobre todas las cosas." WHERE ayatNumber = 51 AND chapterNumber = 30;
UPDATE verses SET es = "Mas si enviásemos un viento y vieran cómo (sus cosechas) se ponen marillas, <em>empezarían </em>cierta­mente a negar <em>Nuestros fa­vores </em>a continuación<em>.</em>" WHERE ayatNumber = 52 AND chapterNumber = 30;
UPDATE verses SET es = "Mas no puedes hacer que los muertos oigan, ni puedes hacer que los sordos oigan la llamada, cuando se dan la vuelta mostrando sus es­paldas;" WHERE ayatNumber = 53 AND chapterNumber = 30;
UPDATE verses SET es = "Ni puedes sacar al ciego de su error. Sólo puedes hacer que oigan quienes crean en Nuestros Signos y se sometan." WHERE ayatNumber = 54 AND chapterNumber = 30;
UPDATE verses SET es = "Es Al‑lah Quien os creó <em>en estado de </em>debilidad y, después de la debilidad, os dio la fuerza; más tarde, tras la fuerza, provocó la debi­lidad y la vejez. Él crea lo que Le place. Él es el Omnisciente, el Todo­poderoso." WHERE ayatNumber = 55 AND chapterNumber = 30;
UPDATE verses SET es = "Y en el día en que llegue la Hora los culpables jurarán que no han ermanecido sino una hora: así fueron apartados <em>del camino recto.</em>" WHERE ayatNumber = 56 AND chapterNumber = 30;
UPDATE verses SET es = "Pero quienes poseen el cono­cimiento y la fe, dirán: “En verdad, habéis permanecido, según el Libro de Al‑lah, hasta el Día de la Re­surrección. Y éste es el Día de la Resurrección, aunque no os preocupasteis de conocerlo”." WHERE ayatNumber = 57 AND chapterNumber = 30;
UPDATE verses SET es = "Así, en aquel día sus excusas no servirán de nada a los injustos; ni se les permitirá acceder a Su umbral." WHERE ayatNumber = 58 AND chapterNumber = 30;
UPDATE verses SET es = "Y en verdad, en este Corán hemos propuesto a los hombres todo tipo de parábolas; y ciertamente si les trajeras un Signo, los incrédulos dirían con seguridad: “No sois más que mentirosos”." WHERE ayatNumber = 59 AND chapterNumber = 30;
UPDATE verses SET es = "Así sella Al-lah los corazones de los que carecen de conocimiento." WHERE ayatNumber = 60 AND chapterNumber = 30;
UPDATE verses SET es = "Sé pues paciente. En verdad la promesa de Al-lah es verdadera; y no ermitas que te pongan en duda los que no tienen certeza alguna <em>en la fe.</em>" WHERE ayatNumber = 61 AND chapterNumber = 30;
UPDATE verses SET es = "En el nombre de Al‑lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 31;
UPDATE verses SET es = "Alif Lam Mim." WHERE ayatNumber = 2 AND chapterNumber = 31;
UPDATE verses SET es = "Éstos son versículos del Libro de la Sabiduría," WHERE ayatNumber = 3 AND chapterNumber = 31;
UPDATE verses SET es = "Guía y misericordia para quie­nes hacen el bien," WHERE ayatNumber = 4 AND chapterNumber = 31;
UPDATE verses SET es = "Quienes cumplen la Oración, pagan el <em>Zakat </em>y tienen una fe firme en el Más Allá." WHERE ayatNumber = 5 AND chapterNumber = 31;
UPDATE verses SET es = "Son ellos quienes siguen la guía de su Señor, y quienes pros­perarán." WHERE ayatNumber = 6 AND chapterNumber = 31;
UPDATE verses SET es = "Entre los hombres hay quien cambia <em>la guía</em> <em>por </em> historietas vanas para apartar <em>a los</em> <em>hombres </em>del camino de Al‑lah, sin conoci­miento, burlándose de él. Para él habrá un castigo ignominioso." WHERE ayatNumber = 7 AND chapterNumber = 31;
UPDATE verses SET es = "Y cuando Nuestros Signos se le recitan, se aparta orgullosamente, como si no los hubiera oído, como si existiera un peso en sus dos oídos. Anúnciale pues un castigo doloroso." WHERE ayatNumber = 8 AND chapterNumber = 31;
UPDATE verses SET es = "En verdad, quienes creen y hacen buenas obras, tendrán Jar­dines de Placer." WHERE ayatNumber = 9 AND chapterNumber = 31;
UPDATE verses SET es = "En los que morarán, Al‑lah ha <em>realizado </em>una promesa verdadera; Él es el Poderoso, el Sabio." WHERE ayatNumber = 10 AND chapterNumber = 31;
UPDATE verses SET es = "Ha creado los cielos sin so­porte alguno que podáis ver y ha puesto en la tierra montañas fir­mes para que no se muevan con vosotros, y ha esparcido en ellas toda clase de animales; Hemos enviado agua de las nubes y hecho crecer en ella todas las nobles es­pecies." WHERE ayatNumber = 11 AND chapterNumber = 31;
UPDATE verses SET es = "Ésta es la creación de Al‑lah. Mostradme ahora lo que han creado otros fuera de Él. No, pero los in­justos están en un error evidente." WHERE ayatNumber = 12 AND chapterNumber = 31;
UPDATE verses SET es = "Concedimos la sabi­duría a Luqman, <em>diciéndole: “</em>Sé gradecido con Al‑lah”, pues quien es agradecido lo es únicamente por <em>el bien de </em>su propia alma. Mas quien sea desagradecido, sepa que Al‑lah es ciertamente Autosuficiente, Merece­dor de alabanza." WHERE ayatNumber = 13 AND chapterNumber = 31;
UPDATE verses SET es = "Y<em> acuérdate </em>de cuando Luq­man, dijo a su hijo mientras le exhortaba: “¡Oh, mi querido hijo! no asocies partícipes con Al‑lah. En ver­dad, asociar partícipes <em>a Dios</em> es una grave iniquidad”." WHERE ayatNumber = 14 AND chapterNumber = 31;
UPDATE verses SET es = "Hemos dado recomendaciones al hombre sobre sus padres -su madre lo lleva pena tras pena, y tarda dos años en destetarlo- “Dame gracias a Mí y a tus padres. Hacia Mí es el retorno <em>final;</em>" WHERE ayatNumber = 15 AND chapterNumber = 31;
UPDATE verses SET es = "“Mas si disputan contigo para que Me asocies lo que desconoces, no les obedezcas; muéstrate amable con ellos en <em>todos </em>los asuntos mun­danos, pero <em>en los espirituales </em>sigue el camino de quien vuelve a Mí. Más tarde <em>todos</em> volveréis a Mí y os informaré de lo que solíais hacer”." WHERE ayatNumber = 16 AND chapterNumber = 31;
UPDATE verses SET es = "“¡Oh, mi querido hijo! aunque tuviese el peso de un gramo de mos­taza, y aunque estuviese en una roca, en los cielos o en la tierra, Al‑lah ciertamente lo sacaría a la luz. En verdad, Al‑lah es el Conocedor de todas las sutilezas, el Omnisciente;" WHERE ayatNumber = 17 AND chapterNumber = 31;
UPDATE verses SET es = "“¡Oh, mi querido hijo! cumple la Oración, recomienda el bien y prohíbe el mal y resiste pacientemente lo que pueda sobrevenirte. En verdad, éstos son los asuntos que <em>exigen </em>una firme resolución." WHERE ayatNumber = 18 AND chapterNumber = 31;
UPDATE verses SET es = "“No hinches tus mejillas con orgullo, ni andes por la tierra con insolencia; en verdad Al‑lah no ama a quien se comporta con arrogancia y jactancia." WHERE ayatNumber = 19 AND chapterNumber = 31;
UPDATE verses SET es = "“Anda con paso moderado y baja la voz; en verdad, la voz más desagradable de todas es la voz de los asnos”." WHERE ayatNumber = 20 AND chapterNumber = 31;
UPDATE verses SET es = "¿No habéis visto que Al‑lah ha sometido a vuestro servicio todo lo que hay en los cielos y todo lo que hay en la tierra, y os ha dado Sus favores, <em>tanto </em>exterior como interior­mente? Entre los hombres los hay que disputan en relación con Al‑lah, sin conocimiento ni guía y sin un Libro lúcido." WHERE ayatNumber = 21 AND chapterNumber = 31;
UPDATE verses SET es = "Mas cuando se les dice: “Seguid lo que Al‑lah ha revelado”, responden: “¡No! seguiremos lo que vimos que seguían nuestros pa­dres”. ¡Cómo! ¿aunque Satanás les estuviera invitando al castigo del fuego ardiente?" WHERE ayatNumber = 22 AND chapterNumber = 31;
UPDATE verses SET es = "Quien se somete <em>totalmente </em>a Al-lah, y hace el bien, en verdad se ha aferrado a un sólido amarre. Pues en Al-lah <em>está </em> el fin de todos los asuntos." WHERE ayatNumber = 23 AND chapterNumber = 31;
UPDATE verses SET es = "<em> En cuanto a </em>quien no cree, no dejes que su incredulidad te angustie. A Nosotros será su retorno y les diremos lo que hicieron; en verdad, Al‑lah conoce perfectamente lo que hay en los corazones." WHERE ayatNumber = 24 AND chapterNumber = 31;
UPDATE verses SET es = "<em></em>Los dejaremos disfrutar un poco; después los conduciremos a un severo tormento." WHERE ayatNumber = 25 AND chapterNumber = 31;
UPDATE verses SET es = "Y si les preguntas: “¿Quién creó los cielos y la tierra?”, respon­derán ciertamente: “Al‑lah”. Di­les: “Toda alabanza corresponde a Al‑lah”. Sin embargo, la mayoría de ellos lo desconocen." WHERE ayatNumber = 26 AND chapterNumber = 31;
UPDATE verses SET es = "A Al‑lah pertenece cuanto hay en los cielos y en la tierra. En verdad, Al‑lah es Auto‑Suficiente, Merecedor de alabanza." WHERE ayatNumber = 27 AND chapterNumber = 31;
UPDATE verses SET es = "Si todos los árboles que hay en la tierra fuesen plumas; y el mar, con la ayuda de otros siete mares <em>fuese tinta, </em>no podrían agotarse las palabras de Al‑lah. En verdad," WHERE ayatNumber = 28 AND chapterNumber = 31;
UPDATE verses SET es = "Vuestra creación y vuestra resurrección son únicamente como <em>la creación y la resurrección de </em>un solo ser. En verdad, Al‑lah es Quien todo lo oye, Todo lo ve." WHERE ayatNumber = 29 AND chapterNumber = 31;
UPDATE verses SET es = "¿No has visto que Al‑lah hace que la noche se convierta en día, y hace que el día se convierta en noche, y que ha puesto en servicio el sol y la luna, cada uno de los cuales sigue su rumbo hasta el plazo fijado; y que Al‑lah conoce perfectamente lo que hacéis?" WHERE ayatNumber = 30 AND chapterNumber = 31;
UPDATE verses SET es = "Esto es porque sólo Al‑lah es el <em>Dios </em>Verdadero, y todo lo que ellos invocan fuera de Él es falsedad, y porque sólo Al‑lah es el Excelso, el Incomparablemente Grande." WHERE ayatNumber = 31 AND chapterNumber = 31;
UPDATE verses SET es = "¿No ves acaso que los buques navegan por el mar por la gracia de Al‑lah, para que os muestre Sus Signos? En ello hay ciertamente Señales para todo el que sea paciente y agradecido." WHERE ayatNumber = 32 AND chapterNumber = 31;
UPDATE verses SET es = "Cuando las olas los cubren como mantos<em> espesos</em>, invocan a Al‑lah, con sinceridad en Él en la fe; pero cuando Él los lleva seguros a tierra firme, entonces algunos de ellos siguen el camino recto. Mas nadie niega Nuestros Signos salvo las personas pérfidas y desagradecidas." WHERE ayatNumber = 33 AND chapterNumber = 31;
UPDATE verses SET es = "Oh, humanos, buscad la pro­tección junto a vuestro Señor y temed el día en que el padre no pueda hacer nada por su hijo ni el hijo pueda hacer nada por su padre. La promesa de Al‑lah es en verdad cierta. No dejéis pues que la vida mundana os seduzca y que el Engañador os en­gañe respecto a Al‑lah." WHERE ayatNumber = 34 AND chapterNumber = 31;
UPDATE verses SET es = "En verdad, sólo Al‑lah posee el conocimiento de la Hora. Él envía la lluvia y sabe lo que hay en los vientres. Nadie sabe lo que ganará mañana ni en qué lugar morirá. En verdad, Al‑lah es el Omnisciente, el Enterado de todo." WHERE ayatNumber = 35 AND chapterNumber = 31;
UPDATE verses SET es = "En el nombre de Al‑lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 32;
UPDATE verses SET es = "Alif Lam Mim." WHERE ayatNumber = 2 AND chapterNumber = 32;
UPDATE verses SET es = "La revelación del Libro ‑no hay duda al respecto<strong>‑ </strong> procede del Señor de los mundos." WHERE ayatNumber = 3 AND chapterNumber = 32;
UPDATE verses SET es = "¿Acaso dicen: “Él lo ha inven­tado?”. No, es la verdad de tu Señor, para que adviertas al pueblo al que no ha llegado ningún Amonestador antes de Ti, para que tal vez sigan la guía." WHERE ayatNumber = 4 AND chapterNumber = 32;
UPDATE verses SET es = "Al‑lah es Quien creó los cielos y la tierra y lo que hay entre ellos, en seis períodos; luego se estableció en el Trono. No tenéis ayuda ni intercesor fuera de Él. ¿Acaso no reflexionaréis?" WHERE ayatNumber = 5 AND chapterNumber = 32;
UPDATE verses SET es = "Él dispone Sus Órdenes desde el Cielo para <em>su</em> <em>ejecución en </em>la tierra y<em>, una vez completado el círculo, </em> vuelve a subir a Él en un día cuya duración, según vuestros cálculos, es de mil años." WHERE ayatNumber = 6 AND chapterNumber = 32;
UPDATE verses SET es = "Así es el Conocedor de lo in­visible y de lo visible, el Poderoso, el Misericordioso;" WHERE ayatNumber = 7 AND chapterNumber = 32;
UPDATE verses SET es = "Quien ha hecho perfecto lo que ha creado. Él inició la creación del hombre de barro;" WHERE ayatNumber = 8 AND chapterNumber = 32;
UPDATE verses SET es = "Luego creó su progenie a partir de un extracto de un líquido insig­nificante;" WHERE ayatNumber = 9 AND chapterNumber = 32;
UPDATE verses SET es = "Más tarde lo equilibró y le insufló Su espíritu. Os dio la audición, los ojos y los corazones. <em>¡Pero </em>qué poco se lo agra­decéis!" WHERE ayatNumber = 10 AND chapterNumber = 32;
UPDATE verses SET es = "Ellos dicen: “¡Cómo! cuando estemos perdidos en la tierra, ¿vamos acaso a convertirnos en una nueva creación?”. No, no son mas que incré­dulos en la reunión con su Señor." WHERE ayatNumber = 11 AND chapterNumber = 32;
UPDATE verses SET es = "Diles: “El ángel de la muerte encargado de vosotros os hará morir; más tarde seréis devuel­tos a vuestro Señor”." WHERE ayatNumber = 12 AND chapterNumber = 32;
UPDATE verses SET es = "Ojalá pudieras ver a los cul­pables, con sus cabezas inclinadas ante su Señor <em>y</em> <em>diciendo</em>: “Señor Nuestro, hemos visto y hemos ído, devuélvenos, pues, para que podamos hacer buenas obras; pues <em>ahora</em> es­tamos convencidos”." WHERE ayatNumber = 13 AND chapterNumber = 32;
UPDATE verses SET es = "Mas si hubiésemos <em>impuesto </em>Nuestra voluntad, podríamos haber ado a cada alma su guía, pero Mi obra, <em>en relación con los malvados, </em>había de cumplirse: “Llenaré el infier­no con Yinn y hombres, todos juntos”." WHERE ayatNumber = 14 AND chapterNumber = 32;
UPDATE verses SET es = "Probad pues <em>el castigo de vuestras obras </em>por haber olvidado la comparecencia de este día vuestro. Nosotros <em>también </em>os hemos olvi­dado. Probad, pues, el castigo dura­dero por lo que solíais hacer." WHERE ayatNumber = 15 AND chapterNumber = 32;
UPDATE verses SET es = "Sólo creen en Nuestros Signos, quienes, cuando se les recuerdan, se postran de bruces, celebran las ala­banzas de su Señor y no son orgu­llosos." WHERE ayatNumber = 16 AND chapterNumber = 32;
UPDATE verses SET es = "Cuyos costados se mantienen apartados de sus lechos; invocan a su Señor con temor y esperanza, y gastan de lo que les hemos con­cedido." WHERE ayatNumber = 17 AND chapterNumber = 32;
UPDATE verses SET es = "No hay alma que sepa la alegría para los ojos que se les man­tiene reservada como recompensa por sus <em>buenas </em>obras." WHERE ayatNumber = 18 AND chapterNumber = 32;
UPDATE verses SET es = "¿Acaso el creyente es como el desobediente? No, no son iguales." WHERE ayatNumber = 19 AND chapterNumber = 32;
UPDATE verses SET es = "En cuanto a quienes creen y practican las buenas obras, tendrán Jardines de Morada Eterna, como premio por lo que solían hacer." WHERE ayatNumber = 20 AND chapterNumber = 32;
UPDATE verses SET es = "Mas en cuanto a los desobedientes, su morada será el Fuego. Cada vez que deseen salir de él, serán devueltos al mismo, y se les dirá: “Probad el castigo del Fuego que acostumbrabais a negar”." WHERE ayatNumber = 21 AND chapterNumber = 32;
UPDATE verses SET es = "Y bien cierto es que les haremos probar el castigo menor antes del castigo mayor, para que vuelvan <em>a Nosotros con arrepentimiento.</em>" WHERE ayatNumber = 22 AND chapterNumber = 32;
UPDATE verses SET es = "¿Quién actúa más injustamente que aquel a quien se le recuerdan los ignos de su Señor y después se aparta de ellos? Nosotros retribuiremos <em>debidamente </em>a los transgresores." WHERE ayatNumber = 23 AND chapterNumber = 32;
UPDATE verses SET es = "Entregamos a Moisés el Libro -no tengas pues duda en cuanto a la reunión con Él‑ y lo convertimos una guía para los Hijos de Israel." WHERE ayatNumber = 24 AND chapterNumber = 32;
UPDATE verses SET es = "E hicimos surgir de entre ellos unos jefes que guiaron<em>al pueblo </em>siguiendo Nuestro mandato, mientras <em>ellos mismos </em>eran perseverantes y mantenían una fe firme en Nuestros Signos." WHERE ayatNumber = 25 AND chapterNumber = 32;
UPDATE verses SET es = "En verdad, tu Señor es Quien juzgará entre ellos en el Día de la Resurrección, sobre aquello en que difieren." WHERE ayatNumber = 26 AND chapterNumber = 32;
UPDATE verses SET es = "¿No les sirve de guía la can­tidad de generaciones que hemos destruido antes de ellos, entre cuyas moradas <em>ahora caminan</em>? Aquí hay ciertamente Signos. ¿No querrán escuchar?" WHERE ayatNumber = 27 AND chapterNumber = 32;
UPDATE verses SET es = "¿No han visto acaso que llevamos el agua a la tierra seca y producimos de ella cosechas de las que comen sus ganados y ellos mismos? ¿Por qué no prestan aten­ción?" WHERE ayatNumber = 28 AND chapterNumber = 32;
UPDATE verses SET es = "Dicen: “¿Cuándo vendrá esta victoria, si sois veraces?”." WHERE ayatNumber = 29 AND chapterNumber = 32;
UPDATE verses SET es = "Diles: “En el día de la vic­toria la fe de los incrédulos no les servirá nada, ni se les concederá respiro”." WHERE ayatNumber = 30 AND chapterNumber = 32;
UPDATE verses SET es = "Aléjate pues de ellos y espera. Ellos también están esperando." WHERE ayatNumber = 31 AND chapterNumber = 32;
UPDATE verses SET es = "En el nombre de Al‑lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 33;
UPDATE verses SET es = "Oh, tú, Profeta, haz de Al‑lah tu escudo y no sigas <em>los</em> <em>deseos de </em>los incrédulos y los hipócritas. En verdad, Al‑lah es el Omnisciente, el Sabio." WHERE ayatNumber = 2 AND chapterNumber = 33;
UPDATE verses SET es = "Sigue lo que te ha sido reve­lado por tu Señor. En verdad, Al‑lah conoce muy bien lo que hacéis." WHERE ayatNumber = 3 AND chapterNumber = 33;
UPDATE verses SET es = "Pon tu confianza en Al‑lah, pues Al‑lah basta como Guardián." WHERE ayatNumber = 4 AND chapterNumber = 33;
UPDATE verses SET es = "Al‑lah no ha creado para ningún hombre dos corazones en su pecho; ni convierte a vuestras esposas en vuestras madres por el mero hecho de que os dirijáis a ellas como a vuestras madres y os abstengáis por ello de mantener relaciones conyugales, ni tampoco convierte a vuestros ijos adop­tados en vuestros hijos <em>reales. </em>Estas son <em>meras </em>palabras de vues­tras bocas; mientras que Al‑lah dice la verdad, y os guía al camino <em>recto.</em>" WHERE ayatNumber = 5 AND chapterNumber = 33;
UPDATE verses SET es = "Llamadles por <em>los</em> <em>nombres de </em>sus padres. Esto es más equitativo a la vista de Al‑lah. Pero si no cono­céis a sus padres, ellos son vuestros hermanos en la fe y vuestros amigos. No se os puede chacar nada por cualquier error sin intención que cometáis en este <em>asunto, </em>salvo lo que vuestros corazones deliberadamente desearon. Pues Al‑lah es el Sumo Indulgente, Mise­ricordioso." WHERE ayatNumber = 6 AND chapterNumber = 33;
UPDATE verses SET es = "El Profeta está más cercano a los creyentes que ellos mismos, y sus esposas son <em>como </em>madres para ellos. Y algunos vínculos de sangre de entre los creyentes y los emigrantes están más cerca que otros en el Libro de Al‑lah, salvo los favores voluntarios que queráis conceder, con bondad, a vuestros amigos personales. Esto es lo que está inscrito en el Libro de la <em>Naturaleza</em>." WHERE ayatNumber = 7 AND chapterNumber = 33;
UPDATE verses SET es = "<em>Y acuérdate </em>de cuando esta­blecimos el pacto con los Profetas, así como contigo, y con Noé, Abraham, Moisés y Jesús, el hijo de María, pues <em>en verdad </em>establecimos con ellos una solemne alianza;" WHERE ayatNumber = 8 AND chapterNumber = 33;
UPDATE verses SET es = "Para que Él interrogue a los veraces sobre su veracidad. Mas para los incrédulos ha preparado un doloroso castigo." WHERE ayatNumber = 9 AND chapterNumber = 33;
UPDATE verses SET es = "¡Oh creyentes!, acordaos del favor de Al‑lah para vosotros cuando cayeron ejércitos sobre vosotros, y enviamos contra ellos a un viento y a ejércitos que vosotros no veíais. Mas Al‑lah ve lo que hacéis." WHERE ayatNumber = 10 AND chapterNumber = 33;
UPDATE verses SET es = "Cuando llegaron sobre voso­tros desde arriba y desde abajo, cuando <em>vuestros </em>ojos se distrajeron y <em>vuestros </em>corazones saltaron a vues­tras gargantas, y mantuvisteis pensamientos rebeldes sobre Al‑lah." WHERE ayatNumber = 11 AND chapterNumber = 33;
UPDATE verses SET es = "<em> Allí y entonces </em>fueron probados <em>amargamente </em>los creyentes, y agitados con una violenta agitación." WHERE ayatNumber = 12 AND chapterNumber = 33;
UPDATE verses SET es = "Cuando los hipócritas y los de corazón enfermo dijeron: “Al‑lah y Su Mensajero no nos han prome­tido más que un engaño”." WHERE ayatNumber = 13 AND chapterNumber = 33;
UPDATE verses SET es = "Y cuando un grupo de ellos dijeron: “Oh, pueblo de Yazrib, <em>tal vez </em>no podáis resistir <em>al enemigo; </em>volveos, pues”. Y un grupo de ellos <em>incluso </em>pidió permiso al Profeta iciendo: “Nuestras casas están ex­puestas <em>e indefensas”. </em>Y <em> en verdad </em>no estaban expuestas. Sólo inten­taban huir." WHERE ayatNumber = 14 AND chapterNumber = 33;
UPDATE verses SET es = "Iban a ser invadidos desde todos los lados de la ciudad, y si se les hubiera pedido que renegaran, lo habrían hecho inmediatamente, y poco habrían dudado." WHERE ayatNumber = 15 AND chapterNumber = 33;
UPDATE verses SET es = "En verdad ya habían hecho un pacto con Al‑lah <em>de que</em> no volverían la espalda. Y de un pacto con a Al‑lah ciertamente habrá que rendir cuentas." WHERE ayatNumber = 16 AND chapterNumber = 33;
UPDATE verses SET es = "Diles: “La huida no servirá si escapáis de la muerte o la matanza; e <em>incluso </em>entonces ganaréis muy poco<em>”.</em>" WHERE ayatNumber = 17 AND chapterNumber = 33;
UPDATE verses SET es = "Diles: “¿Quién puede sal­varos contra Al‑lah si es Su deseo haceros daño o si es Su deseo mos­traros misericordia?”. Mas no encon­trarán amigo ni ayuda alguna ex­cepto en Al‑lah." WHERE ayatNumber = 18 AND chapterNumber = 33;
UPDATE verses SET es = "En verdad Al‑lah conoce a aquellos de vosotros que estorban <em>a los hombres </em>y a los que dicen a sus hermanos: “Venid con nosotros”; y no acuden a la lucha sino durante breves momentos," WHERE ayatNumber = 19 AND chapterNumber = 33;
UPDATE verses SET es = "Siendo poco inclinados a <em>ha­blar bien de vosotros. </em>Pero cuando viene el peligro los ves que miran hacia ti, con los ojos dando vueltas como quien se encuentra aterrado por la cercanía de la muerte. Mas cuando el temor ha pasado, os asal­tan con sus lenguas agudas, codi­ciosos por obtener riquezas. Éstos no han creído nunca; por tanto Al‑lah ha con­vertido sus obras en nulas y sin mérito; esto es algo fácil para Al‑lah." WHERE ayatNumber = 20 AND chapterNumber = 33;
UPDATE verses SET es = "Piensan que las tribus <em>invasoras</em> no se han ido; mas si las tribus invadieran de <em>nuevo, </em>preferirían estar entre los árabes nómadas del desierto, pidiendo noticias sobre vosotros. Pero si estuvieran entre vosotros, no lucharían sino poco." WHERE ayatNumber = 21 AND chapterNumber = 33;
UPDATE verses SET es = "En verdad, tenéis en el Pro­feta de Al‑lah un dechado de vir­tudes para quien tema a Al‑lah y al Último Día y se acuerde mucho de Al‑lah." WHERE ayatNumber = 22 AND chapterNumber = 33;
UPDATE verses SET es = "Mas cuando los creyentes vieron a los confederados, dijeron: “Esto es lo que Al‑lah y Su Mensa­jero nos prometieron, y Al‑lah y Su Mensajero dijeron la verdad”. Y esto no hizo más que aumentar su fe y su sumisión." WHERE ayatNumber = 23 AND chapterNumber = 33;
UPDATE verses SET es = "Entre los creyentes hay hom­bres que han respetado la alianza que hicieron con Al‑lah. Hay <em>al­gunos </em>de ellos que cumplieron su promesa y <em>otros</em> que siguen espe­rando y no han cambiado nada." WHERE ayatNumber = 24 AND chapterNumber = 33;
UPDATE verses SET es = "Para que Al‑lah recompense al veraz por su verdad, y castigue a los hipócritas si así lo desea, o les muestre misericordia. En verdad, es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 25 AND chapterNumber = 33;
UPDATE verses SET es = "Al‑lah rechazó en su cólera a los incrédulos: no consiguieron bien alguno. Pues Al‑lah bastó para los creyentes en su batalla. Porque Al‑lah es Potente, Poderoso." WHERE ayatNumber = 26 AND chapterNumber = 33;
UPDATE verses SET es = "Él ha desalojado de sus fortalezas a aquellos del Pueblo del libro que les ayudaron y ha encendido el temor en sus corazones. Matasteis algunos y a otros los hicisteis pri­sioneros." WHERE ayatNumber = 27 AND chapterNumber = 33;
UPDATE verses SET es = "Y Él os hizo heredar su tierra, sus casas y sus riquezas, y un país en el que nunca pusisteis vuestros pies. Pues Al‑lah tiene poder sobre todas las cosas." WHERE ayatNumber = 28 AND chapterNumber = 33;
UPDATE verses SET es = "¡Oh, Profeta! di a tus esposas: “Si deseáis la vida de este mundo y sus adornos, venid pues; os proveeré y os liberaré de manera decorosa." WHERE ayatNumber = 29 AND chapterNumber = 33;
UPDATE verses SET es = "“Pero si deseáis a Al‑lah y a Su Mensajero y la Morada del Más Allá, entonces <em>sabed que</em> Al‑lah en verdad ha preparado una gran recompensa para aquellas de vosotras que prac­tiquen el bien”." WHERE ayatNumber = 30 AND chapterNumber = 33;
UPDATE verses SET es = "¡Oh, esposas del Profeta! Si una de vosotras fuese culpable de conducta manifiestamente deshonrosa, el castigo le será duplicado. Y esto es fácil para Al‑lah." WHERE ayatNumber = 31 AND chapterNumber = 33;
UPDATE verses SET es = "Pero a aquella de vosotras que obedezca a Al‑lah y a Su Mensa­jero y haga buenas obras, le daremos su recompensa duplicada; pues le he­mos preparado una provi­sión honorable." WHERE ayatNumber = 32 AND chapterNumber = 33;
UPDATE verses SET es = "¡Oh, esposas del Profeta! sois distintas a <em>otras</em> mujeres a condición de que seáis justas. No seáis pues demasiado blandas en vuestras palabras, para que no se sienta tentado quien tenga el corazón enfermo; y hablad con palabras pudorosas." WHERE ayatNumber = 33 AND chapterNumber = 33;
UPDATE verses SET es = "Permaneced en vuestras casas <em>con dignidad, </em>no os adornéis al estilo que os embellecíais en los días de la ig­norancia, cumplid la Oración, pagad el <em>Zakat </em>y obedeced a Al‑lah y a Su Mensajero. En verdad, Al‑lah desea alejar de vosotras todo lo impuro, Oh Miembros de la Casa, y purifi­caros completamente." WHERE ayatNumber = 34 AND chapterNumber = 33;
UPDATE verses SET es = "Y acordaos de lo que se recita en vuestras casas de los Signos de Al‑lah y de la sabiduría. En ver­dad, Al‑lah es Quien conoce las suti­lezas, el Sumo Conocedor." WHERE ayatNumber = 35 AND chapterNumber = 33;
UPDATE verses SET es = "En verdad, los hombres que se someten <em>a Dios </em>y las mujeres que se someten <em>a Él,</em> los hombres y mujeres que creen, los hombres y mujeres obedientes, los hombres y ujeres veraces, los hombres y mu­jeres perseverantes <em>en su fe, </em>los hom­bres y mujeres humildes, los hombres y ujeres que dan limosnas, que ayunan, que guardan <em>su</em> <em>castidad, </em>los hombres que recuerdan mucho a Al‑lah y las mujeres que Lo recuer­dan mucho: para <em>todos </em>ellos Al‑lah ha preparado Su perdón y una mag­nífica recompensa." WHERE ayatNumber = 36 AND chapterNumber = 33;
UPDATE verses SET es = "No corresponde a un hombre creyente o a una mujer creyente, cuando Al‑lah y Su Mensajero han decidido un asunto, poder elegir en lo que les afecte. Pues quien desobedece a Al‑lah y a Su Mensajero, en verdad se aparta en error evidente." WHERE ayatNumber = 37 AND chapterNumber = 33;
UPDATE verses SET es = "Y <em>acuérdate </em>de cuando dijiste a quien Al‑lah había concedido Sus favores y a quien tú también con­cediste tus favores: “Conserva tu esposa para ti y teme a Al‑lah”. Mas escondiste en tu corazón lo que A1‑lah iba a sacar a la luz, y tuviste miedo de los hombres, mientras que Al‑lah tenía mejor derecho a que Lo temieras. espués, cuando Zaid hubo cumplido lo que quería de ella, <em>hasta el punto de no nece­sitarla más, </em>la unimos en matrimonio contigo, para que no hubiese impedi­mento para los creyentes en cuanto a las esposas de sus hijos adoptados, una vez cumplido su deseo. Y el decreto de Al‑lah debe cumplir­se." WHERE ayatNumber = 38 AND chapterNumber = 33;
UPDATE verses SET es = "No puede achacarse culpa al­guna al Profeta respecto a lo que Al‑lah le ha hecho responsable. <em>Ésa fue, en efecto, </em>la costumbre de Al‑lah con los que vivieron antes ‑y el mandamiento de Al‑lah es un decreto promulgado‑" WHERE ayatNumber = 39 AND chapterNumber = 33;
UPDATE verses SET es = "Quienes entregaron el Men­saje de Al‑lah y Lo temieron, y no temieron a nadie sino a Al‑lah. Pues Al‑lah basta para el cómputo." WHERE ayatNumber = 40 AND chapterNumber = 33;
UPDATE verses SET es = "Mohammad no es el padre de ninguno de vuestros hombres, sino que <em>es</em> el Mensajero de Al‑lah y el Sello de los Profetas; y Al‑lah conoce perfectamente todas las cosas." WHERE ayatNumber = 41 AND chapterNumber = 33;
UPDATE verses SET es = "¡Oh, vosotros, los que creéis! Acordaos con frecuencia de Al‑lah;" WHERE ayatNumber = 42 AND chapterNumber = 33;
UPDATE verses SET es = "Y glorificadle por la mañana y por la tarde." WHERE ayatNumber = 43 AND chapterNumber = 33;
UPDATE verses SET es = "Es Él Quien envía bendiciones sobre vosotros, como <em>hacen </em> us án­geles, para que os lleve desde todas <em>las clases de </em>tinieblas a la luz. Pues Él es Misericordioso con los creyentes." WHERE ayatNumber = 44 AND chapterNumber = 33;
UPDATE verses SET es = "Su saludo en el día en que Lo encuentren, será “paz”. Pues Él les ha preparado una recompensa honrosa." WHERE ayatNumber = 45 AND chapterNumber = 33;
UPDATE verses SET es = "¡Oh, Profeta!, en verdad te hemos enviado como Testigo, Por­tador de la buena nueva y Amo­nestador." WHERE ayatNumber = 46 AND chapterNumber = 33;
UPDATE verses SET es = "Como Convocador a Al‑lah por Su orden y como una Lámpara radiante." WHERE ayatNumber = 47 AND chapterNumber = 33;
UPDATE verses SET es = "Anunciad a los creyentes la buena nueva de que recibirán una importante gracia de Al‑lah." WHERE ayatNumber = 48 AND chapterNumber = 33;
UPDATE verses SET es = "Y no sigas a los incrédulos ni a los hipócritas, ignora sus molestias y pon tu confianza en Al‑lah; pues Al‑lah es suficiente como Guardián." WHERE ayatNumber = 49 AND chapterNumber = 33;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! cuando contraigáis matrimonio con mujeres creyentes y después os divor­ciéis de ellas antes de que las hayáis tocado, no tendréis derecho alguno contra ellas respecto al período de espera <em>prescrito </em>que calculáis<em>. </em>Dadles pues alguna provisión y despedidlas de manera hono­rable." WHERE ayatNumber = 50 AND chapterNumber = 33;
UPDATE verses SET es = "¡Oh, Profeta!, Te hemos hecho lícitas tus esposas, cuyas dotes has pagado, las que posee tu mano dere­cha de entre aquellas que Al‑lah te ha dado como ganancia de guerra, las hijas de tu tío paterno, las hijas de tus tías paternas, las hijas de tu tío materno y las hijas de tus tías ma­ternas que han emigrado contigo, y cualquier <em>otra </em> mujer creyente si se ofrece <em>en matrimonio </em>al Profeta, a condición de que el Profeta desee casarse con ella: <em>esto es </em> sólo para ti, y no para <em>otros </em>creyentes- ya hemos dado a conocer lo que les hemos orde­nado a ellos en cuanto a sus esposas y a as que poseen sus manos derechas- para que no haya dificultad para ti <em>en el cumplimiento de tu tarea. </em>Pues Al‑lah es el Sumo Indul­gente, Misericordioso<strong>. </strong>" WHERE ayatNumber = 51 AND chapterNumber = 33;
UPDATE verses SET es = "Puedes diferir <em>casarte </em>con cualquiera de entre ellas si lo deseas, y recibir a quien te agrade; y si deseas tomar de nuevo a alguna de las que hayas dejado a un lado, no tendrás culpa al­guna. Eso será lo más adecuado para que sus ojos se consuelen, para que no sufran aflicción y todas se contenten con lo que tú les hayas dado. Pues Al‑lah sabe lo que hay en vuestros corazones; y Al‑lah es Omnisciente, Tolerante." WHERE ayatNumber = 52 AND chapterNumber = 33;
UPDATE verses SET es = "No se te permite <em>contraer ma­trimonio con </em>mujeres después de eso, ni cambiarlas por <em>otras </em>esposas, aunque su bondad te agrade, excepto cualquiera que posea tu mano de­recha. Pues Al‑lah es Vigilante sobre todas las cosas." WHERE ayatNumber = 53 AND chapterNumber = 33;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! no entréis en las asas del Profeta salvo que se os invite a una comida, y aun entonces <em>no tan pronto </em>que ten­gáis que esperar a su prepara­ción. Pero entrad cuando seáis invitados, y cuando hayáis terminado de comer, retiraos, sin intentar iniciar una conversación. Esto molestaría al Profeta y Él se avergonzaría de <em>pediros que salgáis.</em> Pero Al-lah no se avergüenza <em>de decir</em> lo que es cierto. Y cuando les pidáis algo (a las esposas del Profeta), pedídselo desde detrás de una cortina. Esto es más puro para vuestros corazones y para los de ellas. Y os corresponde no causar molestia al­guna al Mensajero de Al‑lah, ni con­traer nunca matrimonio con sus es­posas después de él. En verdad esto sería una enormidad a los ojos de Al‑lah." WHERE ayatNumber = 54 AND chapterNumber = 33;
UPDATE verses SET es = "Tanto si manifestáis algo como si lo ocultáis, Al‑lah conoce per­fectamente todas las cosas." WHERE ayatNumber = 55 AND chapterNumber = 33;
UPDATE verses SET es = "No hay culpa en ellos a <em>este respecto </em>en cuanto a sus padres, sus hijos, sus hermanos o los hijos de sus hermanos, los hijos de sus hermanas o sus mujeres o aquellos que sus manos derechas poseen. Temed a Al‑lah, <em>Oh esposas del Profeta, </em>pues en verdad Al‑lah es Testigo de todas las cosas." WHERE ayatNumber = 56 AND chapterNumber = 33;
UPDATE verses SET es = "Al‑lah y Sus ángeles envían bendiciones al Profeta. ¡Oh vosotros, los que creéis! invocad <em>también </em>ben­diciones sobre él y saludad<em>le</em> con el saludo de la paz." WHERE ayatNumber = 57 AND chapterNumber = 33;
UPDATE verses SET es = "En verdad, a quienes molestan a Al‑lah y a Su Mensajero, Al‑lah los ha maldecido en este mundo y en el Más Allá, y ha pre­parado para ellos un humillante cas­tigo." WHERE ayatNumber = 58 AND chapterNumber = 33;
UPDATE verses SET es = "Y quienes molestan a los cre­yentes y a las creyentes por lo que no han merecido, serán <em>culpables de </em>ca­lumnia y de pecado manifiesto." WHERE ayatNumber = 59 AND chapterNumber = 33;
UPDATE verses SET es = "¡Oh, Profeta!, di a tus esposas, a tus hijas y a las mujeres de los creyentes, que deben bajar sus túnicas externas desde su cabeza sobre su rostro. Esto es más conveniente para que sean <em>así </em> reco­nocidas y no molestadas. Pues Al‑lah, es el Sumo Indulgente, Misericor­dioso." WHERE ayatNumber = 60 AND chapterNumber = 33;
UPDATE verses SET es = "Si los hipócritas, y los de corazón enfermo y quienes propagan rumores sin fundamento por la ciudad no desisten, ciertamente haremos que te levantes contra ellos; luego no permanecerán junto a vosotros salvo por poco tiempo." WHERE ayatNumber = 61 AND chapterNumber = 33;
UPDATE verses SET es = "<em>Entonces serán ellos </em>malditos. En cualquier lugar en que se encuentren serán apresados y cortados en trozos." WHERE ayatNumber = 62 AND chapterNumber = 33;
UPDATE verses SET es = "Así ha sido el modo de actuar de Al‑lah en <em>el caso de </em> quienes mu­rieron antes, y nunca encontrarás un cambio en el modo de obrar de Al‑lah." WHERE ayatNumber = 63 AND chapterNumber = 33;
UPDATE verses SET es = "Los hombres te preguntan sobre la Hora. Diles: “Su conocimien­to corresponde <em>sólo</em> a Al‑lah”. ¿Y qué te dará a conocer que la Hora está próxima?" WHERE ayatNumber = 64 AND chapterNumber = 33;
UPDATE verses SET es = "Al‑lah ha maldecido cierta­mente a los incrédulos y ha prepa­rado para ellos un fuego llameante," WHERE ayatNumber = 65 AND chapterNumber = 33;
UPDATE verses SET es = "En el que vivirán para siem­pre. <em>En él </em>no encontrarán amigo ni ayudante." WHERE ayatNumber = 66 AND chapterNumber = 33;
UPDATE verses SET es = "En el día en que sus rostros sean vueltos hacia el Fuego, dirán: “¡Oh, ojalá hubiésemos obedecido a Al‑lah y al Mensajero!”." WHERE ayatNumber = 67 AND chapterNumber = 33;
UPDATE verses SET es = "Y dirán: “Señor nuestro, obe­decimos a nuestros jefes y a los grandes de entre nosotros y ellos nos apartaron del camino," WHERE ayatNumber = 68 AND chapterNumber = 33;
UPDATE verses SET es = "“Señor nuestro, dales un doble castigo y maldícelos con la mayor de las maldiciones”." WHERE ayatNumber = 69 AND chapterNumber = 33;
UPDATE verses SET es = "¡Oh vosotros, los que creéis!, no seáis como los que ofendieron y<em>calumniaron</em> a Moisés; Al‑lah lo absolvió de lo que decían <em>de él</em>. Y fue honorable ante los ojos de Al‑lah." WHERE ayatNumber = 70 AND chapterNumber = 33;
UPDATE verses SET es = "¡Oh vosotros, los que creéis!, temed a Al‑lah y decid la palabra justa." WHERE ayatNumber = 71 AND chapterNumber = 33;
UPDATE verses SET es = "Él reformará vuestra conducta y perdonará vuestros pecados. Pues quien obedezca a Al‑lah y a Su Men­sajero, alcanzará ciertamente el mayor de los éxitos." WHERE ayatNumber = 72 AND chapterNumber = 33;
UPDATE verses SET es = "En verdad, ofrecimos la Confianza a los cielos, y a la tierra, y a las montañas, pero se negaron a aceptarla y la temieron. Pero el hombre la aceptó. En verdad, fue demasiado cruel hacia <em>sí mismo</em>, y no consideró las consecuencias." WHERE ayatNumber = 73 AND chapterNumber = 33;
UPDATE verses SET es = "<em>El resultado es</em> que Al-lah castigará a los hombres y mujeres hipócritas, y a los hombres y mujeres idólatras; pero Al-lah se vuelve con misericordia a los hombres y mujeres creyentes; pues Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 74 AND chapterNumber = 33;
UPDATE verses SET es = "En el nombre de Al‑lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 34;
UPDATE verses SET es = "Toda alabanza corresponde a Al‑lah, a Quien pertenece todo lo que hay en los cielos y todo lo que hay en la tierra. Suya es toda la alabanza en el Más Allá; pues Él es el Sabio, el Consciente." WHERE ayatNumber = 2 AND chapterNumber = 34;
UPDATE verses SET es = "Él conoce lo que penetra en la tierra y lo que procede de ella, cuanto desciende del cielo y cuanto a él sube; pues Él es Misericordioso, Sumo Indulgente." WHERE ayatNumber = 3 AND chapterNumber = 34;
UPDATE verses SET es = "Los que no creen dicen: “La Hora nunca vendrá sobre nosotros”. Diles: “Sí, por mi Señor que conoce lo oculto, ciertamente caerá sobre vo­sotros. Ni el peso de un átomo en los cielos o en la tierra, ni nada menor o mayor se Le escapa, sino que <em>todo está registrado </em>en un Libro lúcido," WHERE ayatNumber = 4 AND chapterNumber = 34;
UPDATE verses SET es = "“Para que recompense a los que creen y hacen buenas obras. Son éstos quienes tendrán el perdón y una provisión honorable”." WHERE ayatNumber = 5 AND chapterNumber = 34;
UPDATE verses SET es = "Pero <em>en cuanto a </em>quienes luchan contra Nuestros Signos, inten­tando frustrar <em>Nuestros planes, </em>para ellos habrá el sufrimiento de un do­loroso castigo." WHERE ayatNumber = 6 AND chapterNumber = 34;
UPDATE verses SET es = "Los que han recibido conoci­miento ven que todo lo que te ha sido <strong> </strong>revelado por tu Señor es la verdad, y que guía por el camino del Poderoso, el Merecedor de Alabanza." WHERE ayatNumber = 7 AND chapterNumber = 34;
UPDATE verses SET es = "Quienes no creen dicen: <em>“¿Hará falta que </em>os mostremos a un hombre que os diga <em>que </em>cuando seáis redu­cidos a fragmentos, seréis <em>resucitados </em>como nueva criatura?" WHERE ayatNumber = 8 AND chapterNumber = 34;
UPDATE verses SET es = "“¿Ha forjado él una mentira contra Al‑lah o le ha afligido la locura?”. No, pero quienes no creen en el Más Allá son los que sufren el castigo y han ido dema­siado lejos en el error." WHERE ayatNumber = 9 AND chapterNumber = 34;
UPDATE verses SET es = "¿Acaso no ven lo que hay delante de ellos y lo que hay detrás de ellos del cielo y de la tierra? Si queremos, podemos hacer que la tierra se hunda con ellos o hacer que sobre ellos caigan varios pedazos desde el cielo. Aquí hay en verdad un Signo para cualquier siervo arrepen­tido." WHERE ayatNumber = 10 AND chapterNumber = 34;
UPDATE verses SET es = "Ciertamente concedimos Nues­tra Gracia a David: “Oh montañas, olveos a Dios con plena sumisión junto a é1, y Oh vosotros pájaros, <em>haced lo mismo</em>”. E hicimos que el hierro fuese blan­do para él." WHERE ayatNumber = 11 AND chapterNumber = 34;
UPDATE verses SET es = "Diciéndole: “Haz cotas de ma­lla para todo el cuerpo y haz los anillos de la medida adecuada. Y prac­ticad el bien, pues ciertamente veo todo lo que hacéis”." WHERE ayatNumber = 12 AND chapterNumber = 34;
UPDATE verses SET es = "Y <em>sometimos</em> el viento a Salomón; su trayecto de la mañana ue <em>un viaje </em>de un mes, y su trayecto de la tarde <em>un viaje</em> <em>también </em>de un mes. E hicimos que ara él fluyera una fuente de cobre fundido. Y entre los Yinn hubo<em>algunos </em>que trabajaron para él, por orden de su Señor. Y<em>les</em> <em>habíamos dicho que </em>si cualquiera <strong> </strong>de ellos se apartaba de Nuestro man­damiento, le haríamos probar el cas­tigo del fuego llameante." WHERE ayatNumber = 13 AND chapterNumber = 34;
UPDATE verses SET es = "Hicieron para él lo que deseó: palacios y estatuas, depósitos como estanques, y grandes recipientes para cocinar fijados en sus lugares: “Trabajad, Oh Casa de David, con agradecimiento”; pero muy pocos de Mis siervos son agradecidos." WHERE ayatNumber = 14 AND chapterNumber = 34;
UPDATE verses SET es = "Mas cuando decretamos su muerte (la de Salomón), nada les indicó que había muerto salvo una criatura de la tierra que devoró su báculo. Por eso, cuando cayó, los Yinn comprendieron claramente que si hubiesen sabido lo oculto, no ha­brían permanecido en estado de tor­mento ignominioso." WHERE ayatNumber = 15 AND chapterNumber = 34;
UPDATE verses SET es = "Aquí hubo en verdad un Signo para Saba en su propio país: dos jardines a la derecha y a la izquierda. “Comed de la provisión de uestro Señor y sed agradecidos a Él. ¡Una buena tierra y un Señor Sumo <strong> </strong>In­dulgente!”." WHERE ayatNumber = 16 AND chapterNumber = 34;
UPDATE verses SET es = "Pero se apartaron, por lo que enviamos contra ellos una inunda­ción devastadora <em>procedente de</em> la rotura de un dique. Y en lugar de sus jardines, les dimos dos jardines que daban frutos amargos, tama­rindos y algunos lotos." WHERE ayatNumber = 17 AND chapterNumber = 34;
UPDATE verses SET es = "Que les proporcionamos debido a su ingratitud; y a nadie retribuimos <em>de este modo </em>excepto a los desagra­decidos." WHERE ayatNumber = 18 AND chapterNumber = 34;
UPDATE verses SET es = "Y, entre ellos y las ciudades que habían sido bendecidas por No­sotros, situamos <em>otras </em>ciudades que eran claramente visibles, y fijamos etapas fáciles entre ellas: “Viajad por ellas de noche y de día con toda seguridad”." WHERE ayatNumber = 19 AND chapterNumber = 34;
UPDATE verses SET es = "Pero dijeron: “Señor Nuestro, pon distancias más largas entre <em>las etapas de </em>nuestros viajes”. Y se perjudicaron ellos mismos; por tanto, los convertimos en leyendas y los hicimos pedazos, reduciéndolos a trozos. Aquí hay en verdad Signos para cualquier persona perseverante y agradecida." WHERE ayatNumber = 20 AND chapterNumber = 34;
UPDATE verses SET es = "Iblis demostró en verdad que la valoración que había hecho de ellos era correcta, por lo que ellos lo siguieron, <em>todos </em>excepto un grupo de <em>auténticos </em>creyentes." WHERE ayatNumber = 21 AND chapterNumber = 34;
UPDATE verses SET es = "Y no tenía poder sobre ellos, pero ocurrió <em>así </em>para que pudiéramos distinguir a los que creían en el Más Allá de los que dudaban sobre él. Pues tu Señor es Vigilante de todas las cosas." WHERE ayatNumber = 22 AND chapterNumber = 34;
UPDATE verses SET es = "Diles: “Invocad a aquellos que afirmáis que <em>son dioses </em> fuera de Al‑lah. Ellos no controlan <em>ni siquiera </em>el peso de un átomo en los cielos ni en la tierra, ni tienen parte alguna en ambos, ni tiene Él nadie que le ayude de entre ellos”." WHERE ayatNumber = 23 AND chapterNumber = 34;
UPDATE verses SET es = "Ante Él no sirve intercesión alguna salvo para quien Él se<em>lo</em> permite, hasta que, cuando sus corazones pierden <strong> </strong>el temor, di­cen: “¿Qué es lo que dijo tu Señor?”. Ellos (los Mensajeros) respon­derán: “La verdad”. Pues Él es el Alto, el Grande. " WHERE ayatNumber = 24 AND chapterNumber = 34;
UPDATE verses SET es = "Diles: “¿Quién os da el sostén desde los cielos y la tierra?”. Diles: “Al‑lah. O nosotros o vosotros se­guimos la guía correcta o estamos en error evidente”." WHERE ayatNumber = 25 AND chapterNumber = 34;
UPDATE verses SET es = "Diles: “No seréis interrogados sobre nuestros pecados, ni nosotros seremos interrogados sobre lo que vosotros hacéis”." WHERE ayatNumber = 26 AND chapterNumber = 34;
UPDATE verses SET es = "Diles: “Nuestro Señor nos reu­nirá a todos, entonces Él juzgará entre nosotros con verdad; pues Él es el Árbitro, el Omnisciente”." WHERE ayatNumber = 27 AND chapterNumber = 34;
UPDATE verses SET es = "Diles. “Mostradme aquellos que habéis asociado a Él como partí­cipes”. ¡No!, <em>no podéis hacerlo, </em>pues Él es Al‑lah, el Poderoso, el Sabio." WHERE ayatNumber = 28 AND chapterNumber = 34;
UPDATE verses SET es = "No te hemos enviado sino como portador de la buena nueva y Amonestador para toda la humanidad pero la mayoría de los hombres no lo saben." WHERE ayatNumber = 29 AND chapterNumber = 34;
UPDATE verses SET es = "Y dicen: “¿Cuándo <em>se cum­plirá </em>esta promesa, si sois veraces?”." WHERE ayatNumber = 30 AND chapterNumber = 34;
UPDATE verses SET es = "Diles: “Para vosotros es la promesa de un día en el que no podréis quedaros atrás ni un solo momento, ni adelantaros a <em>él”.</em>" WHERE ayatNumber = 31 AND chapterNumber = 34;
UPDATE verses SET es = "Y quienes no creen dicen: “Nun­ca creeremos en este Corán, ni en lo que existió antes de él”. Ojalá pu­dieras ver cuando se haga que los inicuos comparezcan ante su Señor, y se echen mutuamente la culpa. Los que eran considerados débiles dirán a los orgullosos: “De no haber sido por vosotros, ciertamente hubiésemos sido creyentes”." WHERE ayatNumber = 32 AND chapterNumber = 34;
UPDATE verses SET es = "Los que fueron orgullosos dirán a los considerados débiles: “¿Acaso fuimos nosotros los que os apartamos de la guía, después de que os vino? No, fuisteis vosotros mismos los culpables”." WHERE ayatNumber = 33 AND chapterNumber = 34;
UPDATE verses SET es = "Mas los considerados débiles dirán a los que fueron orgullosos: “No, fueron <em>vuestras </em>maquinaciones de día y noche, cuando nos ordenabais no creer en Al‑lah y ensalzar a otros como iguales a Él; ocul­tarán <em>su </em>remordimiento cuando vean el castigo; y Nosotros pondre­mos cadenas alrededor de los cuellos de los que no creyeron. Sólo serán retribuidos por lo que hicieron." WHERE ayatNumber = 34 AND chapterNumber = 34;
UPDATE verses SET es = "Nunca enviamos a un Amones­tador a población alguna sin que sus ricos dijeran: “En verdad, no cree­mos en aquello que os ha sido re­velado”." WHERE ayatNumber = 35 AND chapterNumber = 34;
UPDATE verses SET es = "Y dicen: “Tenemos más riquezas e hijos; no seremos castigados”." WHERE ayatNumber = 36 AND chapterNumber = 34;
UPDATE verses SET es = "Diles: “En verdad, mi Señor amplía la provisión a quien Le place, y la estrecha <em>para quien quiere; </em>aunque la mayoría de los hombres no lo saben”." WHERE ayatNumber = 37 AND chapterNumber = 34;
UPDATE verses SET es = "No son vuestra riqueza ni vuestros hijos los que os llevarán más cerca de Nosotros en considera­ción, pero quienes crean y prac­tiquen las buenas obras, tendrán una doble recompensa por lo que hicieron. Y en mansiones elevadas estarán seguros." WHERE ayatNumber = 38 AND chapterNumber = 34;
UPDATE verses SET es = "<em>En cuanto </em>a los que se es­fuerzan por frustrar <em>la finalidad de </em>Nuestros Signos, ellos son quienes se enfrentarán cara a cara con el castigo." WHERE ayatNumber = 39 AND chapterNumber = 34;
UPDATE verses SET es = "Diles: “En verdad, mi Señor amplía la provisión para aquellos de us siervos que Le agradan y <em>la</em> estrecha para aquellos que <em>Él quiere. </em>Y todo lo que empleéis, Él lo re­pondrá; pues Él es el Mejor de los Proveedores”." WHERE ayatNumber = 40 AND chapterNumber = 34;
UPDATE verses SET es = "<em>Acuérdate </em>del día en que Él los reunirá a todos; entonces dirá a los ángeles: “¿Eras tu, <em>en particular</em>, a quien adoraban?”." WHERE ayatNumber = 41 AND chapterNumber = 34;
UPDATE verses SET es = "Responderán: “Santo eres Tú. Eres nuestro Protector contra ellos. No, pero adoraban a los Yinn; en ellos era en quienes creían la mayoría”." WHERE ayatNumber = 42 AND chapterNumber = 34;
UPDATE verses SET es = "“Así, en este día, no tendréis poder para beneficiaros o perjudicaros mutuamente”. Y diremos a los que actuaron erróneamente: “Probad el castigo del Fuego que negasteis”." WHERE ayatNumber = 43 AND chapterNumber = 34;
UPDATE verses SET es = "Y cuando les recitan Nues­tros Signos manifiestos, dicen: “Éste no es más que un hombre que intenta apartaros de lo que adoraban vues­tros padres”. Y dicen: “No es más que una mentira inventada”. Y quienes no creen, cuando les llega la verdad, dicen sobre ella: “Esto no es más que magia evidente”." WHERE ayatNumber = 44 AND chapterNumber = 34;
UPDATE verses SET es = "No les dimos libros que estudiaron, ni les enviamos Amonestador alguno antes de ti." WHERE ayatNumber = 45 AND chapterNumber = 34;
UPDATE verses SET es = "Mas quienes existieron antes que ellos, rechazaron<em>también la verdad, </em>y éstos no han alcanzado <em>ni siquiera </em>una décima parte de lo que les dimos, pero trataron a Mis Mensajeros como mentirosos. ¡Qué <em>terrible </em>fue pues el cambio que <em>Yo</em> <em>les traje!</em>" WHERE ayatNumber = 46 AND chapterNumber = 34;
UPDATE verses SET es = "Diles: “Sólo os exhorto <em>a hacer </em>una cosa: A que permanezcáis de pie ante Al‑lah, de dos en dos e indivi­dualmente, y luego reflexionéis. <em>En­tonces sabréis que </em>no hay locura alguna en vuestro compañero; él no es más que un Amonestador que os advierte del severo castigo que os espera”." WHERE ayatNumber = 47 AND chapterNumber = 34;
UPDATE verses SET es = "Diles: “Cualquier recompen­sa que haya podido pedir de vosotros, que sea vuestra. Mi recompensa sólo corresponde a Al‑lah; y Él es Testigo sobre todas las cosas”." WHERE ayatNumber = 48 AND chapterNumber = 34;
UPDATE verses SET es = "Diles: “En verdad, Mi Señor arroja la Verdad <em>contra la falsedad. </em>Él es el Gran Conocedor de lo invi­sible”." WHERE ayatNumber = 49 AND chapterNumber = 34;
UPDATE verses SET es = "Diles: “Ha llegado la Verdad” y la falsedad no puede iniciar ni repetir <em>nada</em>”." WHERE ayatNumber = 50 AND chapterNumber = 34;
UPDATE verses SET es = "Diles: “Si me extravío, me extravío únicamente contra mí mis­mo; y si estoy rectamente guiado, es gracias a lo que mi Señor me ha revelado. En verdad, Él es Quien todo lo oye, el Cercano”." WHERE ayatNumber = 51 AND chapterNumber = 34;
UPDATE verses SET es = "¡Ojalá pudieras ver cuando sean sobrecogidos por el temor! En­tonces no habrá escapatoria, y serán atrapados desde un lugar cercano." WHERE ayatNumber = 52 AND chapterNumber = 34;
UPDATE verses SET es = "Y dirán: “Ahora creemos en ello”. Pero ¿cómo podrán alcanzarlo <em>ahora </em>habiéndose alejado tanto?." WHERE ayatNumber = 53 AND chapterNumber = 34;
UPDATE verses SET es = "En verdad, lo habían rechazado antes entreteniéndose con conjeturas atrevidas desde una posición distante." WHERE ayatNumber = 54 AND chapterNumber = 34;
UPDATE verses SET es = "Mas se colocará una barrera entre ellos y lo que desean, como se izo con sus afines que antes existieron. En verdad que <em>también </em>estuvieron en una duda in­quietante." WHERE ayatNumber = 55 AND chapterNumber = 34;
UPDATE verses SET es = "En el nombre de Al‑lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 35;
UPDATE verses SET es = "Toda alabanza corresponde a Al‑lah, el Hacedor de los cielos y de la tierra, Quien emplea a los ángeles como Mensajeros, que tienen alas, dos, tres y cuatro. Él añade a <em>Su </em>creación lo que Le agrada; pues Al‑lah tiene poder sobre todas las cosas." WHERE ayatNumber = 2 AND chapterNumber = 35;
UPDATE verses SET es = "Sea cual fuere lo que Al‑lah concede a los hombres de Su misericordia, nadie puede impedirlo; y sea cual fuere lo que Él escatima, nadie puede prodigarlo después; pues Él es Poderoso, el Sabio." WHERE ayatNumber = 3 AND chapterNumber = 35;
UPDATE verses SET es = "Oh, humanos, recordad el favor de Al‑lah para con vosotros. ¿Hay algún Creador fuera de Al‑lah <em>que </em>os provea del cielo y de la tierra? Nadie hay merecedor de adoración fuera de Él. ¿Os apartáis a pesar de todo?" WHERE ayatNumber = 4 AND chapterNumber = 35;
UPDATE verses SET es = "Mas si te rechazan, <em>sabe que</em> en verdad los Mensajeros <em>de Dios </em>han sido rechazados antes de ti; mas a Al‑lah serán devueltas <em>todas </em>las cosas <em>para la decisión.</em>" WHERE ayatNumber = 5 AND chapterNumber = 35;
UPDATE verses SET es = "Oh, humanos, ciertamente la promesa de Al‑lah es verdadera, no dejéis, pues, que la vida actual os engañe, ni que el Engañador os en­gañe respecto a Al‑lah." WHERE ayatNumber = 6 AND chapterNumber = 35;
UPDATE verses SET es = "En verdad, Satanás es ene­migo vuestro; tomadlo pues como enemigo; él llama únicamente a sus seguidores para que se conviertan en moradores del Fuego Ardiente." WHERE ayatNumber = 7 AND chapterNumber = 35;
UPDATE verses SET es = "Para los incrédulos existe un severo castigo. Mas para los que creen y practican las buenas obras está el perdón y una gran recom­pensa." WHERE ayatNumber = 8 AND chapterNumber = 35;
UPDATE verses SET es = "¿Acaso es aquel cuyas obras se le hacen aparecer atractivas, de orma que las considera buenas, <em>como</em> <em>el que cree y verdaderamente practica el bien? </em>En verdad, Al‑lah juzga como extraviado a quien quiere y guía a quien Le place. No dejes pues que tu alma se mortifique llorando por ellos. En verdad Al‑lah sabe lo que hacen." WHERE ayatNumber = 9 AND chapterNumber = 35;
UPDATE verses SET es = "Pues Al‑lah es Quien envía los vientos que levantan las nubes; des­pués las conducimos sobre un trozo yermo de tierra y resucitamos on ella la tierra después de su muerte. De la misma manera <em>será </em>la Re­surrección." WHERE ayatNumber = 10 AND chapterNumber = 35;
UPDATE verses SET es = "Quien desee honor, <em>hazle saber, </em>pues, <em>que </em>todo honor pertenece a Al‑lah. A Él ascienden las palabras piadosas y las buenas acciones les ayudan a elevarse. Mas quienes traman el mal: para ellos hay un severo castigo; y su maquinación perecerá." WHERE ayatNumber = 11 AND chapterNumber = 35;
UPDATE verses SET es = "Al-lah os creó del polvo, después de una gota de esperma y a ontinuación os dispuso en parejas. Ninguna mujer concibe ni da a luz <em>un</em> <em>niño </em>sin Su conocimiento. Y nadie cuya vida sea prolongada tiene pro­longada <em>su</em> vida, ni nada se le retira de su vida sin que<em> esté registrado </em>en un Libro. Esto, en verdad, es fácil para Al‑lah." WHERE ayatNumber = 12 AND chapterNumber = 35;
UPDATE verses SET es = "Los dos mares no son simi­lares: hay uno potable, dulce <em>y</em> agra­dable para beber, y otro salado y amargo. Pero de cada uno de ellos coméis carne fresca y extraéis orna­mentos que os ponéis. Y en ellos ves los barcos surcando <em>las</em> <em>olas</em> para que busques de Su Magnanimidad y seas agradecido." WHERE ayatNumber = 13 AND chapterNumber = 35;
UPDATE verses SET es = "Él funde la noche en el día, y funde el día en la noche. Ha puesto en servicio el sol y la luna; cada uno recorre <em>su </em>trayectoria hasta el plazo fijado. Así es Al‑lah, vuestro Señor; Suyo es el Reino, y aquellos a los que invocáis fuera de Al‑lah no<strong> </strong> poseen ni siquiera una brizna." WHERE ayatNumber = 14 AND chapterNumber = 35;
UPDATE verses SET es = "Si los invocáis, no oirán vues­tra invocación, y aunque la oyeran, no podrían responderos. Y en el Día de la Resurrección negarán que los habéis asociado <em>a</em> <em>Dios</em>. Mas nadie puede informarte como <em>Aquel que </em>lo conoce todo." WHERE ayatNumber = 15 AND chapterNumber = 35;
UPDATE verses SET es = "Oh, humanos, sois meros mendigos ante Al-lah, mientras que Al-lah es Quien no necesita ayuda de nadie, el Merecedor de alabanza." WHERE ayatNumber = 16 AND chapterNumber = 35;
UPDATE verses SET es = "Si así Le place, puede haceros desaparecer y traer una nueva crea­ción <em>en vuestro lugar.</em>" WHERE ayatNumber = 17 AND chapterNumber = 35;
UPDATE verses SET es = "Pues eso no es difícil para Al-lah." WHERE ayatNumber = 18 AND chapterNumber = 35;
UPDATE verses SET es = "Ningún <em>alma </em>cargada puede llevar el fardo de otra; y si un <em>alma </em>pesadamente cargada pide a otra que <em>lleve </em>su carga, <em>la otra</em> no podrá llevar nada de ella, aunque fuese pariente suyo. Sólo puedes amones­tar a quienes temen a su Señor en secreto y cumplen la Oración. Pues quien alcanza la pureza, alcanza la pureza para su propio bien; y a Al‑lah será el retorno." WHERE ayatNumber = 19 AND chapterNumber = 35;
UPDATE verses SET es = "Los ciegos y los que ven no son iguales," WHERE ayatNumber = 20 AND chapterNumber = 35;
UPDATE verses SET es = "Como no lo son las tinieblas y la luz," WHERE ayatNumber = 21 AND chapterNumber = 35;
UPDATE verses SET es = "Ni la sombra y el calor," WHERE ayatNumber = 22 AND chapterNumber = 35;
UPDATE verses SET es = "No son iguales los vivos y los muertos. En verdad, Al‑lah hace que oiga quien Él quiere; y no puedes hacer que oigan los que están en las tumbas." WHERE ayatNumber = 23 AND chapterNumber = 35;
UPDATE verses SET es = "No eres más que un Amo­nestador." WHERE ayatNumber = 24 AND chapterNumber = 35;
UPDATE verses SET es = "En verdad, Te hemos enviado con la verdad, <em>como</em> portador de la buena nueva y <em>como</em> Amonestador; y no existe ningún pueblo al que no se haya enviado un Amonestador." WHERE ayatNumber = 25 AND chapterNumber = 35;
UPDATE verses SET es = "Mas si te tratan como mentiroso, aquellos que les precedieron, <em>también </em>trataron de mentirosos <em>a sus profetas. </em>Sus Mensajeros lle­garon a ellos con Signos claros, y con las Escrituras y el Libro radiante." WHERE ayatNumber = 26 AND chapterNumber = 35;
UPDATE verses SET es = "Entonces caí sobre los incré­dulos, y ¡qué <em>terrible </em>fue el cambio que <em>produje!</em>" WHERE ayatNumber = 27 AND chapterNumber = 35;
UPDATE verses SET es = "¿No ves que Al‑lah envía el agua desde el cielo, y que hacemos brotar con ella frutas de diferentes colores; y entre las mon­tañas hay franjas blancas y rojas, de diversos matices y otras de color negro oscuro?" WHERE ayatNumber = 28 AND chapterNumber = 35;
UPDATE verses SET es = "¿Y que entre los hombres, animales y ganado, de la misma manera, hay diversos colores? Sólo aquellos de Sus siervos que poseen conoci­miento temen a Al‑lah. En verdad, Al‑lah es Poderoso, Sumo Indulgente." WHERE ayatNumber = 29 AND chapterNumber = 35;
UPDATE verses SET es = "En verdad, <em>sólo</em> los que siguen el Libro de Al‑lah, cumplen la Ora­ción y emplean de lo que les hemos proporcionado -en secreto y a la vista de todos-, mantienen la esperanza de una recompensa que nunca fa­llará;" WHERE ayatNumber = 30 AND chapterNumber = 35;
UPDATE verses SET es = "Para que Él les conceda Su plena recompensa e <em>incluso</em> la aumente por Su magnanimidad. Él es en verdad el Sumo Indulgente, el Sumo Apreciador<strong>.</strong>" WHERE ayatNumber = 31 AND chapterNumber = 35;
UPDATE verses SET es = "El Libro que te hemos reve­lado es la <em>misma</em> verdad, que cumple lo que le precedió. En verdad, Al‑lah es Quien todo lo sabe, Quien todo lo ve, en relación con Sus siervos." WHERE ayatNumber = 32 AND chapterNumber = 35;
UPDATE verses SET es = "Entonces entregamos el Libro como herencia a aquellos de Nues­tros siervos que elegimos. Y entre ellos hay <em>algunos </em>que luchan eno­dadamente contra ellos mismos <em>para hacer</em> <em>el bien, </em>otros que siguen el camino recto y <em>otros</em> que obresalen en las buenas obras con el agrado de Al‑lah. Aquí está <em>en</em> <em>verdad </em>la gran distinción." WHERE ayatNumber = 33 AND chapterNumber = 35;
UPDATE verses SET es = "¡Jardines de Eternidad! en­trarán en ellos. Allí serán adornados con brazaletes de oro y perlas; y allí sus vestidos serán de seda." WHERE ayatNumber = 34 AND chapterNumber = 35;
UPDATE verses SET es = "Dirán: “Toda alabanza corresponde a Al‑lah, Quien nos ha librado de la aflicción. En verdad nuestro Señor es el Sumo Indulgente, el Sumo Apreciador," WHERE ayatNumber = 35 AND chapterNumber = 35;
UPDATE verses SET es = "“Quien, de Su magnanimidad, nos ha puesto en la Morada de la Eternidad, en donde no nos ago­biará pena alguna ni nos afectará ninguna <em>sensación de </em>cansancio”." WHERE ayatNumber = 36 AND chapterNumber = 35;
UPDATE verses SET es = "Mas para los incrédulos es­tará el fuego del Infierno. No se decretará que mueran ni se les aligerará el castigo. Así recom­pensamos a toda persona desagra­decida." WHERE ayatNumber = 37 AND chapterNumber = 35;
UPDATE verses SET es = "Gritarán allí pidiendo ayuda: “Oh, Señor nuestro, sácanos; ha­remos obras justas, distintas a las que solíamos hacer”. “¿Acaso no os dimos una vida <em>suficientemente larga </em>para que reflexionara en ella quien pudiera reflexionar? Y<em> también </em>os llegó un Amonestador. Probad pues <em>el castigo; </em>pues los injustos no ten­drán quien les ayude”." WHERE ayatNumber = 38 AND chapterNumber = 35;
UPDATE verses SET es = "Ciertamente Al‑lah conoce los secretos de los cielos y de la tierra. En verdad sabe muy bien todo lo <em>que </em>hay en sus corazones." WHERE ayatNumber = 39 AND chapterNumber = 35;
UPDATE verses SET es = "Él es Quien os hizo vicarios en la tierra. Por tanto, quien<strong> </strong>no crea sufrirá<strong> </strong><em>él</em> <strong> </strong><em>mismo las consecuencias de </em>su incredulidad. Y para los incré­dulos, su incredulidad no hará más que aumentar el enojo de Al‑lah; y la falta de fe no hará mas que aumentar la perdición de los no creyentes." WHERE ayatNumber = 40 AND chapterNumber = 35;
UPDATE verses SET es = "Diles: “¿Habéis visto a vues­tros dioses partícipes a quienes invo­cáis en lugar de<strong> </strong>Al‑lah? Mostradme <em>pues </em> lo que han creado de la tierra. ¿Tienen acaso una participación en la creación de los cielos? O ¿les hemos dado un Libro para que tengan una prueba de ello?”. No, los inicuos no se prometen mutuamente más que el engaño." WHERE ayatNumber = 41 AND chapterNumber = 35;
UPDATE verses SET es = "En verdad, Al‑lah sostiene los cielos y la tierra para que no se desvíen <em>de sus lugares. </em>Y si se des­viaran, nadie podría sostenerlos aparte de Él. En verdad, Él es Remisorio, Sumo Indulgente." WHERE ayatNumber = 42 AND chapterNumber = 35;
UPDATE verses SET es = "Juraron por Al‑lah con sus juramentos más solemnes que si les llegara un Amonestador, seguirían la guía mejor que ningún otro pueblo. Pero cuando les llegó el Amones­tador, ello no hizo más que aumentar su aversión." WHERE ayatNumber = 43 AND chapterNumber = 35;
UPDATE verses SET es = "Por su arrogancia en la tierra y sus perversas maquinaciones. Pero la maquinación perversa sólo asedia a sus autores. ¿Acaso buscan algo istinto a la manera <em>habitual</em> <em>de </em>Dios de <em>tratar a</em> los pueblos de la antigüedad? Mas nunca encontrarás cambio al­guno en el modo de obrar de Al‑lah; ni encontrarás alteración alguna en Su modo de obrar." WHERE ayatNumber = 44 AND chapterNumber = 35;
UPDATE verses SET es = "¿Acaso no han viajado por la tierra y han visto qué <em>desgraciado</em> fue el fin de quienes los precedieron? Y sin embargo tuvieron mayor poder que ellos. Pues tal es Al‑lah que nada en los cielos o en la tierra puede frustrar Sus <em>planes;</em> en verdad, Él es el Omnisciente, Omnipotente." WHERE ayatNumber = 45 AND chapterNumber = 35;
UPDATE verses SET es = "Y si Al‑lah fuese a castigar a un pueblo por lo que hacen, no de­jaría criatura viva en la superficie de <em>la tierra</em>; pero Él les concede un respiro hasta el plazo fijado; y cuando llega su plazo ijado, ¡<em>Cuidado</em>! Pues Al‑lah conoce plenamente a Sus siervos: <em>la humanidad.<strong></strong></em>" WHERE ayatNumber = 46 AND chapterNumber = 35;
UPDATE verses SET es = "En el nombre de Al‑lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 36;
UPDATE verses SET es = "Ya Sin." WHERE ayatNumber = 2 AND chapterNumber = 36;
UPDATE verses SET es = "Por el Corán, lleno de sa­biduría," WHERE ayatNumber = 3 AND chapterNumber = 36;
UPDATE verses SET es = "Eres ciertamente <em>uno</em> de los Mensajeros," WHERE ayatNumber = 4 AND chapterNumber = 36;
UPDATE verses SET es = "Que siguen el camino recto." WHERE ayatNumber = 5 AND chapterNumber = 36;
UPDATE verses SET es = "<em>Ésta</em> es una revelación del Poderoso, el Misericordioso," WHERE ayatNumber = 6 AND chapterNumber = 36;
UPDATE verses SET es = "Para que adviertas a un pueblo cuyos padres no fueron advertidos, y por tanto son descuidados." WHERE ayatNumber = 7 AND chapterNumber = 36;
UPDATE verses SET es = "En verdad, ha resultado ser cierta la palabra contra la mayoría de ellos, porque no creen." WHERE ayatNumber = 8 AND chapterNumber = 36;
UPDATE verses SET es = "Hemos puesto collares alrededor de sus cuellos que llegan hasta el mentón, para que sus cabezas queden echadas hacia atrás." WHERE ayatNumber = 9 AND chapterNumber = 36;
UPDATE verses SET es = "Hemos colocado una barrera ante ellos y otra tras ellos, y los hemos cubierto para que no puedan ver." WHERE ayatNumber = 10 AND chapterNumber = 36;
UPDATE verses SET es = "Lo mismo les da si les ad­viertes como si no; no creerán." WHERE ayatNumber = 11 AND chapterNumber = 36;
UPDATE verses SET es = "Sólo puedes advertir a quien está dispuesto a seguir el Recordatorio y teme en secreto al <em>Dios</em> Clemente. Dale pues la buena nueva del perdón y una noble recompensa." WHERE ayatNumber = 12 AND chapterNumber = 36;
UPDATE verses SET es = "En verdad, <em>sólo</em> Nosotros damos la vida a los muertos y regis­tramos lo que envían delante de ellos y lo que dejan detrás; pues hemos anotado todas las cosas en un re­gistro claro." WHERE ayatNumber = 13 AND chapterNumber = 36;
UPDATE verses SET es = "Y preséntales la parábola de los moradores de la ciudad, cuando llegaron a ella los Mensa­jeros." WHERE ayatNumber = 14 AND chapterNumber = 36;
UPDATE verses SET es = "Cuando les enviamos a dos Mensajeros, y les rechazaron a ambos; por tanto <em>les </em>fortalecimos con un tercero, y dijeron: “En verdad, hemos sido enviados a vosotros como Mensajeros”." WHERE ayatNumber = 15 AND chapterNumber = 36;
UPDATE verses SET es = "Respondieron: “No sois sino hombres como nosotros y el <em>Dios </em>Clemente no ha revelado nada. No hacéis más que mentir”." WHERE ayatNumber = 16 AND chapterNumber = 36;
UPDATE verses SET es = "Respondieron: “Nuestro Señor sabe que somos ciertamente <em>Sus</em> Mensajeros para vosotros”;" WHERE ayatNumber = 17 AND chapterNumber = 36;
UPDATE verses SET es = "“Y a nosotros solamente corresponde la simple entrega del Mensaje”." WHERE ayatNumber = 18 AND chapterNumber = 36;
UPDATE verses SET es = "Respondieron: “Ciertamente esperamos mala suerte de vosotros; si no desistís, ciertamente que os la­pidaremos, y os sobrevendrá un doloroso castigo de nuestras manos”." WHERE ayatNumber = 19 AND chapterNumber = 36;
UPDATE verses SET es = "Ellos contestaron: “Vuestra mala suerte os corresponde a vo­sotros mismos. ¿Es por que habéis sido amonestados? ¡No! sois un pue­blo que sobrepasáis todos los lí­mites”." WHERE ayatNumber = 20 AND chapterNumber = 36;
UPDATE verses SET es = "Y de la parte más alejada de la ciudad vino un hombre corriendo. Dijo: “Oh, pueblo mío, seguid a los Mensajeros," WHERE ayatNumber = 21 AND chapterNumber = 36;
UPDATE verses SET es = "“Seguid a quienes no os piden recompensa y están rectamente guiados”;" WHERE ayatNumber = 22 AND chapterNumber = 36;
UPDATE verses SET es = "“¿Qué razón tengo para no adorar a Quien me ha creado, y a Quien seréis devueltos?" WHERE ayatNumber = 23 AND chapterNumber = 36;
UPDATE verses SET es = "“¿Debo acaso tomar a otros fuera de Él como dioses? Si el <em>Dios</em> Clemente pretendiera hacerme cualquier daño, la intercesión de ellos no me serviría de nada, ni podrían rescatarme." WHERE ayatNumber = 24 AND chapterNumber = 36;
UPDATE verses SET es = "“En ese caso estaría, en verdad, en error evidente”." WHERE ayatNumber = 25 AND chapterNumber = 36;
UPDATE verses SET es = "“Creo en vuestro Señor; escu­chadme pues”." WHERE ayatNumber = 26 AND chapterNumber = 36;
UPDATE verses SET es = "Se <em>le</em> dijo: “Entra en el Pa­raíso”. Él respondió: “Oh, si mi pueblo supiera," WHERE ayatNumber = 27 AND chapterNumber = 36;
UPDATE verses SET es = "“¡Con <em>cuánta clemencia </em>mi Señor me ha concedido el perdón y me ha hecho uno de los honorables!”." WHERE ayatNumber = 28 AND chapterNumber = 36;
UPDATE verses SET es = "Mas no enviamos contra su pueblo, tras él, ejército alguno de los cielos, ni necesitábamos enviar <em>a ninguno.</em>" WHERE ayatNumber = 29 AND chapterNumber = 36;
UPDATE verses SET es = "No hubo más que un único soplo, y he ahí cómo quedaron ful­minados." WHERE ayatNumber = 30 AND chapterNumber = 36;
UPDATE verses SET es = "¡Ay de la humanidad! no les llega ningún Mensajero sin que se burlen de él." WHERE ayatNumber = 31 AND chapterNumber = 36;
UPDATE verses SET es = "¿No han visto acaso a cuán­tas generaciones destruimos antes de ellos, y que nunca volvieron a ellos?" WHERE ayatNumber = 32 AND chapterNumber = 36;
UPDATE verses SET es = "Pues todos ellos, reunidos, serán ciertamente traídos ante No­sotros." WHERE ayatNumber = 33 AND chapterNumber = 36;
UPDATE verses SET es = "Y la tierra muerta es un Signo para ellos: Nosotros la resuci­tamos y producimos grano de ella, del que se alimentan." WHERE ayatNumber = 34 AND chapterNumber = 36;
UPDATE verses SET es = "Hemos colocado en ella jar­dines con palmeras y viñas, y hemos hecho que broten manan­tiales en ella," WHERE ayatNumber = 35 AND chapterNumber = 36;
UPDATE verses SET es = "Para que coman de sus frutos, aunque no fueron sus manos las que los crearon. ¿No serán pues agra­decidos?" WHERE ayatNumber = 36 AND chapterNumber = 36;
UPDATE verses SET es = "Santo es Quien creó todas las cosas en parejas, de lo que crece en la tierra, de ellos mismos y de lo que desconocen." WHERE ayatNumber = 37 AND chapterNumber = 36;
UPDATE verses SET es = "Un Signo para ellos es la noche de la que retiramos al día, y ¡helos ahí! en tinieblas." WHERE ayatNumber = 38 AND chapterNumber = 36;
UPDATE verses SET es = "Y el sol que sigue el rumbo que se le ha prescrito. Ése es el decreto del Todopoderoso, el <em>Dios </em>Omnisciente." WHERE ayatNumber = 39 AND chapterNumber = 36;
UPDATE verses SET es = "Para la luna hemos estable­cido fases, hasta que vuelve <em>a</em> <em>su antigua forma</em>, similar a una rama vieja y seca de palmera." WHERE ayatNumber = 40 AND chapterNumber = 36;
UPDATE verses SET es = "No corresponde al sol alcanzar la luna, ni la noche puede adelan­tarse al día. Todos flotan en una órbita." WHERE ayatNumber = 41 AND chapterNumber = 36;
UPDATE verses SET es = "Y un Signo para ellos es que llevamos a sus descendientes en la embarcación cargada." WHERE ayatNumber = 42 AND chapterNumber = 36;
UPDATE verses SET es = "Y crearemos para ellos algo similar en lo que montarán." WHERE ayatNumber = 43 AND chapterNumber = 36;
UPDATE verses SET es = "Mas si hubiésemos querido, los hubiésemos ahogado; entonces no tendrían a nadie que <em>les </em>socorrieran, ni serían rescatados," WHERE ayatNumber = 44 AND chapterNumber = 36;
UPDATE verses SET es = "Salvo por Nuestra miseri­cordia y como provisión durante algún tiempo." WHERE ayatNumber = 45 AND chapterNumber = 36;
UPDATE verses SET es = "Y cuando se les dijo: “Guar­daos contra lo que está delante de osotros y lo que está tras vo­sotros, <em>es decir, de las faltas mani­fiestas y</em> <em>ocultas,</em> para que se os muestre misericordia”." WHERE ayatNumber = 46 AND chapterNumber = 36;
UPDATE verses SET es = "Y no llega a ellos ninguno de los Signos de su Señor sin que se aparten de él." WHERE ayatNumber = 47 AND chapterNumber = 36;
UPDATE verses SET es = "Mas cuando se les dice: “Em­plead de lo que Al‑lah os ha propor­cionado”, los incrédulos dicen a los creyentes: “¿Debemos limentar a quien Al‑lah habría alimentado si Él lo hubiese <em>así </em>querido? Estáis en un error evidente”." WHERE ayatNumber = 48 AND chapterNumber = 36;
UPDATE verses SET es = "Dicen: “¿Cuándo será <em>cumplida </em>esta promesa, si <em> en verdad s</em>ois veraces?”." WHERE ayatNumber = 49 AND chapterNumber = 36;
UPDATE verses SET es = "Están esperando al único so­plo que los arrebatará mientras disputan." WHERE ayatNumber = 50 AND chapterNumber = 36;
UPDATE verses SET es = "Y no podrán otorgar testa­mento ni regresarán a sus familias." WHERE ayatNumber = 51 AND chapterNumber = 36;
UPDATE verses SET es = "La trompeta sonará, y he ahí que de sus tumbas se apresurarán a ir a su Señor." WHERE ayatNumber = 52 AND chapterNumber = 36;
UPDATE verses SET es = "Dirán: “¡Oh, ay de no­sotros! ¿Quién nos ha sacado de nuestras tumbas? Esto es lo que el <em>Dios</em> Clemente había prometido, y los Mensajeros dijeron la verdad”." WHERE ayatNumber = 53 AND chapterNumber = 36;
UPDATE verses SET es = "¡No será más que un soplo y he ahí que todos ellos serán traídos ante Nosotros!" WHERE ayatNumber = 54 AND chapterNumber = 36;
UPDATE verses SET es = "Y en ese día, ningún alma será perjudicada en absoluto; ni se­réis retribuidos excepto por lo que solíais practicar." WHERE ayatNumber = 55 AND chapterNumber = 36;
UPDATE verses SET es = "En verdad, los habitantes del<strong> </strong>Cielo, en ese día estarán felizmente ocupados en diversos quehaceres." WHERE ayatNumber = 56 AND chapterNumber = 36;
UPDATE verses SET es = "<em></em>Ellos y sus esposas estarán bajo sombras agradables, reclinados en divanes elevados." WHERE ayatNumber = 57 AND chapterNumber = 36;
UPDATE verses SET es = "Tendrán allí frutos y tendrán todo lo que pidan." WHERE ayatNumber = 58 AND chapterNumber = 36;
UPDATE verses SET es = "“La paz <em>sea con vosotros</em>”, una palabra <em>de saludo </em> del Señor Misericordioso." WHERE ayatNumber = 59 AND chapterNumber = 36;
UPDATE verses SET es = "Y <em>Dios dirá</em>: “¡Separaos <em>de los justos </em>en este día, Oh vosotros, los culpables!" WHERE ayatNumber = 60 AND chapterNumber = 36;
UPDATE verses SET es = "“¿Acaso no os ordené, Oh vo­sotros, hijos de Adán, que no adorarais a Satanás ‑pues él es vuestro enemigo declarado‑" WHERE ayatNumber = 61 AND chapterNumber = 36;
UPDATE verses SET es = "“Y que Me adorarais? Éste es el camino recto." WHERE ayatNumber = 62 AND chapterNumber = 36;
UPDATE verses SET es = "“Mas él extravió a una gran multitud de vosotros. ¿Por qué no entendisteis entonces?" WHERE ayatNumber = 63 AND chapterNumber = 36;
UPDATE verses SET es = "“Éste es el Infierno que se os prometió," WHERE ayatNumber = 64 AND chapterNumber = 36;
UPDATE verses SET es = "“Entrad en él en este día, porque no creísteis”." WHERE ayatNumber = 65 AND chapterNumber = 36;
UPDATE verses SET es = "En este día pondremos un sello en sus bocas, y sus manos nos hablarán, y sus pies serán testigos de lo que consiguieron." WHERE ayatNumber = 66 AND chapterNumber = 36;
UPDATE verses SET es = "Y si <em>así</em> lo hubiésemos querido, les habríamos privado de los ojos, y entonces habrían continuado pi­sando el camino, pero ¿cómo lo per­cibirían?" WHERE ayatNumber = 67 AND chapterNumber = 36;
UPDATE verses SET es = "Y si <em>así </em>lo hubiésemos querido, los hubiésemos transformado <em>a fin</em> <em>de inmovilizarlos </em>en sus lugares, y entonces no hubieran podido avan­zar ni retroceder." WHERE ayatNumber = 68 AND chapterNumber = 36;
UPDATE verses SET es = "Y a quien le concedemos lar­ga vida, hacemos que vuelva a un estado de senilidad. ¿Acaso no en­tenderán entonces?" WHERE ayatNumber = 69 AND chapterNumber = 36;
UPDATE verses SET es = "No le hemos enseñado la poesía, ni es propia de él. No es más que un Recordatorio y un Corán que deja las <em>cosas </em>claras." WHERE ayatNumber = 70 AND chapterNumber = 36;
UPDATE verses SET es = "Para advertir a todos los que viven, y para que el decreto de Al‑lah se cumpla contra los incrédulos." WHERE ayatNumber = 71 AND chapterNumber = 36;
