UPDATE verses SET es = "Que serán ellos ciertamen­te los ayudados;" WHERE ayatNumber = 173 AND chapterNumber = 37;
UPDATE verses SET es = "Y que Nuestro ejército es el que saldrá victorioso con toda segu­ridad." WHERE ayatNumber = 174 AND chapterNumber = 37;
