UPDATE verses SET es = "Y obsérvalos, pues muy pronto verán <em>su propio fin.</em>" WHERE ayatNumber = 176 AND chapterNumber = 37;
UPDATE verses SET es = "¿Es pues Nuestro castigo el que intentan apresurar?" WHERE ayatNumber = 177 AND chapterNumber = 37;
UPDATE verses SET es = "Mas cuando descienda a sus patios, será una mañana funesta para los que fueron advertidos." WHERE ayatNumber = 178 AND chapterNumber = 37;
UPDATE verses SET es = "Apártate pues de ellos algún tiempo." WHERE ayatNumber = 179 AND chapterNumber = 37;
UPDATE verses SET es = "Y observa, pues pronto verán." WHERE ayatNumber = 180 AND chapterNumber = 37;
UPDATE verses SET es = "Santo es tu Señor, el Señor del Honor <em>y</em> <em>el Poder</em> , muy por en­cima de lo que ellos afirman." WHERE ayatNumber = 181 AND chapterNumber = 37;
