UPDATE verses SET es = "En el nombre de Al‑lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 38;
UPDATE verses SET es = "Sad. Por el Corán, pleno de exhortación," WHERE ayatNumber = 2 AND chapterNumber = 38;
UPDATE verses SET es = "Mas los incrédulos <em>están empa­pados de falso </em>orgullo y enemis­tad." WHERE ayatNumber = 3 AND chapterNumber = 38;
UPDATE verses SET es = "¡A cuántas generaciones ante­riores a ellos destruimos! Gritaron <em>pidiendo ayuda, </em>pero ya no era el momento para escapar." WHERE ayatNumber = 4 AND chapterNumber = 38;
UPDATE verses SET es = "Se asombran de que les haya llegado un Amonestador de entre ellos mismos; y los incrédulos dicen: “Es un mago, un gran embustero”." WHERE ayatNumber = 5 AND chapterNumber = 38;
UPDATE verses SET es = "“¿Ha fraguado a los dioses en un Único Dios? Esta es la cosa más extraña <em>que hemos oído</em>”. " WHERE ayatNumber = 6 AND chapterNumber = 38;
UPDATE verses SET es = "Ante esto, sus líderes discutieron, <em>amonestándoles</em>: Id y permaneced unidos a vuestros dioses. Es algo muy preciado”;" WHERE ayatNumber = 7 AND chapterNumber = 38;
UPDATE verses SET es = "Nunca hemos oído cosa igual en otra religión. No es más que una invención." WHERE ayatNumber = 8 AND chapterNumber = 38;
UPDATE verses SET es = "“¿Qué? ¿De entre todos nosotros ha de ser a él a quien se reveló la exhortación?”. No, están en duda en cuanto a Mi Exhortación. ¡No!, es que aún no han probado Mi castigo." WHERE ayatNumber = 9 AND chapterNumber = 38;
UPDATE verses SET es = "¿Poseen acaso los tesoros de la misericordia de tu Señor, el Po­deroso, el Gran Dispensador?" WHERE ayatNumber = 10 AND chapterNumber = 38;
UPDATE verses SET es = "¿O bien es suyo el Reino de los cielos y de la tierra y todo lo que hay entre ellos? Que hagan, pues, su es­fuerzo, utilizando todos sus me­dios, " WHERE ayatNumber = 11 AND chapterNumber = 38;
UPDATE verses SET es = "<em>Son </em>un grupo de entre los confederados <em>que</em> <em>serán </em>encamina­dos hacia aquí." WHERE ayatNumber = 12 AND chapterNumber = 38;
UPDATE verses SET es = "Igual que antes que ellos, el pueblo de Noé, y la <em>tribu de </em>Ad y el Faraón, el señor de los campamentos, <em>les</em> habían rechazado. " WHERE ayatNumber = 13 AND chapterNumber = 38;
UPDATE verses SET es = "Así como l<em>a tribu de</em> Zamud, el pueblo de Lot y los moradores del osque: éstas fueron las hordas <em>derrotadas de forma aplastante.</em>" WHERE ayatNumber = 14 AND chapterNumber = 38;
UPDATE verses SET es = "Todos sin excepción rechazaron a los Mensajeros, por lo que Mi castigo se hizo inevitable." WHERE ayatNumber = 15 AND chapterNumber = 38;
UPDATE verses SET es = "No esperan mas que un largo chillido en el que no habrá respiro." WHERE ayatNumber = 16 AND chapterNumber = 38;
UPDATE verses SET es = "Dicen: “Señor nuestro, apre­sura sobre nosotros nuestra parte <em>en el castigo</em> antes del Día de las Cuen­tas”." WHERE ayatNumber = 17 AND chapterNumber = 38;
UPDATE verses SET es = "Soporta con paciencia lo que dicen, y recuerda a Nuestro siervo David, hombre de muchos poderes; en verdad siempre volvía a <em>Dios.</em>" WHERE ayatNumber = 18 AND chapterNumber = 38;
UPDATE verses SET es = "<em>Le</em> sometimos las montañas. Ellas celebraron con él las alabanzas de Dios al anochecer y al amanecer." WHERE ayatNumber = 19 AND chapterNumber = 38;
UPDATE verses SET es = "Y <em>le</em> <em>sometimos</em> los pájaros reunidos juntos: todos volvieron a él." WHERE ayatNumber = 20 AND chapterNumber = 38;
UPDATE verses SET es = "Fortalecimos su reino, y le dimos la sabiduría y <em>el talento para</em> una palabra decisiva." WHERE ayatNumber = 21 AND chapterNumber = 38;
UPDATE verses SET es = "¿Te ha llegado el relato de los litigantes, cuando subieron por el muro de <em>su</em> cámara?," WHERE ayatNumber = 22 AND chapterNumber = 38;
UPDATE verses SET es = "Cuando entraron hasta David, y éste se asustó de ellos. Dije­ron: No temas. <em>Somos</em> dos litigan­ tes; uno de nosotros ha cometido una transgresión contra el otro; juzga pues entre nosotros con justicia, no te desvíes e la equidad y guíanos al camino recto”." WHERE ayatNumber = 23 AND chapterNumber = 38;
UPDATE verses SET es = "“Éste es mi hermano; tiene noventa y nueve ovejas y yo una sola. Y sin embar­go dice: “Dámela a mí” y me venció en su discurso." WHERE ayatNumber = 24 AND chapterNumber = 38;
UPDATE verses SET es = "<em>David </em>dijo: “En verdad, él te ha perjudicado pidiéndote tu oveja además de las suyas propias. Pues ciertamente muchos socios se perjudican mutuamente, excepto quienes creen <em>en Dios </em>y practican el bien; mas éstos son pocos”. Y David comprendió que Nosotros lo habíamos probado; por lo que pidió perdón a su Señor y ayó postrado en adoración, volviendo <em>a Él.</em>" WHERE ayatNumber = 25 AND chapterNumber = 38;
UPDATE verses SET es = "Por eso se lo perdonamos; pues en verdad él tenía una posición cercana a nosotros y un retiro ex­celente." WHERE ayatNumber = 26 AND chapterNumber = 38;
UPDATE verses SET es = "“Oh David, te hemos conver­tido en vicario en la tierra; juzga pues entre los hombres con justicia, no sigas los vanos deseos, no sea que e aparten del camino de Al‑lah”. En verdad, quienes se aparten del ca­mino de Al‑lah tendrán un severo castigo, porque han olvidado el Día de la Cuenta." WHERE ayatNumber = 27 AND chapterNumber = 38;
UPDATE verses SET es = "No hemos creado en vano el cielo y la tierra y todo lo que hay entre ellos. Ésta es la opinión de los incrédulos. ¡Ay, pues, de los ncré­dulos a causa del Fuego!" WHERE ayatNumber = 28 AND chapterNumber = 38;
UPDATE verses SET es = "¿Acaso trataremos a quienes creen y hacen buenas obras, como a quienes actúan perversamente en la tierra? ¿Debemos tratar a los justos como a los malvados?" WHERE ayatNumber = 29 AND chapterNumber = 38;
UPDATE verses SET es = "<em>Éste es </em>un Libro que te hemos revelado, lleno de endiciones , para que reflexiones sobre sus ver sículos y hagan caso los que estén dotados de entendimiento." WHERE ayatNumber = 30 AND chapterNumber = 38;
UPDATE verses SET es = "A David le concedimos Salomón, que fue un siervo excelente. Él ambién acudía siempre <em>a Nosotros</em>." WHERE ayatNumber = 31 AND chapterNumber = 38;
UPDATE verses SET es = "Cuando ante él fueron llevados, al atardecer, corceles de pura angre y patas veloces." WHERE ayatNumber = 32 AND chapterNumber = 38;
UPDATE verses SET es = "Él dijo: “Me siento inclinado a amar a los caballos porque me recuerdan a mi Señor”. Se sentó, pues, hasta que desaparecieron detrás del velo," WHERE ayatNumber = 33 AND chapterNumber = 38;
UPDATE verses SET es = "<em>Dijo;</em> Traédmelos de nuevo! Entonces empezó a acariciar <em>sus </em>patas y <em>sus </em>cuellos”. " WHERE ayatNumber = 34 AND chapterNumber = 38;
UPDATE verses SET es = "Probamos a Salomón y olocamos en su trono un <em>simple</em> cuerpo. Entonces se volvió <em>a Dios, buscando Su Misericordia. </em>" WHERE ayatNumber = 35 AND chapterNumber = 38;
UPDATE verses SET es = "Dijo; “Oh, mi Señor, concé­deme el perdón y dame un reino que no sea poseído por nadie después de mí que no lo merezca. En verdad, Tú eres muy Generoso”." WHERE ayatNumber = 36 AND chapterNumber = 38;
UPDATE verses SET es = "Por ello le sometimos el viento para que soplara suavemente a su mandato, a donde quisiera que fuera." WHERE ayatNumber = 37 AND chapterNumber = 38;
UPDATE verses SET es = "Y a los gigantes, y todo tipo <em>de </em>constructores <em>y </em> buzos," WHERE ayatNumber = 38 AND chapterNumber = 38;
UPDATE verses SET es = "Y a otros atados en ca­denas. " WHERE ayatNumber = 39 AND chapterNumber = 38;
UPDATE verses SET es = "“Éste es Nuestro don: libé­ralos pues con generosidad o retenlos sin cuenta, <em>según te agrade”.</em>" WHERE ayatNumber = 40 AND chapterNumber = 38;
UPDATE verses SET es = "En verdad tenía un puesto cer­cano a Nosotros y un retiro exce­lente." WHERE ayatNumber = 41 AND chapterNumber = 38;
UPDATE verses SET es = "Y acuérdate de Nuestro siervo Job, cuando invocó a su Señor <em>di­ciendo: </em>“Satanás me ha afligido con fatigas y tormentos”. " WHERE ayatNumber = 42 AND chapterNumber = 38;
UPDATE verses SET es = "<em>“</em>Espolea<em> tu montura”</em>. Aquí hay agua fría para que te laves y bebas”. " WHERE ayatNumber = 43 AND chapterNumber = 38;
UPDATE verses SET es = "<em></em>Y le restituimos su familia y, junto a ella, otros muchos semejantes, como misericordia Nuestra y como recor­datorio para los hombres de enten­dimiento." WHERE ayatNumber = 44 AND chapterNumber = 38;
UPDATE verses SET es = "Y<em> le dijimos: </em>“Toma contigo algunas aves, como rovisión; avan­za y no sucumbas a la false­ dad”. En verdad, lo encontramos perseverante. ¡Qué excelente siervo fue! En verdad, siempre acudía <em>a Dios. </em>" WHERE ayatNumber = 45 AND chapterNumber = 38;
UPDATE verses SET es = "Acuérdate de Nuestros sier­vos. Abraham, Isaac y Jacob, <em>hom­bres </em>de fuerza y visión." WHERE ayatNumber = 46 AND chapterNumber = 38;
UPDATE verses SET es = "Los elegimos especialmente para recordar <em>a los hombres </em>la morada <em>del Mas Allá.</em>" WHERE ayatNumber = 47 AND chapterNumber = 38;
UPDATE verses SET es = "Pues en verdad están ante Nuestra vista entre los elegidos <em>y </em>los mejores." WHERE ayatNumber = 48 AND chapterNumber = 38;
UPDATE verses SET es = "Y acuérdate de Ismael, Eli­seo y Ezequiel; todos fueron de los mejores." WHERE ayatNumber = 49 AND chapterNumber = 38;
UPDATE verses SET es = "Esto es un recordatorio. Pues los justos tendrán ciertamente un excelente retiro:" WHERE ayatNumber = 50 AND chapterNumber = 38;
UPDATE verses SET es = "Jardines de Eternidad, con las puertas abiertas a ellos," WHERE ayatNumber = 51 AND chapterNumber = 38;
UPDATE verses SET es = "Reclinándose allí <em> sobre coji­nes; </em> pedirán <em>a voluntad </em>cantidades abundantes de fruta y bebida;" WHERE ayatNumber = 52 AND chapterNumber = 38;
UPDATE verses SET es = "Y con ellos habrá <em>mujeres</em> puras, con la mirada recatada, com­pañeras de su misma edad." WHERE ayatNumber = 53 AND chapterNumber = 38;
UPDATE verses SET es = "Esto es lo que se os promete para el Día de las Cuentas. " WHERE ayatNumber = 54 AND chapterNumber = 38;
UPDATE verses SET es = "En verdad, ésta es Nuestra provisión que nunca se agotará." WHERE ayatNumber = 55 AND chapterNumber = 38;
UPDATE verses SET es = "Esto es <em>para los creyentes. </em>Mas para los rebeldes hay un mal lugar de retorno." WHERE ayatNumber = 56 AND chapterNumber = 38;
UPDATE verses SET es = "El infierno, en el que arderán. ¡Qué lugar de reposo más malo!." WHERE ayatNumber = 57 AND chapterNumber = 38;
UPDATE verses SET es = "Esto es lo <em>que tendrán. </em>Dejad pues que lo prueben; un líquido hirviendo y una bebida intensamen­te fría y pestilente." WHERE ayatNumber = 58 AND chapterNumber = 38;
UPDATE verses SET es = "<em> Y varios tipos de tormentos </em>diferentes de naturaleza similar." WHERE ayatNumber = 59 AND chapterNumber = 38;
UPDATE verses SET es = "“Éste es un grupo <em> e los vues­tros </em> orriendo apresurados a vuestro lado, <em>Oh</em> <em>jefes de la iniquidad. </em>Ninguna bienvenida hay para ellos. De­ben arder en el Fuego." WHERE ayatNumber = 60 AND chapterNumber = 38;
UPDATE verses SET es = "Dirán: “¡No!, ¡sois oso­ tros!. No sois <em>ciertamente</em> bienvenidos. Fuisteis los que nos preparasteis esto. ¡Qué mal lugar de descanso!" WHERE ayatNumber = 61 AND chapterNumber = 38;
UPDATE verses SET es = "Dirán <em>también: </em>“Señor Nues­tro, quien sea el que haya repara­do esto para nosotros, así añádele un doble castigo en el Fuego”." WHERE ayatNumber = 62 AND chapterNumber = 38;
UPDATE verses SET es = "Y dirán: “¿Qué nos ha suce­dido, que no vemos a los hombres a los que solíamos contar entre los malvados?”." WHERE ayatNumber = 63 AND chapterNumber = 38;
UPDATE verses SET es = "“¿Es porque los pusimos en ridículo <em>inj ustamente</em>, o es que es­capan a nuestras miradas?”." WHERE ayatNumber = 64 AND chapterNumber = 38;
UPDATE verses SET es = "Verdaderamente, es una reali­dad: la disputa de los moradores del Fuego entre sí." WHERE ayatNumber = 65 AND chapterNumber = 38;
UPDATE verses SET es = "Diles: “Sólo soy un Amones­tador; y no hay más Dios que Al‑lah, el Único, el Supremo;" WHERE ayatNumber = 66 AND chapterNumber = 38;
UPDATE verses SET es = "“El Señor de los cielos y la tierra, y de todo lo que hay entre ambos, el Poderoso, el Gran Perdo­nador”." WHERE ayatNumber = 67 AND chapterNumber = 38;
UPDATE verses SET es = "Diles: “Es una noticia de gran importancia” ." WHERE ayatNumber = 68 AND chapterNumber = 38;
UPDATE verses SET es = "“De la que os apartáis”;" WHERE ayatNumber = 69 AND chapterNumber = 38;
UPDATE verses SET es = "“No tenía conocimiento algu­no de la Asamblea exaltada, cuan­do <em>lo </em>discutían entre ellos” ," WHERE ayatNumber = 70 AND chapterNumber = 38;
UPDATE verses SET es = "“Pero esto es lo que me ha sido revelado: que soy un simple Amo­ nestador”." WHERE ayatNumber = 71 AND chapterNumber = 38;
UPDATE verses SET es = "Cuando tu Señor dijo a los ángeles: “Estoy a punto de crear al hombre del barro”;" WHERE ayatNumber = 72 AND chapterNumber = 38;
UPDATE verses SET es = "“Y así cuando lo haya mol­deado <em>en perfección </em>y le haya insu­flado Mi Espíritu, postraos ante él en sumisión”. " WHERE ayatNumber = 73 AND chapterNumber = 38;
UPDATE verses SET es = "Así se sometieron los ánge­les, todos ellos juntos." WHERE ayatNumber = 74 AND chapterNumber = 38;
UPDATE verses SET es = "Pero Iblis <em>no lo hizo.</em> Se com­portó orgullosamente y fue de los que no creyeron." WHERE ayatNumber = 75 AND chapterNumber = 38;
UPDATE verses SET es = "<em>Dios</em> dijo: “Oh, Iblis, ¿qué te impidió someterte a lo que había creado con Mis dos manos? ¿Has obrado por orgullo o realmente perteneces a los enaltecidos?”." WHERE ayatNumber = 76 AND chapterNumber = 38;
UPDATE verses SET es = "Él respondió: “Soy mejor que él. Tú me has creado de fuego y a él lo has creado de barro”." WHERE ayatNumber = 77 AND chapterNumber = 38;
UPDATE verses SET es = "Dios dijo: “Sal pues de aquí porque en verdad eres re­chazado;" WHERE ayatNumber = 78 AND chapterNumber = 38;
UPDATE verses SET es = "“Y en verdad sobre ti caerá Mi maldición hasta el Día del Juicio”." WHERE ayatNumber = 79 AND chapterNumber = 38;
UPDATE verses SET es = "Él dijo: “Mi Señor, oncéde­me entonces un respiro hasta el día en que sean resucitados”. " WHERE ayatNumber = 80 AND chapterNumber = 38;
UPDATE verses SET es = "<em>Dios</em> dijo: “Ciertamente eres de aquellos a los que se concede un respiro”," WHERE ayatNumber = 81 AND chapterNumber = 38;
UPDATE verses SET es = "“Hasta el día de la hora fi­jada”." WHERE ayatNumber = 82 AND chapterNumber = 38;
UPDATE verses SET es = "Respondió: “Así, por Tu poder, extraviaré ciertamente a to­dos ellos” ," WHERE ayatNumber = 83 AND chapterNumber = 38;
UPDATE verses SET es = "“Excepto a Tus siervos elegi­dos de entre ellos”." WHERE ayatNumber = 84 AND chapterNumber = 38;
UPDATE verses SET es = "<em>Dios</em> dijo: “Entonces ésta es la verdad y Yo digo la verdad”." WHERE ayatNumber = 85 AND chapterNumber = 38;
UPDATE verses SET es = "“<em>Que </em>llenaré ciertamente el Infierno contigo y con quienes te sigan, todos juntos “." WHERE ayatNumber = 86 AND chapterNumber = 38;
UPDATE verses SET es = "Diles; “No pido de vosotros recompensa alguna por ello, ni soy de los dados a la afectación”." WHERE ayatNumber = 87 AND chapterNumber = 38;
UPDATE verses SET es = "“No es más que un Recor­datorio para <em>todos</em> los pueblos”." WHERE ayatNumber = 88 AND chapterNumber = 38;
UPDATE verses SET es = "“Y ciertamente aprenderéis su verdadera importancia des­pués de un ierto tiempo”. <strong></strong>" WHERE ayatNumber = 89 AND chapterNumber = 38;
UPDATE verses SET es = "En el nombre de Al‑lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 39;
UPDATE verses SET es = "La revelación de este Libro procede de Al‑lah, el Poderoso, el Sabio." WHERE ayatNumber = 2 AND chapterNumber = 39;
UPDATE verses SET es = "Por cierto que somos Nosotros quienes te hemos revelado el Libro con verdad; adora pues a Al‑lah, dedicándole tu fe con toda sinceridad." WHERE ayatNumber = 3 AND chapterNumber = 39;
UPDATE verses SET es = "Escuchad: <em>sólo</em> a Al‑lah se le debe sincera obediencia. Y quienes toman por protectores a otros distintos de Él, <em>dicen: </em> “Los servimos única­mente para que nos lleven a un nivel próximo a Al‑lah”. En verdad, Al‑lah juzgará entre ellos en cuanto a lo que disputan. Ciertamente Al­-lah no guía al mentiroso desagra­decido." WHERE ayatNumber = 4 AND chapterNumber = 39;
UPDATE verses SET es = "Si Al‑lah hubiese querido to­mar un Hijo, hubiera elegido a quien le gustara de lo creado por Él ¡Santo es Él! Él es Al‑lah, el Único, el Dominante." WHERE ayatNumber = 5 AND chapterNumber = 39;
UPDATE verses SET es = "Creó los cielos y la tierra de acuerdo con los requisitos de la sa­biduría. Hace que la noche cubra el día y que el día cubra la noche; ha puesto en servicio al sol y a la luna; cada uno sigue <em>su </em> curso hasta el momento señalado. Escuchad, <em>sólo</em> Él es Poderoso, el Gran Perdonador." WHERE ayatNumber = 6 AND chapterNumber = 39;
UPDATE verses SET es = "Él os creó de un solo ser; y a partir de él creó a su compañera; y os ha enviado ocho <em>cabezas </em>de ganado en parejas. Os crea en los vientres de vuestras madres, creación tras creación, en tres grados de ti­nieblas. Éste es Al‑lah, vuestro Señor. Suyo es el Reino. No existe ningún dios sino Él. ¿Incluso así os apartáis?" WHERE ayatNumber = 7 AND chapterNumber = 39;
UPDATE verses SET es = "Si sois desagradecidos, en ver­dad Al‑lah es Autosuficiente, <em>por ser independiente </em>de vosotros. Mas Él no aprueba la ngratitud en Sus siervos. Pero si mostráis gratitud, le agrada <em>verla </em>en vosotros. Ningún portador de carga llevará la carga de otro. Después volveréis a vuestro Señor; Él os informará lo que habéis estado haciendo. En verdad, Él co­noce muy bien lo que hay oculto en los pechos." WHERE ayatNumber = 8 AND chapterNumber = 39;
UPDATE verses SET es = "Mas cuando la aflicción cae sobre el hombre, acude a su Señor, volviendo a Él, <em>con penitencia. </em>Des­pués, cuando Él le otorga Su favor, olvida lo que solía implorar antes y empieza a asignar rivales a Al‑lah, para apartar <em>a los hombres </em>de Su Camino. Dile: “Aprovéchate algún tiempo de tu incredulidad; en verdad eres de los habitantes del Fuego”." WHERE ayatNumber = 9 AND chapterNumber = 39;
UPDATE verses SET es = "¿Acaso quien ora con devo­ción <em>a Dios </em>en las horas de la noche, postrado y de pie, teme el Más Allá y espera la misericordia de u Señor, <em>puede equipararse a quien no actúa así? </em>Di <em>: </em>“¿Son acaso iguales quienes conocen y quienes no conocen?”. En verdad, sólo caerán en la cuenta los dotados de conocimiento." WHERE ayatNumber = 10 AND chapterNumber = 39;
UPDATE verses SET es = "Diles: “Oh vosotros, Mis sier­vos creyentes, temed a vuestro Señor. Hay una recompensa excelente para quienes practican el bien en esta vida. Y la tierra de Al‑lah es vasta. En verdad, los perseverantes ten­drán su recompensa sin medida”." WHERE ayatNumber = 11 AND chapterNumber = 39;
UPDATE verses SET es = "Diles: “En verdad, se me ha ordenado que adore a Al‑lah, ofre­ciéndole una sincera devoción en la fe." WHERE ayatNumber = 12 AND chapterNumber = 39;
UPDATE verses SET es = "“Y se me ha ordenado que sea el primero de los que se someten <em>a Él”.</em>" WHERE ayatNumber = 13 AND chapterNumber = 39;
UPDATE verses SET es = "Diles: “En verdad temo, si desobedezco a mi Señor, el castigo del gran día”." WHERE ayatNumber = 14 AND chapterNumber = 39;
UPDATE verses SET es = "Diles: “A Al‑lah es a Quien adoro, siendo sincero ante Él en mi religión." WHERE ayatNumber = 15 AND chapterNumber = 39;
UPDATE verses SET es = "“Adorad pues a lo que queráis fuera de El”. Diles: “En verdad los perdedores serán quienes arruinen a sus almas y a sus familias en el Día de la Resurrección”.¡Cuidado! ésa será ciertamente la pérdida manifiesta." WHERE ayatNumber = 16 AND chapterNumber = 39;
UPDATE verses SET es = "Tendrán sobre ellos capas de fuego, y debajo de ellos, otras <em>similares. </em>Contra esto es contra lo que Al‑lah advierte a Sus siervos: “Oh, siervos Míos, temedme sólo a Mí”." WHERE ayatNumber = 17 AND chapterNumber = 39;
UPDATE verses SET es = "Mas quienes se apartan de los falsos dioses para no adorarles y vuelven a Al‑lah: Para ellos hay una buena nueva. Da pues la buena nueva a Mis siervos." WHERE ayatNumber = 18 AND chapterNumber = 39;
UPDATE verses SET es = "Que escuchan la Palabra y siguen lo mejor de ella. Ellos son los guiados por Al‑lah y ellos son los hombres del entendimiento." WHERE ayatNumber = 19 AND chapterNumber = 39;
UPDATE verses SET es = "¿Podrá ser <em>rescatado, </em>pues, aquel contra quien se ha dictado la sentencia del castigo? ¿Puedes res­catar a quien está en el Fuego?" WHERE ayatNumber = 20 AND chapterNumber = 39;
UPDATE verses SET es = "Pero para los que temen a su Señor hay mansiones elevadas y otras construidas aún más elevadas, por debajo de las cuales fluyen ríos. Al‑lah ha hecho esa promesa; y Al‑lah no rompe <em>Su</em> promesa." WHERE ayatNumber = 21 AND chapterNumber = 39;
UPDATE verses SET es = "¿No has visto que Al‑lah envía el agua del cielo, y hace que fluya <em>en forma de </em>pequeños arroyuelos en la tierra, produciendo en­tonces con ella vegetación de di­versos colores? Después se seca y la ves ponerse amarilla; más tarde se convierte en desperdicio. Aquí hay en verdad un recordatorio para los hombres que entienden." WHERE ayatNumber = 22 AND chapterNumber = 39;
UPDATE verses SET es = "¿Acaso es aquel cuyo seno ha abierto Al‑lah para la <em>aceptación del </em>Islam, a fin de que sea guiado por una luz procedente de su Señor, <em> como quien anda a tientas en las tinieblas de la incredulidad? </em> ¡Ay, pues, de aquellos cuyos corazones están endurecidos contra el recuerdo de Al‑lah! Están en un error evidente." WHERE ayatNumber = 23 AND chapterNumber = 39;
UPDATE verses SET es = "Al‑lah ha revelado el mejor Mensaje <em>en forma de </em>un libro,<em>cuyos versículos </em>se apoyan mutuamente, y se repiten <em>en diversas formas. </em>Ante él se estremece la piel de quienes temen a su Señor. Después su piel y sus corazones se ablandan ante el recuerdo de Al‑lah; Así es la guía de Al‑lah; con ella encamina a quien Le agrada. Mas a quien Al‑lah sentencia como extraviado... no tendrá guía <em>alguna</em>." WHERE ayatNumber = 24 AND chapterNumber = 39;
UPDATE verses SET es = "¿Acaso aquel que, no tiene más que su propia fe para pro­tegerlo el severo castigo del Día de la Resurrección <em>es como el que se siente seguro? </em>Mas se dirá a los inicuos: “probad lo <em>que solíais ganar”.</em>" WHERE ayatNumber = 25 AND chapterNumber = 39;
UPDATE verses SET es = "Quienes existieron antes que ellos rechazaron <em>a Nuestros Mensa­jeros, </em>por lo que el castigo les sobre­vino de donde no esperaban." WHERE ayatNumber = 26 AND chapterNumber = 39;
UPDATE verses SET es = "Al‑lah les ha hecho, pues, probar la humillación en la presente vida y el castigo del Más Allá será ciertamente mayor; ¡ojalá lo su­pieran!" WHERE ayatNumber = 27 AND chapterNumber = 39;
UPDATE verses SET es = "En verdad en este Corán hemos presentado a los hombres toda clase de parábolas, para que caigan en la cuenta." WHERE ayatNumber = 28 AND chapterNumber = 39;
UPDATE verses SET es = "<em> Hemos revelado </em>el Corán manifiestamente claro y sin torci­miento alguno, para que puedan llegar a ser justos." WHERE ayatNumber = 29 AND chapterNumber = 39;
UPDATE verses SET es = "Al‑lah presenta una parábola: un hombre perteneciente a varios socios, que están mutuamente en desacuerdo, y otro hombre que per­tenece exclusivamente a un solo hombre. ¿Es igual el ejemplo de ambos? Toda alabanza perte­nece a Al‑lah. Pero la mayoría de ellos lo desconocen." WHERE ayatNumber = 30 AND chapterNumber = 39;
UPDATE verses SET es = "Ciertamente morirás, y cier­tamente ellos morirán <em>también.</em>" WHERE ayatNumber = 31 AND chapterNumber = 39;
UPDATE verses SET es = "Y después, en el Día de la Resurrección, disputaréis mutuamen­te ante vuestro Señor." WHERE ayatNumber = 32 AND chapterNumber = 39;
UPDATE verses SET es = "¿Quién es, pues, más injusto que quien miente contra Al‑lah y rechaza la verdad cuando le llega? ¿No hay acaso en el Infierno una morada para los incrédulos?" WHERE ayatNumber = 33 AND chapterNumber = 39;
UPDATE verses SET es = "Mas quien ha traído la verdad, y quien da testimonio de ella <em>como tal: </em>ésos son los justos." WHERE ayatNumber = 34 AND chapterNumber = 39;
UPDATE verses SET es = "Tendrán con su Señor cuanto deseen; ésa es la recompensa de los que practican el bien." WHERE ayatNumber = 35 AND chapterNumber = 39;
UPDATE verses SET es = "Por lo que Al‑lah retirará de ellos las <em>consecuencias </em> nefastas de lo que hicieron, y les concederá su recompensa de acuerdo con lo mejor de lo que hicieron." WHERE ayatNumber = 36 AND chapterNumber = 39;
UPDATE verses SET es = "¿No es Al‑lah suficiente para Sus siervos? Sin embargo ellos inten­tarán atemorizarte con lo que colocan en Su lugar. Mas a quien Al‑lah sentencia como perdido: para él no habrá guía." WHERE ayatNumber = 37 AND chapterNumber = 39;
UPDATE verses SET es = "Y a quien Al‑lah guía, no hay nadie que pueda extraviarlo. ¿Acaso no es Al‑lah el Poderoso, el Señor de la retribución?" WHERE ayatNumber = 38 AND chapterNumber = 39;
UPDATE verses SET es = "Y si les preguntas: “¿Quién creó los cielos y la tierra?” dirán ciertamente: “Al‑lah”. Diles: “¿Pensáis vosotros que si Al‑lah qui­siera hacerme algún daño, aquellos a quienes invocáis fuera de Al‑lah po­drán evitar el daño infligido por Él? O, si Él deseara mostrarme Su mise­ricordia, ¿podrían ellos privarme de Su misericordia?”. Diles: “Al‑lah me basta. En Él confían quienes están dispuestos a confiar”." WHERE ayatNumber = 39 AND chapterNumber = 39;
UPDATE verses SET es = "Diles: “Oh, pueblo Mío, ac­tuad del <em>mejor </em>modo posible; Yo <em>también </em>actúo. Pronto sabréis," WHERE ayatNumber = 40 AND chapterNumber = 39;
UPDATE verses SET es = "“Quién es aquel a quien azo­tará un castigo que lo afligirá y sobre quien caerá un castigo permanente”." WHERE ayatNumber = 41 AND chapterNumber = 39;
UPDATE verses SET es = "En verdad, te hemos revelado el Libro con la verdad para el<em>bien de </em>los hombres. Así pues, quien sigue la guía, <em>la sigue</em> a favor de su propia alma; y quien se extravía, se extravía únicamente en su perjuicio. Mas tú no eres su guardián." WHERE ayatNumber = 42 AND chapterNumber = 39;
UPDATE verses SET es = "Al‑lah se lleva las almas de los vivos en el momento de su muerte; y durante su sueño las de los que <em>aún </em>no han muerto. Y des­pués retiene las de aquellos cuya muerte ha decretado y devuelve a las otras hasta el plazo fijado. Aquí hay ciertamente Signos para las gentes que reflexionan." WHERE ayatNumber = 43 AND chapterNumber = 39;
UPDATE verses SET es = "¿Se han buscado intercesores fuera de Al‑lah? Diles: <em>“¿Habéis ac­tuado así </em>aunque no tienen poder sobre nada, ni inteligencia?”." WHERE ayatNumber = 44 AND chapterNumber = 39;
UPDATE verses SET es = "Diles: “Toda la intercesión corresponde a Al‑lah. A Él pertenece El Reino de los cielos y de la tierra. Y a Él seréis más tarde devueltos”." WHERE ayatNumber = 45 AND chapterNumber = 39;
UPDATE verses SET es = "Mas cuando se menciona sólo a Al‑lah, retroceden con aborrecimiento los corazones de quienes no" WHERE ayatNumber = 46 AND chapterNumber = 39;
UPDATE verses SET es = "Di: “¡Oh, Al‑lah! Hacedor de los cielos y de la tierra; Conocedor de lo visible y de lo invisible; <em>sólo</em> Tú juzgarás entre Tus siervos en rela­ción con sus divergencias”." WHERE ayatNumber = 47 AND chapterNumber = 39;
UPDATE verses SET es = "Si los inicuos poseyeran todo lo que hay en la tierra y el doble de todo ello, ciertamente <em>de­searían </em>rescatarse con ello del severo castigo del Día de la Resurrec­ción; pero recibirán de Al‑lah lo que nunca pensaron." WHERE ayatNumber = 48 AND chapterNumber = 39;
UPDATE verses SET es = "Y se les hará evidente las nefastas <em>consecuencias </em>de lo que merecieron y caerá sobre ellos todo aquello de lo que solían burlarse." WHERE ayatNumber = 49 AND chapterNumber = 39;
UPDATE verses SET es = "Cuando la tribulación cae sobre el hombre, Nos invoca. Pero cuando e concedemos un favor Nuestro, dice: “esto se me ha dado gracias a <em>mi</em> <em>propio </em>conocimien­to”. No; se trata sólo de una prueba, pero la mayoría de ellos lo desconocen." WHERE ayatNumber = 50 AND chapterNumber = 39;
UPDATE verses SET es = "Quienes existieron antes que ellos dijeron lo mismo, y sin em­bargo todo lo que habían conseguido no les sirvió de nada;" WHERE ayatNumber = 51 AND chapterNumber = 39;
UPDATE verses SET es = "De ese modo las <em>consecuencias </em>malvadas de lo que merecieron, ca­yeron sobre ellos; y en cuanto a quienes hacen el mal de entre estos <em>incrédulos, </em>también serán sobreco­gidos con lo que no podrán lograr frustrar." WHERE ayatNumber = 52 AND chapterNumber = 39;
UPDATE verses SET es = "¿Acaso no saben que Al‑lah amplía sus favores a quien Le agrada, y los constriñe <em>para quien quiere? </em>En verdad aquí hay Signos para los hombres que creen." WHERE ayatNumber = 53 AND chapterNumber = 39;
UPDATE verses SET es = "Diles: “¡Oh siervos Míos, que habéis cometido excesos contra vues­tras propias almas! no deses­peréis de la misericordia de Al‑lah, pues en verdad Al‑lah per­dona todos los pecados. Ciertamente Él es el Sumo Indulgente, Miseri­cordioso." WHERE ayatNumber = 54 AND chapterNumber = 39;
UPDATE verses SET es = "“Volved a vuestro Señor, y someteos a Él, antes de que os llegue el castigo; <em>pues </em>entonces no recibiréis ayuda." WHERE ayatNumber = 55 AND chapterNumber = 39;
UPDATE verses SET es = "“Seguid la mejor <em>Enseñanza, </em>que os ha sido revelada por vuestro Señor, antes de que el castigo os sobrevenga de improviso, mientras no lo esperáis;" WHERE ayatNumber = 56 AND chapterNumber = 39;
UPDATE verses SET es = "“Para que ningún alma pueda decir, “¡Ay de mí! Incumplí <em>mi obligación </em>a pesar de estar en la presencia de Al‑lah; y ciertamente fui de aquellos que se bur­laron”." WHERE ayatNumber = 57 AND chapterNumber = 39;
UPDATE verses SET es = "“O que diga: “Si Al‑lah me hubiese guiado, ciertamente habría estado entre los justos”;" WHERE ayatNumber = 58 AND chapterNumber = 39;
UPDATE verses SET es = "“O que diga cuando observe el castigo: “¡Si pudiera volver <em>al</em> <em>mundo, </em>sería entonces de aquellos que prac­tican el bien!”." WHERE ayatNumber = 59 AND chapterNumber = 39;
UPDATE verses SET es = "<em>Dios responderá: </em>“Ay, te lle­garon Mis Signos pero tú los trataste como mentiras, fuiste arrogante y perteneciste a los incrédulos”." WHERE ayatNumber = 60 AND chapterNumber = 39;
UPDATE verses SET es = "<em></em>Y en el Día de la Resurrección, verás a quienes mintieron contra Al‑lah, con sus rostros ennegrecidos. ¿No hay acaso en el Infierno una morada para los orgullosos?" WHERE ayatNumber = 61 AND chapterNumber = 39;
UPDATE verses SET es = "<em></em>Mas Al‑lah conducirá a los que Le temen, a su lugar legítimo de seguridad <em>y éxito</em>; el mal no les afectará ni serán afligidos." WHERE ayatNumber = 62 AND chapterNumber = 39;
UPDATE verses SET es = "Al‑lah es el Creador de todas las cosas y es el Guardián de todas las cosas." WHERE ayatNumber = 63 AND chapterNumber = 39;
UPDATE verses SET es = "A Él pertenecen las llaves de los cielos y la tierra; mas uienes no crean en los Signos de Al‑lah, ellos serán los perdedores. " WHERE ayatNumber = 64 AND chapterNumber = 39;
UPDATE verses SET es = "Diles: “¿Es a otros <em>dioses </em>distintos a Al‑lah a quienes me orde­náis adorar, Oh ignorantes?”." WHERE ayatNumber = 65 AND chapterNumber = 39;
UPDATE verses SET es = "<em></em>En verdad se te ha revelado al igual que a los que te precedieron: “Si atribuyes partícipes a <em>Dios,</em> tu trabajo será vano en verdad y te contarás ciertamente entre los perdedores”." WHERE ayatNumber = 66 AND chapterNumber = 39;
UPDATE verses SET es = "<em></em>¡Ay! adorad a Al‑lah y sed de los agradecidos." WHERE ayatNumber = 67 AND chapterNumber = 39;
UPDATE verses SET es = "No respetan apropiadamente la Majestad de Al‑lah como es debido. Y la tierra estará enteramente bajo Su dominio en el Día del Juicio Final, y así también estarán los cielos enrollados en Su mano derecha. Glorificado y exal­tado sea Él por encima de lo que ellos Le asocian." WHERE ayatNumber = 68 AND chapterNumber = 39;
UPDATE verses SET es = "Sonará la trompeta y <em>todos </em>los que estén en los cielos y <em>todos </em>los que estén en la tierra <em>caerán </em> desva­necidos, excepto aquellos a quienes Al‑lah guste <em>librar.</em> Después sonará una segunda vez, e inmediatamente se pondrán de pie, mirando alre­dedor." WHERE ayatNumber = 69 AND chapterNumber = 39;
UPDATE verses SET es = "La tierra brillará con la luz de su Señor, el Libro será colocado <em>abierto ante ellos</em>, se traerá a los Profetas y a los testigos, dictán­dose sentencia entre ellos con jus­ticia, y no serán perjudicados." WHERE ayatNumber = 70 AND chapterNumber = 39;
UPDATE verses SET es = "Pues cada alma será recompensada plenamente por lo que hizo. Él sabe muy bien lo que hacen." WHERE ayatNumber = 71 AND chapterNumber = 39;
UPDATE verses SET es = "Quienes no creyeron serán conducidos al Infierno en grupos hasta que, al llegar a él, se abran sus puertas, y sus Guardianes les digan: “¿Acaso no os llegaron Men­sajeros de entre vosotros mismos, recitándoos los Signos de vuestro Señor, y advirtiéndoos de la reunión de este Día vuestro? Ellos respon­derán: “Sí, pero la sentencia del castigo ha sido dictada justamente contra los incrédulos”." WHERE ayatNumber = 72 AND chapterNumber = 39;
UPDATE verses SET es = "Se dirá entonces: “Entrad pues en las puertas del Infierno, morando en él. Pues pésima es la morada del arrogante”." WHERE ayatNumber = 73 AND chapterNumber = 39;
UPDATE verses SET es = "Pero quienes temieron a su Señor serán llevados al Cielo en grupos hasta que, al llegar a él y una vez abiertas sus puertas, sus uar­dianes les digan: “¡La paz sea con vosotros! Sed felices, y morad <em>aquí</em>”." WHERE ayatNumber = 74 AND chapterNumber = 39;
UPDATE verses SET es = "Ellos responderán: “Toda alabanza corresponde a Al‑lah, que ha cumplido Su promesa para con nosotros y nos ha dado la tierra como herencia, fijando nuestra morada en el Jardín, en donde nos agrade”, Qué excelente es pues la recompensa de los trabajadores <em>justos</em>!" WHERE ayatNumber = 75 AND chapterNumber = 39;
UPDATE verses SET es = "Verás a los ángeles rodeando el Trono, glorificando a su Señor con Su alabanza. Se juzgará entre ellos con justicia. Y se dirá: “Toda alabanza corresponde a Al-lah, el Señor de los mundos”." WHERE ayatNumber = 76 AND chapterNumber = 39;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 40;
UPDATE verses SET es = "Ha Mim." WHERE ayatNumber = 2 AND chapterNumber = 40;
UPDATE verses SET es = "La revelación del Libro procede de Al-lah, el Poderoso, el Omnisciente." WHERE ayatNumber = 3 AND chapterNumber = 40;
UPDATE verses SET es = "El Perdonador de los pecados y Aceptador del arrepentimiento, Severo en el castigo, Poseedor de la generosidad. No hay ningún Dios sino Él. Hacia Él será el retorno final." WHERE ayatNumber = 4 AND chapterNumber = 40;
UPDATE verses SET es = "Nadie disputa sobre los Signos de Al-lah excepto los incrédulos. No dejes, pues, que sus movimientos por el territorio te engañen." WHERE ayatNumber = 5 AND chapterNumber = 40;
UPDATE verses SET es = "El pueblo de Noé y <em>otros</em> grupos después de ellos, negaron <em>Nuestros Signos</em> antes de este <em>pueblo</em> y cada nación se sforzó por atrapar a su Mensajero, disputando por medio de falsos <em>argumentos</em> para rechazar con ellos la verdad. Entonces caí sobre ellos y ¡qué <em>terrible</em> fue Mi retribución!" WHERE ayatNumber = 6 AND chapterNumber = 40;
UPDATE verses SET es = "Así se demostró la verdad de la palabra de tu Señor contra los incrédulos; porque son los habitantes del Fuego." WHERE ayatNumber = 7 AND chapterNumber = 40;
UPDATE verses SET es = "Quienes llevan el Trono, y quienes lo rodean, glorifican a su Señor con <em>Sus</em> alabanzas, creen en Él y piden perdón por los creyentes,<em> diciendo:</em> “Señor nuestro, Tú abarcas todas las cosas en <em>Tu</em> misericordia y conocimiento. Perdona pues a quienes se arrepienten y siguen Tu camino y protégelos contra el castigo del Infierno”." WHERE ayatNumber = 8 AND chapterNumber = 40;
UPDATE verses SET es = "“Haz, Señor nuestro, que entren en los Jardines de Eternidad que Tú les has prometido, así como a aquellos de sus padres, sus esposas y sus hijos que sean virtuosos. En verdad Tú eres el Poderoso, el Sabio." WHERE ayatNumber = 9 AND chapterNumber = 40;
UPDATE verses SET es = "“Y protégelos del mal; pues a quien protejas de los males en ese ía, a ése le has mostrado ciertamente misericordia. Pues ése es <em>en verdad</em> el triunfo supremo”." WHERE ayatNumber = 10 AND chapterNumber = 40;
UPDATE verses SET es = "Ciertamente se hará una llamada a los incrédulos: “La aversión de Al-lah es mayor que vuestro mutuo aborrecimiento cuando fuisteis llamados a creer y rehusasteis”." WHERE ayatNumber = 11 AND chapterNumber = 40;
UPDATE verses SET es = "Responderán: “Señor nuestro, nos has hecho morir dos veces, y nos has dado la vida dos veces, y ahora confesamos nuestros pecados. ¿Hay algún medio para salir de aquí?”." WHERE ayatNumber = 12 AND chapterNumber = 40;
UPDATE verses SET es = "Esto es porque, cuando Al-lah fue proclamado Uno, rechazasteis la lamada, pero cuando se Le asociaron partícipes, creísteis. Pero la <em>última </em>palabra corresponde a Al-lah, el Sublime, el Grande”." WHERE ayatNumber = 13 AND chapterNumber = 40;
UPDATE verses SET es = "Él es Quien os muestra Sus Signos, y os envía provisiones desde el ielo; pero nadie cae en la cuenta excepto quien se vuelve <em>a Dios.</em>" WHERE ayatNumber = 14 AND chapterNumber = 40;
UPDATE verses SET es = "Invocad pues a Al-lah, siendo sinceros con Él en la fe, aunque los incrédulos se aparten." WHERE ayatNumber = 15 AND chapterNumber = 40;
UPDATE verses SET es = "El que Eleva en rangos, el Señor del Trono. Él hace que el espíritu descienda por Su orden a aquel de Sus siervos que Le place, para poder advertir sobre el Día de la Reunión." WHERE ayatNumber = 16 AND chapterNumber = 40;
UPDATE verses SET es = "El día en que comparecerán<em> todos</em>; nada de lo que se refiera a ellos estará oculto para Al-lah. “¿A quien pertenece el reino en este día?”. “A Al-lah, el Único, el Sumo Supremo”." WHERE ayatNumber = 17 AND chapterNumber = 40;
UPDATE verses SET es = "“En este día cada alma será retribuida con lo que ganó. No habrá injusticia en este día. En verdad, Al-lah es Rápido en las cuentas”." WHERE ayatNumber = 18 AND chapterNumber = 40;
UPDATE verses SET es = "Y adviérteles del Día que se acerca, cuando los corazones su­birán hasta sus gargantas, mientras estén intentando suprimir su angustia. Los injustos no tendrán amigos que les quieran, ni intercesor alguno cuya <em>intercesión</em> les sirva." WHERE ayatNumber = 19 AND chapterNumber = 40;
UPDATE verses SET es = "Él conoce la traición de los ojos y lo que ocultan los pechos." WHERE ayatNumber = 20 AND chapterNumber = 40;
UPDATE verses SET es = "Y Al-lah juzga con verdad, mientras que aquellos a quienes invocan fuera de Al-lah no deciden nada. En verdad, Al-lah es Quien Todo lo Oye, Quien todo lo Ve." WHERE ayatNumber = 21 AND chapterNumber = 40;
UPDATE verses SET es = "¿Acaso no han viajado por la tierra y han visto cuál fue el fin de quienes les precedieron? Eran más poderosos que éstos y mas potentes en las huellas que dejaron en la tierra. Pero Al-lah les atrapó por sus pecados, y no tuvieron protector alguno contra Al-lah." WHERE ayatNumber = 22 AND chapterNumber = 40;
UPDATE verses SET es = "Esto fue porque Sus Mensajeros llegaron a ellos con Signos manifiestos, pero no creyeron; Al-lah pues, cayó sobre ellos. En verdad Él es Poderoso, Severo en el castigo." WHERE ayatNumber = 23 AND chapterNumber = 40;
UPDATE verses SET es = "Enviamos a Moisés, con Nuestros Signos y autoridad manifiesta," WHERE ayatNumber = 24 AND chapterNumber = 40;
UPDATE verses SET es = "Al Faraón, Haman y Korah; pero dijeron: “Es un mago y un impostor”." WHERE ayatNumber = 25 AND chapterNumber = 40;
UPDATE verses SET es = "Mas cuando llegó a ellos con Nuestra verdad, dijeron: “Matad a los hijos de quienes han creído con él, y dejad vivas a sus mujeres”. Pero el designio de los incrédulos no es sino inútil." WHERE ayatNumber = 26 AND chapterNumber = 40;
UPDATE verses SET es = "El Faraón dijo: “Dejadme <em>sólo</em> para que mate a Moisés. Y dejadle que invoque a su Señor. Temo que vaya a cambiar vuestra religión o inunde la tierra con el desorden y la corrupción”." WHERE ayatNumber = 27 AND chapterNumber = 40;
UPDATE verses SET es = "Moisés dijo: “Me refugio en mi Señor y vuestro Señor contra cualquier <em>persona</em> arrogante que no crea en el Día de las Cuentas”." WHERE ayatNumber = 28 AND chapterNumber = 40;
UPDATE verses SET es = "Un hombre creyente de entre el pueblo del Faraón, que ocultaba su fe, dijo: “¿Vais a matar a un hombre porque dice: “mi Señor es Al-lah”, al tiempo que os ha presentado pruebas claras de vuestro Señor? Si fuese un mentiroso, que caiga sobre él <em>el pecado de</em> su mentira; pero si es veraz, algo de aquello con lo que os ha amenazado caerá <em>ciertamente</em> sobre vosotros. En verdad Al-lah no guía a quien es transgresor <em>y </em>mentiroso." WHERE ayatNumber = 29 AND chapterNumber = 40;
UPDATE verses SET es = "“Oh, pueblo mío, vuestra es la soberanía en este día, ya que domináis en el país. Pero ¿quién nos ayudará <em>y protegerá</em> contra el castigo de Al-lah si cae sobre nosotros?”. El Faraón dijo: “Sólo te digo lo que veo por mí mismo y sólo te guío al camino de la rectitud”." WHERE ayatNumber = 30 AND chapterNumber = 40;
UPDATE verses SET es = "Mas quien creía respondió: “Oh, pueblo mío, temo para vosotros algo similar al tiempo fatídico de los confederados”." WHERE ayatNumber = 31 AND chapterNumber = 40;
UPDATE verses SET es = "<em>“A lo sucedido </em>del pueblo del Noé, de Ad y Zamud, y de los que los siguieron. Y Al-lah no causa injusticia alguna a <em>Sus</em> siervos”." WHERE ayatNumber = 32 AND chapterNumber = 40;
UPDATE verses SET es = "“¡Oh, pueblo mío! temo para vosotros el día de las mutuas llamadas <em>de socorro</em>”," WHERE ayatNumber = 33 AND chapterNumber = 40;
UPDATE verses SET es = "“Un día en el que volveréis la espalda huyendo. Nadie os defenderá contra Al-lah. Y para quien Al-lah sentencia como extraviado no habrá guía”." WHERE ayatNumber = 34 AND chapterNumber = 40;
UPDATE verses SET es = "“José vino a vosotros con pruebas claras, pero no dejasteis de dudar sobre aquello con lo que vino a vosotros hasta que, a su muerte, dijisteis: “Al-lah nunca suscitará un Mensajero después de él”. Así juzga Al-lah ciertamente como perdidos a los que transgreden <em>y</em> dudan”," WHERE ayatNumber = 35 AND chapterNumber = 40;
UPDATE verses SET es = "“A quienes disputan sobre los Signos de Al-lah sin que les haya sido dada autoridad alguna. Esto es terriblemente odioso a la vista de Al-lah y a la vista de quienes creen. Así sella Al-lah el corazón de toda <em>persona</em> arrogante, orgullosa”." WHERE ayatNumber = 36 AND chapterNumber = 40;
UPDATE verses SET es = "El Faraón respondió: “Oh, Haman, constrúyeme un edificio elevado para que pueda tener acceso," WHERE ayatNumber = 37 AND chapterNumber = 40;
UPDATE verses SET es = "“Los medios para acercarme a los Cielos, para poder echar una mirada al Dios de Moisés, pues creo ciertamente que es un mentiroso”. De este modo se hizo que la maldad de sus acciones <em>apareciera</em> justa a los ojos del Faraón, y fue apartado del camino <em>recto;</em> más el designio del Faraón no terminó más que en la ruina." WHERE ayatNumber = 38 AND chapterNumber = 40;
UPDATE verses SET es = "Aquel que creía dijo: “Oh, pueblo mío, seguidme. Os guiaré al camino de la rectitud”." WHERE ayatNumber = 39 AND chapterNumber = 40;
UPDATE verses SET es = "“Oh, pueblo mío, la vida de este mundo no es más que una provisión <em>temporal</em>; y el Más Allá es ciertamente el hogar para una estancia permanente”." WHERE ayatNumber = 40 AND chapterNumber = 40;
UPDATE verses SET es = "“Quien hace el mal será pagado únicamente con algo similar; pero quien hace el bien, sea hombre o mujer, y crea, éstos entrarán en el Jardín; en él se les proporcionará sin medida”." WHERE ayatNumber = 41 AND chapterNumber = 40;
UPDATE verses SET es = "“Oh, pueblo mío, qué <em>extraño</em> es que yo os llame a la salvación y vosotros me llaméis al Fuego”." WHERE ayatNumber = 42 AND chapterNumber = 40;
UPDATE verses SET es = "“Me invitáis a no creer en Al-lah y asociarle aquello de lo que no tengo conocimiento, mientras que yo os invito al Poderoso, al Gran Perdonador." WHERE ayatNumber = 43 AND chapterNumber = 40;
UPDATE verses SET es = "“En verdad que aquello a lo que me llamáis no tiene <em>título</em> para ser invocado en este mundo o en el Más Allá; y que nuestro retorno será ciertamente a Al-lah y que los transgresores serán los moradores del Fuego." WHERE ayatNumber = 44 AND chapterNumber = 40;
UPDATE verses SET es = "“Pronto recordaréis, pues, lo que os digo. Confío mi causa a Al-lah. En verdad Al-lah ve <em>a todos</em> Sus siervos”." WHERE ayatNumber = 45 AND chapterNumber = 40;
UPDATE verses SET es = "El resultado fue que Al-lah lo salvaguardó de los males de todo lo que tramaron, y un castigo terrible cayó sobre el pueblo del Faraón:" WHERE ayatNumber = 46 AND chapterNumber = 40;
UPDATE verses SET es = "El Fuego. Están expuestos a él día y noche. Y el día en que llegue la Hora, <em>se dirá: </em>“Arrojad al pueblo del Faraón al más severo de los castigos”." WHERE ayatNumber = 47 AND chapterNumber = 40;
UPDATE verses SET es = "Y cuando disputen mutuamente en el Fuego, los débiles dirán a los que fueron orgullosos: “En verdad fuimos vuestros seguidores; ¿no podéis, pues, aliviarnos de una parte del Fuego?”." WHERE ayatNumber = 48 AND chapterNumber = 40;
UPDATE verses SET es = "Los que fueron orgullosos dirán: “Todos estamos en él. Al-lah ha sentenciado ya entre<em> Sus</em> siervos”." WHERE ayatNumber = 49 AND chapterNumber = 40;
UPDATE verses SET es = "Quienes estén en el Fuego dirán a los Guardianes del Infierno: Rogad a vuestro Señor para que nos aligere el castigo por un <em>solo</em> día”." WHERE ayatNumber = 50 AND chapterNumber = 40;
UPDATE verses SET es = "Responderán: “¿Acaso no os llegaron vuestros Mensajeros con Signos manifiestos?”. Contestarán: “Sí”.<em> Los Guardianes</em> les replicarán: “Entonces rezad”. Pero la oración de los incrédulos no sirve de nada." WHERE ayatNumber = 51 AND chapterNumber = 40;
UPDATE verses SET es = "Ciertamente que ayudamos a nuestros Mensajeros y a quienes creen, <em>tanto</em> en la vida presente como en el día en que comparecerán los testigos." WHERE ayatNumber = 52 AND chapterNumber = 40;
UPDATE verses SET es = "El día en que sus excusas no aprovecharán a los inicuos, pues suya será la maldición y suya será la peor morada." WHERE ayatNumber = 53 AND chapterNumber = 40;
UPDATE verses SET es = "Mas en verdad dimos a Moisés la guía e hicimos a los hijos de Israel los herederos del Libro:" WHERE ayatNumber = 54 AND chapterNumber = 40;
UPDATE verses SET es = "Guía y recordatorio para los hombres que entienden." WHERE ayatNumber = 55 AND chapterNumber = 40;
UPDATE verses SET es = "Ten, pues, paciencia. En verdad la promesa de Al-lah es verdadera. Busca protección <em>en tu Señor</em> contra tus faltas y glorifica a Tu Señor con Su alabanza por la tarde y por la mañana." WHERE ayatNumber = 56 AND chapterNumber = 40;
UPDATE verses SET es = "Quienes disputan sobre los Signos de Al-lah sin que se les haya dado autoridad alguna, no tienen nada en sus pechos salvo una presunción de grandeza que nunca alcanzarán. Busca pues refugio en Al-lah. En verdad Él es Quien todo lo Oye, Quien todo lo Ve." WHERE ayatNumber = 57 AND chapterNumber = 40;
UPDATE verses SET es = "En verdad, la creación de los cielos y la tierra es mayor que la creación de la humanidad; pero la mayoría de los hombres lo desconocen." WHERE ayatNumber = 58 AND chapterNumber = 40;
UPDATE verses SET es = "Los ciegos y los que ven no son iguales; tampoco quienes creen y practican las buenas obras <em>son iguales</em> a quienes hacen el mal. Muy poco reflexionáis." WHERE ayatNumber = 59 AND chapterNumber = 40;
UPDATE verses SET es = "La Hora vendrá ciertamente; no hay duda sobre ello; sin embargo, la mayoría de los hombres no creen." WHERE ayatNumber = 60 AND chapterNumber = 40;
UPDATE verses SET es = "Y vuestro Señor dice: “Rogadme; Yo responderé a vuestra <em>oración.</em> Pero quienes son demasiado orgullosos para adorarme entrarán ciertamente en el Infierno, humillados”." WHERE ayatNumber = 61 AND chapterNumber = 40;
UPDATE verses SET es = "Es Al-lah quien ha hecho la noche para que descanséis en ella y el día para <em>permitiros</em> ver. En verdad, Al-lah es el Señor de la gracia para la humanidad, aunque la mayoría de los hombres son desagradecidos." WHERE ayatNumber = 62 AND chapterNumber = 40;
UPDATE verses SET es = "Así es Al-lah, vuestro Señor, el Creador de todas las cosas. No hay otro Dios sino Él. ¿Cómo entonces os apartáis?" WHERE ayatNumber = 63 AND chapterNumber = 40;
UPDATE verses SET es = "Así ciertamente son alejados quienes niegan los Signos de Al-lah." WHERE ayatNumber = 64 AND chapterNumber = 40;
UPDATE verses SET es = "Al-lah es Quien ha hecho para vosotros la tierra como lugar de descanso y el cielo como medio de dependencia; os ha modelado en figuras y ha hecho excelentes vuestras formas y os ha suministrado provisiones sanas. Así es Al-lah, vuestro Señor. Bendito sea pues, Al-lah, el Señor de los mundos." WHERE ayatNumber = 65 AND chapterNumber = 40;
UPDATE verses SET es = "Él es el <em>Dios</em> Vivo. No hay Dios sino Él. Rogadle, pues, siendo sinceros ante Él en la religión. Toda alabanza corresponde a Al-lah, el Señor de los mundos." WHERE ayatNumber = 66 AND chapterNumber = 40;
UPDATE verses SET es = "Diles: “Se me ha prohibido adorar a quienes invocáis fuera de Al-lah, ya que me han llegado pruebas claras de mi Señor; y se me ha ordenado que me someta al Señor de los mundos”." WHERE ayatNumber = 67 AND chapterNumber = 40;
UPDATE verses SET es = "Él es Quien os creó del polvo, después de una gota de esperma y más arde de un coágulo; después os permite alcanzar la niñez y más tarde <em>os hace crecer</em> para que alcancéis toda vuestra fuerza; por último<em> permite</em> que os hagáis ancianos – aunque a alguno de vosotros se les hace morir antes – y <em>os deja vivir</em> para que lleguéis al plazo fijado y aprendáis la sabiduría." WHERE ayatNumber = 68 AND chapterNumber = 40;
UPDATE verses SET es = "Él es Quien da la vida y causa la muerte. Y cuando decreta una cosa, sólo la dice: “¡Sea!” y comienza a ser." WHERE ayatNumber = 69 AND chapterNumber = 40;
UPDATE verses SET es = "¿No has visto acaso a quienes disputan sobre los Signos de Al-lah? ¡Cómo son alejados<em> de la verdad</em>!" WHERE ayatNumber = 70 AND chapterNumber = 40;
UPDATE verses SET es = "Quienes rechazan el Libro y lo que revelamos a Nuestros Mensajeros. Más pronto sabrán." WHERE ayatNumber = 71 AND chapterNumber = 40;
UPDATE verses SET es = "Cuando las argollas de hierro rodeen sus cuellos, y <em>también</em> cadenas. Serán arrastrados" WHERE ayatNumber = 72 AND chapterNumber = 40;
UPDATE verses SET es = "Al agua hirviente; luego arderán en el Fuego." WHERE ayatNumber = 73 AND chapterNumber = 40;
UPDATE verses SET es = "Entonces se les dirá: “¿En dónde están aquellos que asociabais a <em>Dios”</em>." WHERE ayatNumber = 74 AND chapterNumber = 40;
UPDATE verses SET es = "“Aparte de Al-lah?”. Ellos responderán: “Se nos han desvanecido. No, nunca rogamos a nada antes”. Así confundirá Al-lah a los incrédulos." WHERE ayatNumber = 75 AND chapterNumber = 40;
UPDATE verses SET es = "“Esto es porque os regocijasteis en la tierra sin justificación y porque os mostrasteis vanamente eufóricos”." WHERE ayatNumber = 76 AND chapterNumber = 40;
UPDATE verses SET es = "“Entrad en las puertas del Infierno, para morar en él. ¡Qué despreciable es la morada de los orgullosos!”." WHERE ayatNumber = 77 AND chapterNumber = 40;
UPDATE verses SET es = "Ten pues paciencia. Ciertamente la promesa de Al-lah es verdadera. Y tanto si te mostramos parte de lo que les hemos prometido como si te hacemos morir<em> antes del cumplimiento de Nuestra promesa,</em> a Nosotros serán devueltos <em>en cualquier caso.</em>" WHERE ayatNumber = 78 AND chapterNumber = 40;
UPDATE verses SET es = "Enviamos Mensajeros antes de ti; de ellos hay algunos que te hemos mencionado y otros que no te hemos mencionado; mas no es posible que ningún Mensajero traiga un Signo excepto con permiso de Al-lah. Pero cuando llegó el decreto de Al-lah, el asunto se decidió con verdad, y se convirtieron en perdedores quienes falsificaron la verdad." WHERE ayatNumber = 79 AND chapterNumber = 40;
UPDATE verses SET es = "Al-lah es Quien creó el ganado para vosotros, para que cabalgarais sobre algunos de ellos y comierais de otros." WHERE ayatNumber = 80 AND chapterNumber = 40;
UPDATE verses SET es = "Y tenéis <em>otras</em> ventajas en ellos: y para que, por su ediación, podáis satisfacer cualquier otra necesidad <em> que pudiera haber</em> en vuestros pechos. Y sobre ellos y sobre los barcos sois transportados." WHERE ayatNumber = 81 AND chapterNumber = 40;
UPDATE verses SET es = "Él os muestra Sus Signos; ¿cuál de los Signos de Al-lah negaréis, pues?" WHERE ayatNumber = 82 AND chapterNumber = 40;
UPDATE verses SET es = "Acaso no han viajado por la tierra para poder ver cuál fue el final de quienes los precedieron? Fueron más numerosos que éstos, más fuertes en su poder y<em> en</em> las huellas<em> que dejaron tras ellos</em> en la tierra. Pero todo lo que consiguieron no les sirvió de nada." WHERE ayatNumber = 83 AND chapterNumber = 40;
UPDATE verses SET es = "Pues cuando sus Mensajeros llegaron a ellos con Signos manifiestos, se regocijaron del conocimiento que poseían. Y porque aquello de lo que se burlaron los rodeó." WHERE ayatNumber = 84 AND chapterNumber = 40;
UPDATE verses SET es = "Mas cuando vieron Nuestro castigo, dijeron: “Creemos sólo en Al-lah y rechazamos todo lo que solíamos asociar a Él”." WHERE ayatNumber = 85 AND chapterNumber = 40;
UPDATE verses SET es = "Pero su fe no pudo beneficiarles <em>en el momento</em> en que ieron Nuestro castigo. Ésta es la Ley de Al-lah que se ha aplicado <em>siempre</em> respecto a Sus siervos. Así han perecido los que no creyeron." WHERE ayatNumber = 86 AND chapterNumber = 40;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 41;
UPDATE verses SET es = "Ha Mim." WHERE ayatNumber = 2 AND chapterNumber = 41;
UPDATE verses SET es = "<em> Ésta es</em> una revelación del Clemente, el Misericordioso." WHERE ayatNumber = 3 AND chapterNumber = 41;
UPDATE verses SET es = "Un Libro, cuyos versículos han sido expuestos con detalle – recitados a menudo y creados manifiestamente claros- para un pueblo que tiene conocimiento." WHERE ayatNumber = 4 AND chapterNumber = 41;
UPDATE verses SET es = "Heraldo de la buena nueva y amonestador. Pero la mayoría de ellos se apartan y no lo escuchan." WHERE ayatNumber = 5 AND chapterNumber = 41;
UPDATE verses SET es = "Y dicen: “Nuestros corazones están cubiertos <em>y protegidos</em> contra aquello a lo que nos invocas, en nuestros oídos hay sordera, y ntre tú y nosotros hay un velo. Continúa pues tu trabajo; nosotros <em>también</em> estamos trabajando”." WHERE ayatNumber = 6 AND chapterNumber = 41;
UPDATE verses SET es = "Diles: “Sólo soy un hombre como vosotros. Se me ha revelado que uestro Dios es el Dios Único. Tomad el camino recto hacia Él <em> sin desviaros</em> y pedidle perdón”. ¡Ay de los idólatras!" WHERE ayatNumber = 7 AND chapterNumber = 41;
UPDATE verses SET es = "Que no dan el Zakat y son los que niegan el Más Allá." WHERE ayatNumber = 8 AND chapterNumber = 41;
UPDATE verses SET es = "<em> En cuanto a</em> quienes creen y practican buenas obras, ciertamente tendrán una recompensa que no terminará nunca." WHERE ayatNumber = 9 AND chapterNumber = 41;
UPDATE verses SET es = "Diles: “¿Realmente no creéis en Aquel que creó la tierra en dos períodos? ¿Y colocáis a otros iguales a Él?”. Ése es el Señor de los mundos." WHERE ayatNumber = 10 AND chapterNumber = 41;
UPDATE verses SET es = "Él estableció en ella montañas firmes que se levantan sobre su <em>superficie</em> y dispuso bendiciones en ella y equilibró inuciosamente sus medios de sustento en cuatro etapas, iguales para <em>todos</em> los que buscan." WHERE ayatNumber = 11 AND chapterNumber = 41;
UPDATE verses SET es = "Luego se dirigió al Cielo mientras era <em>algo similar al</em> humo, y dijo a él y a la tierra: “Venid vosotros dos, voluntaria o involuntariamente”. Dijeron: “Vamos voluntariamente”." WHERE ayatNumber = 12 AND chapterNumber = 41;
UPDATE verses SET es = "Así Él los perfeccionó en siete cielos en dos períodos y reveló a ada cielo en su función. Y adornamos el cielo inferior con lámparas <em> para dar luz</em> y protección. Así es el decreto del Poderoso, el Omnisciente." WHERE ayatNumber = 13 AND chapterNumber = 41;
UPDATE verses SET es = "Pero si se apartan, diles entonces: “Os advierto de un castigo destructor como el castigo que <em>cayó</em> sobre Ad y Zamud”." WHERE ayatNumber = 14 AND chapterNumber = 41;
UPDATE verses SET es = "Cuando sus Mensajeros llegaron a ellos por delante y por detrás, <em>diciendo: </em>“No adoréis a nadie sino a Al-lah”, dijeron: Si nuestro Señor<em> así</em> lo hubiese querido, habría enviado ciertamente ángeles. No creemos pues en aquello con lo que has sido enviado”." WHERE ayatNumber = 15 AND chapterNumber = 41;
UPDATE verses SET es = "En cuanto a <em>Ad,</em> se comportaron con arrogancia en la tierra sin justificación alguna y dijeron: “¿quién es más fuerte que nosotros en poder?”. Acaso no ven que Al-lah, que los creó, es más fuerte que ellos en poderío? Y sin embargo continúan negando Nuestros Signos." WHERE ayatNumber = 16 AND chapterNumber = 41;
UPDATE verses SET es = "Por ello les enviamos un furioso vendaval durante varios días nefastos, para hacerles probar el castigo de la humillación en esta vida. Mas el castigo del Más Allá ciertamente será más humillante y no recibirán ayuda." WHERE ayatNumber = 17 AND chapterNumber = 41;
UPDATE verses SET es = "En cuanto a <em>Zamud,</em> les proporcionamos la guía, pero prefirieron la ceguera a la guía, por lo que la desgracia de un castigo humillante cayó sobre ellos, en razón de lo que ha­bían merecido." WHERE ayatNumber = 18 AND chapterNumber = 41;
UPDATE verses SET es = "Mas salvamos a quienes creyeron y actuaron rectamente." WHERE ayatNumber = 19 AND chapterNumber = 41;
UPDATE verses SET es = "El día en el que los enemigos de Al-lah sean reunidos en masa y conducidos al Fuego, se les hará marchar en orden." WHERE ayatNumber = 20 AND chapterNumber = 41;
UPDATE verses SET es = "Hasta que, cuando lleguen a él, sus oídos, sus ojos y su piel, sean testigos contra ellos por lo que estuvieron cometiendo." WHERE ayatNumber = 21 AND chapterNumber = 41;
UPDATE verses SET es = "Y dirán a sus pieles: ¿”Por qué presentáis testimonio contra nosotros?”. Dirán: “Al-lah nos ha hecho hablar como ha hecho que todas las <em>demás</em> cosas hablen. Pues Él es Quien os creó la primera vez y a Él habéis sido devueltos." WHERE ayatNumber = 22 AND chapterNumber = 41;
UPDATE verses SET es = "“No temisteis, mientras <em>cometíais pecados</em> que vuestros oídos, vuestros ojos y vuestras pieles fuesen testigos contra vosotros; no, pensabais que<em> incluso</em> Al-lah desconocía gran parte de lo que solíais hacer." WHERE ayatNumber = 23 AND chapterNumber = 41;
UPDATE verses SET es = "“Mas ese pensamiento vuestro, que teníais respecto a vuestro Señor, os ha arruinado. Así pues, <em>ahora</em> sois de los que están perdidos”." WHERE ayatNumber = 24 AND chapterNumber = 41;
UPDATE verses SET es = "Si ahora pueden resistir, El Fuego es su morada; y si buscan una audiencia, no serán de los escuchados." WHERE ayatNumber = 25 AND chapterNumber = 41;
UPDATE verses SET es = "Y les asignamos compañeros que hicieron que les pareciera atractivo lo que había pasado ante ellos y lo que tenían delante de sí; y el juicio se dictó contra ellos de igual manera que se dictó respecto a os pueblos que les precedieron, pertenecientes a los Yinn - <em>hombres grandes-</em> o a los hombres comunes. En verdad ellos fueron los perdedores." WHERE ayatNumber = 26 AND chapterNumber = 41;
UPDATE verses SET es = "Los incrédulos dicen: “No escuchéis a este Corán; al contrario, haced ruido durante su<em> recitación</em> para que tengáis la ventaja”." WHERE ayatNumber = 27 AND chapterNumber = 41;
UPDATE verses SET es = "Haremos ciertamente que los incrédulos prueben un severo castigo y, con toda seguridad, les pagaremos por las peores de sus acciones." WHERE ayatNumber = 28 AND chapterNumber = 41;
UPDATE verses SET es = "Ésa es la recompensa de los enemigos de Al-lah: el Fuego. Para ellos habrá en él un hogar duradero como retribución por haber negado Nuestros Signos." WHERE ayatNumber = 29 AND chapterNumber = 41;
UPDATE verses SET es = "Los incrédulos dirán: “Señor nuestro, muéstranos a aquellos que nos extraviaron tanto de entre los Yinn como de entre los hombres, para que los pongamos bajo nuestros pies y ambos pasen a ser de los más viles”." WHERE ayatNumber = 30 AND chapterNumber = 41;
UPDATE verses SET es = "<em>En cuanto a </em>los que dicen: “Nuestro Señor es Al-lah” y permanecen después perseverantes, los ángeles descienden sobre ellos, <em>diciéndoles</em>: “No temáis ni os aflijáis; regocijaos en el Jardín que se os ha prometido”;" WHERE ayatNumber = 31 AND chapterNumber = 41;
UPDATE verses SET es = "<em>“</em>Somos vuestros amigos en esta vida y en el Más Allá. Allí poseeréis todo lo que deseen vuestras almas, y allí tendréis todo cuanto pidáis”," WHERE ayatNumber = 32 AND chapterNumber = 41;
UPDATE verses SET es = "<em>“</em>El agasajo del Sumo Indulgente, el Misericordioso”." WHERE ayatNumber = 33 AND chapterNumber = 41;
UPDATE verses SET es = "Mas ¿quién habla mejor que quien invita <em>a los hombres</em> a Al-lah, practica las buenas obras y dice: “Soy, en verdad, de quienes se someten?”." WHERE ayatNumber = 34 AND chapterNumber = 41;
UPDATE verses SET es = "Pues el bien y el mal no son equiparables. Rechaza<em> el mal</em> con lo que es mejor y observa como aquel entre cuya persona y tú existía la enemistad, se vuelve como si fuera un amigo entrañable." WHERE ayatNumber = 35 AND chapterNumber = 41;
UPDATE verses SET es = "Mas a nadie se le concede esto excepto a los perseverantes; y a adie se le concede esto salvo al que posee una parte importante <em> de excelsitud.</em>" WHERE ayatNumber = 36 AND chapterNumber = 41;
UPDATE verses SET es = "Si te tienta alguna incitación de Satanás, busca refugio en Al-lah. En verdad, Él es el Quien todo lo oye, el Omnisciente." WHERE ayatNumber = 37 AND chapterNumber = 41;
UPDATE verses SET es = "Entre Sus signos están la noche y el día, y el sol y la luna. No os postréis ante el sol ni ante la luna, sino ante Al-lah, Quien los ha creado, si es a Él a Quien <em>realmente</em> adoráis." WHERE ayatNumber = 38 AND chapterNumber = 41;
UPDATE verses SET es = "Pero si rehúsan con desdén, <em>lo hacen en perjuicio propio,</em> mientras que los que están con tu Señor lo glorifican día y noche, sin cansarse nunca." WHERE ayatNumber = 39 AND chapterNumber = 41;
UPDATE verses SET es = "Entre Sus signos está <em>éste:</em> que ves la tierra reseca, pero cuando le enviamos agua, se agita e hincha <em>con verdor.</em> En verdad, Quien le infundió vida puede resucitar a los muertos. Ciertamente Él tiene poder sobre todas las cosas." WHERE ayatNumber = 40 AND chapterNumber = 41;
UPDATE verses SET es = "En verdad quienes disputan respecto a Nuestros Signos no están ocultos para Nosotros. ¿Acaso quien es arrojado al Fuego es mejor que quien sale a salvo en el Día de la Resurrección? Haced lo que queráis. Ciertamente Él ve todo lo que hacéis." WHERE ayatNumber = 41 AND chapterNumber = 41;
UPDATE verses SET es = "Quien no creen en el Recordatorio cuando les llega <em>son los perdedores.</em> Pues ciertamente es un Libro poderoso." WHERE ayatNumber = 42 AND chapterNumber = 41;
UPDATE verses SET es = "La falsedad no puede acercarse a él <em>ni</em> por delante ni por detrás. <em>Es </em>una revelación del Sabio, el Merecedor de alabanza." WHERE ayatNumber = 43 AND chapterNumber = 41;
UPDATE verses SET es = "No se te dice sino lo que se dijo a los Mensajeros que te recedieron. Tu Señor es en verdad el Señor del perdón; y es <em>también</em> el Señor del castigo doloroso." WHERE ayatNumber = 44 AND chapterNumber = 41;
UPDATE verses SET es = "Y si hubiésemos hecho un Corán en idioma extranjero, ciertamente habrían dicho: “¿Por qué sus versículos no han sido hechos con claridad? ¡Cómo! ¿Una<em> escritura en</em> lengua extranjera y un profeta árabe?”. Diles: “Es una guía y curación para los creyentes”. Mas <em>en cuanto a quienes</em> no creen, hay sordera en sus oídos y es ceguera para ellos. Es, <em>por así decirlo, </em> como si estuvieran siendo llamados desde un lugar muy lejano." WHERE ayatNumber = 45 AND chapterNumber = 41;
UPDATE verses SET es = "Dimos a Moisés el Libro, pero crearon diferencias respecto a él; y e no haber sido por una palabra que fue revelada antes por tu Señor, <em>el asunto</em> habría quedado decidido entre ellos; pues en verdad se encuentran en una duda inquietante respecto a él." WHERE ayatNumber = 46 AND chapterNumber = 41;
UPDATE verses SET es = "Quien actúa rectamente, lo hace para su propia alma; y quien practica el mal, <em>sólo actuará</em> contra ella. Y tu Señor no es en absoluto injusto con <em>Sus</em> siervos." WHERE ayatNumber = 47 AND chapterNumber = 41;
UPDATE verses SET es = "A Él<em> sólo</em> concierne el conocimiento de la Hora. Ningún fruto sale de su envoltura, ni hembra alguna concibe <em>a un niño</em> , ni <em>lo</em> da a luz, si no es con Su conocimiento. Y pensad en el día en que les convocará, <em>diciéndoles: </em>“¿Dónde están los partícipes que Me atribuís?” Responderán: “Te aseguramos que no hay nadie de nosotros que sea testigo <em>de ello”.</em>" WHERE ayatNumber = 48 AND chapterNumber = 41;
UPDATE verses SET es = "Pues todo lo que solían invocar anteriormente desaparecerá para ellos, y se darán cuenta de que no tendrán escapatoria." WHERE ayatNumber = 49 AND chapterNumber = 41;
UPDATE verses SET es = "El hombre no se cansa de rogar por el bien; pero si el mal lo afecta, se desespera, <em>y </em>abandona<em> toda</em> esperanza." WHERE ayatNumber = 50 AND chapterNumber = 41;
UPDATE verses SET es = "Mas si le hacemos probar Nuestra misericordia, después de haber sufrido<em> alguna</em> aflicción, dirá ciertamente: “Esto es lo que me corresponde; no creo que llegue <em>nunca</em> la Hora. Pero si soy devuelto a mi Señor, ciertamente tendré con Él lo mejor de todo”. Entonces contaremos a los incrédulos todo lo que hicieron, y en verdad les haremos probar un duro castigo." WHERE ayatNumber = 51 AND chapterNumber = 41;
UPDATE verses SET es = "Cuando concedemos un favor al hombre, <em>lo</em> ignora y se aparta a un lado. Pero si el mal lo toca, ¡he ahí como se vuelve suplicante de largas plegarias!" WHERE ayatNumber = 52 AND chapterNumber = 41;
UPDATE verses SET es = "Diles: “Decidme: si procede de Al-lah pero no creéis en él...¿hay alguien más extraviado que quien se ha apartado <em>de Al-lah?</em>" WHERE ayatNumber = 53 AND chapterNumber = 41;
UPDATE verses SET es = "Pronto les mostraremos Nuestros signos en el horizonte y entre ellos mismos, hasta que les quede manifiesto que es la verdad. ¿No es suficiente que tu Señor sea testigo sobre todas las cosas?" WHERE ayatNumber = 54 AND chapterNumber = 41;
UPDATE verses SET es = "Sí, ciertamente tienen dudas sobre la reunión con su Señor. Sí, Él ciertamente abarca todas las cosas." WHERE ayatNumber = 55 AND chapterNumber = 41;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso" WHERE ayatNumber = 1 AND chapterNumber = 42;
UPDATE verses SET es = "Ha Mim." WHERE ayatNumber = 2 AND chapterNumber = 42;
UPDATE verses SET es = "‘Ain Sin Qaf." WHERE ayatNumber = 3 AND chapterNumber = 42;
UPDATE verses SET es = "De este modo, Al-lah el Poderoso, el Sabio, se revela a ti y Se <em>reveló</em> a quienes te precedieron." WHERE ayatNumber = 4 AND chapterNumber = 42;
UPDATE verses SET es = "A Él pertenece cuanto hay en los cielos y cuanto hay en la tierra, pues Él es el Altísimo, el Grande." WHERE ayatNumber = 5 AND chapterNumber = 42;
UPDATE verses SET es = "Los cielos podrían desgarrarse en pedazos en las alturas celestiales; mientras los ángeles glorifican a Su Señor con Sus alabanzas y piden perdón por quienes están en la tierra. ¡Mirad! Al-lah es ciertamente el Sumo Indulgente, el Misericordioso." WHERE ayatNumber = 6 AND chapterNumber = 42;
UPDATE verses SET es = "En cuanto a quienes toman <em>para ellos</em> protectores aparte de Él, Al-lah los vigila; y tú no eres guardián de ellos." WHERE ayatNumber = 7 AND chapterNumber = 42;
UPDATE verses SET es = "Así te hemos revelado el Corán en árabe, para que adviertas a la adre de las ciudades y a todas las que la rodean; y <em>para que les</em> adviertas del Día de la Reunión sobre el que no hay duda: Una parte morará en los jardines <em>del Paraíso</em>, y otra parte estará en el Fuego ardiente." WHERE ayatNumber = 8 AND chapterNumber = 42;
UPDATE verses SET es = "Mas si Al-lah <em>así</em> lo hubiese querido, los hubiese convertido en un pueblo; pero Él admite a Su Misericordia a quien Le agrada. <em>En cuanto a</em> los inicuos, no tendrán protector ni quien les ayude." WHERE ayatNumber = 9 AND chapterNumber = 42;
UPDATE verses SET es = "¿Han tomado <em>para ellos</em> protectores distintos de Él? Mas Al-lah es el <em>auténtico</em> Protector. Pues Él resucita a los muertos y tiene poder sobre todas las cosas." WHERE ayatNumber = 10 AND chapterNumber = 42;
UPDATE verses SET es = "Y sea cual fuere aquello en lo que discrepéis, su juicio <em>corresponde</em> a Al-lah. Diles: “Así es Al-lah, mi Señor; en Él pongo mi confianza, y a Él <em>siempre</em> vuelvo”." WHERE ayatNumber = 11 AND chapterNumber = 42;
UPDATE verses SET es = "<em>Él es</em> el Creador Perfecto de los cielos y la tierra. Ha creado parejas de entre vosotros y <em>también </em>parejas de entre el ganado para vuestro beneficio. Con ello os multiplica. No hay nada que se parezca a Él; Él es Quien todo lo oye, Quien todo lo ve." WHERE ayatNumber = 12 AND chapterNumber = 42;
UPDATE verses SET es = "A Él pertenecen las llaves de los cielos y la tierra. Él amplía la rovisión para quien Le agrada y la estrecha para <em>quien quiere.</em> En verdad, Él conoce perfectamente todas las cosas." WHERE ayatNumber = 13 AND chapterNumber = 42;
UPDATE verses SET es = "Él os ha prescrito la religión que impuso a Noé, y que te hemos revelado, y que impusimos a Abraham, Moisés y Jesús, <em>diciendo</em>: “Permaneced perseverantes en la obediencia y no os dividáis en ella. Es duro para los idólatras aquello a lo que les exhortáis. Al-lah elige ara Él a quien le agrada y guía hacia Sí mismo a quien vuelve <em>a Él”</em>." WHERE ayatNumber = 14 AND chapterNumber = 42;
UPDATE verses SET es = "Y sólo quedaron divididos y discreparon después de llegarles el conocimiento, por envidia mutua. Mas de no haber sido por una palabra ue había sido ya revelada por tu Señor para un plazo fijado, <em>el asunto</em> se habría decidido ciertamente entre ellos. Pues en verdad, quienes se hizo heredar el Libro después de ellos, se encuentran en una duda inquietante respecto al mismo." WHERE ayatNumber = 15 AND chapterNumber = 42;
UPDATE verses SET es = "Ante esto, pues, invita tú a la <em>humanidad.</em> Sé perseverante como se te ha ordenado, y no sigas sus perversas inclinaciones, sino diles: “Creo en cualquier Libro revelado por Al-lah y se me ha ordenado juzgar con justicia entre vosotros. Al-lah es nuestro Señor y vuestro Señor. Para nosotros es la recompensa de nuestras obras y para vosotros la recompensa de las vuestras. No hay disputa alguna entre nosotros y vosotros. Al-lah nos reunirá juntos y a Él será el retorno”." WHERE ayatNumber = 16 AND chapterNumber = 42;
UPDATE verses SET es = "Mas quienes disputan sobre Al-lah después de<em>haber sido testigos</em> de que sus oraciones ( <em>las del Santo Profeta)</em> han sido aceptadas no poseerán rgumento válido ante su Señor. Sobre ellos caerá la cólera <em> de Dios</em> y para ellos habrá un severo castigo." WHERE ayatNumber = 17 AND chapterNumber = 42;
UPDATE verses SET es = "Al-lah es Quien ha revelado el Libro con la verdad, y <em>también</em> la Balanza. ¿Qué te hará conocer que la Hora pueda estar muy cerca?" WHERE ayatNumber = 18 AND chapterNumber = 42;
UPDATE verses SET es = "Quienes no creen en ella intentan apresurarla; pero los que creen la temen, y saben que es la verdad. ¡Cuidado! Los que disputan en relación con la Hora están en un error profundo." WHERE ayatNumber = 19 AND chapterNumber = 42;
UPDATE verses SET es = "Al-lah es extremadamente Bondadoso con Sus siervos. Él provee a quien Le place. Él es el Poderoso, el Potente." WHERE ayatNumber = 20 AND chapterNumber = 42;
UPDATE verses SET es = "A quien desee la cosecha del Más Allá, le daremos su cosecha aumentada; y a quien desee la de este mundo, se <em>la </em>daremos, pero no tendrá parte en el Más Allá." WHERE ayatNumber = 21 AND chapterNumber = 42;
UPDATE verses SET es = "¿Tienen ellos partícipes <em>tales con Al-lah</em> que les han hecho lícito en la religión lo que Al-lah no ha permitido? Mas de no er por <em>Nuestra</em> palabra sobre el juicio final, el <em> asunto</em> <em>ya</em> se habría decidido entre ellos. En verdad, los inicuos tendrán un humillante castigo." WHERE ayatNumber = 22 AND chapterNumber = 42;
UPDATE verses SET es = "Verás a los inicuos temerosos en razón de lo que han merecido, y que es seguro que caerá sobre ellos. Pero quienes creen y hacen buenas obras estarán en las praderas de los Jardines. Tendrán con su Señor cuanto deseen. Ésa es la gran magnanimidad de <em>Dios.</em>" WHERE ayatNumber = 23 AND chapterNumber = 42;
UPDATE verses SET es = "Ésta es la buena nueva que Al-lah da a Sus siervos que creen y hacen buenas obras. Diles: “No os pido recompensa alguna por ello, sólo el afecto mostrado entre parientes y amigos. Y a quien haga un acto de bondad, le realzaremos más aún el atractivo de su bondad. En verdad Al-lah es el Sumo Indulgente, Sumo Reconocedor." WHERE ayatNumber = 24 AND chapterNumber = 42;
UPDATE verses SET es = "¿Acaso dicen: “Ha inventado una mentira contra Al-lah?”. Si Al-lah <em>así</em> lo hubiese querido, podría haber sellado tu corazón. Por el contrario, Al-lah hace desaparecer la falsedad y establece la verdad con Sus Mandamientos. En verdad Él sabe muy bien lo que hay en los corazones." WHERE ayatNumber = 25 AND chapterNumber = 42;
UPDATE verses SET es = "Él es Quien acepta el arrepentimiento de Sus siervos y perdona los pecados. Él sabe lo que hacéis." WHERE ayatNumber = 26 AND chapterNumber = 42;
UPDATE verses SET es = "Él acepta<em> las oraciones</em> de quienes creen y practican las buenas obras y les da más de Su Gracia; <em>en cuanto a </em>los incrédulos, tendrán un severo castigo." WHERE ayatNumber = 27 AND chapterNumber = 42;
UPDATE verses SET es = "Pues si Al-lah ampliara la provisión para Sus siervos, se ebelarían en la tierra; mas Él envía de acuerdo con una medida <em>apropiada</em> según Le place. En verdad, Él es Consciente de todo y Quien todo lo ve respecto a Sus siervos." WHERE ayatNumber = 28 AND chapterNumber = 42;
UPDATE verses SET es = "Él es Quien envía la lluvia después de que se han desesperado y distribuye Su misericordia. Pues Él es el Protector, Merecedor de Alabanzas." WHERE ayatNumber = 29 AND chapterNumber = 42;
UPDATE verses SET es = "Entre Sus Signos está la creación de los cielos y la tierra y <em> de</em> todas las criaturas vivas que ha distribuido en ambos. Él tiene el poder de reunirlos cuando Le agrada." WHERE ayatNumber = 30 AND chapterNumber = 42;
UPDATE verses SET es = "Cualquier infortunio que os suceda, se debe a lo que vuestras ropias manos han forjado. Mas Él perdona muchos <em>de vuestros pecados.</em>" WHERE ayatNumber = 31 AND chapterNumber = 42;
UPDATE verses SET es = "Y no podéis frustrar los planes<em> de Dios</em> en la tierra, ni tenéis amigo ni ayudante alguno aparte de Al-lah." WHERE ayatNumber = 32 AND chapterNumber = 42;
UPDATE verses SET es = "Entre Sus Signos están los navíos en el mar, como cimas de montaña." WHERE ayatNumber = 33 AND chapterNumber = 42;
UPDATE verses SET es = "Si <em>así</em> lo quisiera podría hacer que el viento amainara de manera que quedaran sin movimiento en su superficie. Aquí hay en verdad Signos para cualquier persona que sea paciente y agradecida." WHERE ayatNumber = 34 AND chapterNumber = 42;
UPDATE verses SET es = "O puede destruirlos por lo que ellos (los hombres) han merecido -pero Él perdona muchos de <em>sus pecados -.</em>" WHERE ayatNumber = 35 AND chapterNumber = 42;
UPDATE verses SET es = "Y<em> los destruye de manera que</em> quienes disputan sobre los Signos de Al-lah sepan <em>que</em> no tienen refugio." WHERE ayatNumber = 36 AND chapterNumber = 42;
UPDATE verses SET es = "Cuanto se os ha concedido no es más que una provisión temporal de esta vida, pero lo que está con Al-lah es mejor y más duradero para quienes creen y ponen su confianza en su Señor;" WHERE ayatNumber = 37 AND chapterNumber = 42;
UPDATE verses SET es = "Quienes se abstienen de los pecados y obscenidades más graves y, cuando están encolerizados, perdonan," WHERE ayatNumber = 38 AND chapterNumber = 42;
UPDATE verses SET es = "Quienes escuchan a su Señor y cumplen la Oración; cuyos asuntos se <em> deciden</em> por consulta mutua, y emplean de lo que les hemos proporcionado," WHERE ayatNumber = 39 AND chapterNumber = 42;
UPDATE verses SET es = "Y quienes se defienden cuando se les perjudica." WHERE ayatNumber = 40 AND chapterNumber = 42;
UPDATE verses SET es = "La recompensa de un daño es un daño similar; mas quien perdona y <em>su acto</em> produce la reforma, tendrá su recompensa con Al-lah. En verdad, Él no ama a los inicuos." WHERE ayatNumber = 41 AND chapterNumber = 42;
UPDATE verses SET es = "Mas no hay culpa en quienes se defienden después de haber sido oprimidos." WHERE ayatNumber = 42 AND chapterNumber = 42;
UPDATE verses SET es = "Sólo son culpables quienes perjudican a los hombres y cometen transgresión en la tierra sin justificación. Éstos tendrán un castigo doloroso." WHERE ayatNumber = 43 AND chapterNumber = 42;
UPDATE verses SET es = "Mas quien es paciente y perdona: aquí hay en verdad un asunto de enérgica determinación." WHERE ayatNumber = 44 AND chapterNumber = 42;
UPDATE verses SET es = "A quien Al-lah decreta extraviado, <em>sepa que</em> no tendrá después protector. Pero encontrarás que los injustos, cuando ven el castigo, dicen: “¿Hay algún camino de retorno?”." WHERE ayatNumber = 45 AND chapterNumber = 42;
UPDATE verses SET es = "Y los verás conducidos ante él (el Fuego), bajando <em> sus ojos</em> en razón de su humillación y observándo<em>lo</em> con una mirada furtiva. Mas quienes creen dirán: “Los perdedores son en verdad quienes se arruinan a sí mismos y a sus familias en el Día de la Resurre­cción. “¡Mirad! Los injustos habrán<em> de permanecer</em> en un castigo duradero”." WHERE ayatNumber = 46 AND chapterNumber = 42;
UPDATE verses SET es = "Y no tienen a nadie que les ayude aparte de Al-lah. Y para quien l-lah decreta que se ha extraviado, no hay camino <em> en absoluto.</em>" WHERE ayatNumber = 47 AND chapterNumber = 42;
UPDATE verses SET es = "Escuchad a vuestro Señor antes de que llegue el día en el que no habrá escapatoria contra <em>el decreto de</em> Al-lah. En ese día no habrá refugio para vosotros, ni tendréis <em>posibilidad</em> alguna de negar." WHERE ayatNumber = 48 AND chapterNumber = 42;
UPDATE verses SET es = "Mas si se apartan, no te hemos enviado como guardián de ellos. Tu deber es únicamente el de transmitir <em>el Mensaje</em>. En verdad cuanto, cuando hacemos que el hombre pruebe Nuestra misericordia, se alegra con ella. Pero si les sobreviene el mal por lo que cometieron sus manos ¡he ahí como el hombre se vuelve desagradecido!" WHERE ayatNumber = 49 AND chapterNumber = 42;
UPDATE verses SET es = "A Al-lah pertenece el reino de los cielos y la tierra. Él crea lo que le place. Concede hijas a quien quiere y concede hijos a quien le agrada;" WHERE ayatNumber = 50 AND chapterNumber = 42;
UPDATE verses SET es = "O bien, Él los mezcla, varones y hembras; y Él hace estéril a quien le place. En verdad, Él es Omnisciente, Poderoso." WHERE ayatNumber = 51 AND chapterNumber = 42;
UPDATE verses SET es = "Y no es propio para el hombre que Al-lah le hable salvo por revelación o tras un velo, o enviándole un mensajero para que le revele con Su mandamiento lo que desee. En verdad, Él es Altísimo, Sabio." WHERE ayatNumber = 52 AND chapterNumber = 42;
UPDATE verses SET es = "Así te hemos revelado una gran revelación por Nuestro mandamiento. No sabías lo que era el Libro ni qué era la fe. Pero la hicimos (a la revelación) <em>ser</em> una luz con la que guiamos a quien Nos place de Nuestros siervos. En verdad tú guías <em>a los hombres</em> al camino recto," WHERE ayatNumber = 53 AND chapterNumber = 42;
UPDATE verses SET es = "El camino de Al-lah, a Quien pertenece cuanto hay en los cielos y cuanto hay en la tierra. ¡Mirad! A Al-lah retornan todas las cosas." WHERE ayatNumber = 54 AND chapterNumber = 42;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 43;
UPDATE verses SET es = "Ha Mim." WHERE ayatNumber = 2 AND chapterNumber = 43;
UPDATE verses SET es = "Por medio de este Libro lúcido," WHERE ayatNumber = 3 AND chapterNumber = 43;
UPDATE verses SET es = "Hemos hecho un Corán en idioma claro y elocuente para que entendáis." WHERE ayatNumber = 4 AND chapterNumber = 43;
UPDATE verses SET es = "En verdad está <em>a salvo</em> con Nosotros en la Madre del Libro, exaltado, lleno de sabiduría." WHERE ayatNumber = 5 AND chapterNumber = 43;
UPDATE verses SET es = "¿Acaso hemos de retiraros el Recordatorio, olvidándoos por ser un pueblo extravagante?" WHERE ayatNumber = 6 AND chapterNumber = 43;
UPDATE verses SET es = "¡Cuántos profetas enviamos entre los pueblos antiguos!" WHERE ayatNumber = 7 AND chapterNumber = 43;
UPDATE verses SET es = "Mas nunca les llegó un profeta sin que se burlaran de él." WHERE ayatNumber = 8 AND chapterNumber = 43;
UPDATE verses SET es = "Destruimos <em>a quienes</em> eran más poderosos en el control que éstos, y el ejemplo de los pueblos antiguos ha quedado patente." WHERE ayatNumber = 9 AND chapterNumber = 43;
UPDATE verses SET es = "Mas si les preguntas: “¿Quién creó los cielos y la tierra?” responderán ciertamente: “El <em>Dios</em> Poderoso, el Omnisciente los creó”." WHERE ayatNumber = 10 AND chapterNumber = 43;
UPDATE verses SET es = "<em>Aquel</em> que ha hecho para vosotros de la tierra una cuna, y ha creado los caminos en ella para que podáis seguir el camino recto;" WHERE ayatNumber = 11 AND chapterNumber = 43;
UPDATE verses SET es = "Quien envía el agua desde el cielo en la medida justa, dando así nueva vida a una tierra muerta –así seréis resucitados-." WHERE ayatNumber = 12 AND chapterNumber = 43;
UPDATE verses SET es = "Ha creado todo en parejas y creó para vosotros los buques y el ganado en el que podéis montar," WHERE ayatNumber = 13 AND chapterNumber = 43;
UPDATE verses SET es = "Para que os asentéis firmemente en sus lomos y entonces, una vez firmemente sentados en ellos, recordéis el favor de vuestro Señor y digáis: “Santo es Quien nos sometió todo esto, ya que nosotros no podríamos arrearlos por nuestra cuenta”." WHERE ayatNumber = 14 AND chapterNumber = 43;
UPDATE verses SET es = "“A nuestro Señor ciertamente retornaremos”." WHERE ayatNumber = 15 AND chapterNumber = 43;
UPDATE verses SET es = "<em>Sin embargo</em> afirman que algunos de Sus siervos son parte de El . En verdad, el hombre es claramente desagradecido." WHERE ayatNumber = 16 AND chapterNumber = 43;
UPDATE verses SET es = "¿Acaso Él ha tomado hijas de lo que ha creado y os ha honrado con hijos?" WHERE ayatNumber = 17 AND chapterNumber = 43;
UPDATE verses SET es = "No obstante, cuando a uno de ellos se le comunica que ha recibido aquello cuya semejanza atribuye al <em>Dios</em> Clemente, su rostro se ensombrece y se sofoca con una angustia interior." WHERE ayatNumber = 18 AND chapterNumber = 43;
UPDATE verses SET es = "<em>¿Atribuís acaso a Dios</em> alguien criado entre ornamentos, que no es claro en la discusión?" WHERE ayatNumber = 19 AND chapterNumber = 43;
UPDATE verses SET es = "Y describen a los ángeles, que son siervos del <em>Dios</em> Clemente, como hembras. ¿Fueron acaso testigos de su creación? Entonces será registrado su testimonio y serán interrogados." WHERE ayatNumber = 20 AND chapterNumber = 43;
UPDATE verses SET es = "Mas dicen: “Si el <em>Dios </em>Clemente <em>así</em> lo hubiese querido, no los habríamos adorado”. No tienen conocimiento alguno de ello. No hacen más que conjeturas." WHERE ayatNumber = 21 AND chapterNumber = 43;
UPDATE verses SET es = "¿Les hemos dado acaso una Escritura antes de ésta, para que se aferren a ella?" WHERE ayatNumber = 22 AND chapterNumber = 43;
UPDATE verses SET es = "No. Dicen: “Hallamos que nuestros antecesores seguían un <em>cierto</em> camino, y nos guiamos por sus pasos”." WHERE ayatNumber = 23 AND chapterNumber = 43;
UPDATE verses SET es = "Y así <em>ha ocurrido siempre</em> que nunca hemos enviado Amonestador alguno antes de ti a ninguna ciudad sin que sus moradores rósperos dijeran: “Encontramos a nuestros antecesores siguiendo un <em>determinado</em> camino, y seguimos sus pasos”." WHERE ayatNumber = 24 AND chapterNumber = 43;
UPDATE verses SET es = "<em>Su Amonestador</em> dijo: “¡Cómo! ¿Aunque os traiga una guía mejor de la que encontráis que seguían vuestros padres?”. Respondieron: “Ciertamente no creemos en aquello con lo que has sido enviado”." WHERE ayatNumber = 25 AND chapterNumber = 43;
UPDATE verses SET es = "Por ello les exigimos retribución. ¡Mirad, pues, cuál fue el fin de quienes rechazaron <em>a los profetas!</em>" WHERE ayatNumber = 26 AND chapterNumber = 43;
UPDATE verses SET es = "<em>Acordaos</em> <em>de</em> cuando Abraham dijo a su padre y a su pueblo: “Rechazo <em>categóricamente</em> lo que adoráis," WHERE ayatNumber = 27 AND chapterNumber = 43;
UPDATE verses SET es = "“Excepto a Aquel que me creó, pues Él ciertamente me guiará”." WHERE ayatNumber = 28 AND chapterNumber = 43;
UPDATE verses SET es = "Y lo convirtió en palabra que perduró entre su posteridad, para que volvieran <em>a Dios.</em>" WHERE ayatNumber = 29 AND chapterNumber = 43;
UPDATE verses SET es = "No, pero les permití, a ellos y a sus padres, un gozo <em> temporal</em> hasta que les llegó la verdad y un Mensajero que expone bien claro <em>su Mensaje.</em>" WHERE ayatNumber = 30 AND chapterNumber = 43;
UPDATE verses SET es = "Mas cuando les llegó la verdad, dijeron: “Esto es magia y ciertamente la rechazamos”." WHERE ayatNumber = 31 AND chapterNumber = 43;
UPDATE verses SET es = "Dicen: “¿Por qué este Corán no ha sido enviado a algún hombre grande de las dos ciudades?”." WHERE ayatNumber = 32 AND chapterNumber = 43;
UPDATE verses SET es = "¿Son acaso ellos quienes distribuyen las mercedes de tu Señor? Somos Nosotros quienes distribuimos entre ellos su sustento en la existencia presente y exaltamos a algunos de ellos sobre otros en grados <em>de importancia,</em> aunque ¡ay! sólo para que algunos subyuguen a los demás. Mas la misericordia de tu Señor es mejor que cuanto atesoran." WHERE ayatNumber = 33 AND chapterNumber = 43;
UPDATE verses SET es = "De no ser porque la humanidad se habría convertido en un solo<em>tipo de</em> personas, habríamos concedido a quienes no creen en el<em>Dios</em> Clemente, techos de plata para sus casas y escaleras <em>de plata</em> por las que pudieran ascender;" WHERE ayatNumber = 34 AND chapterNumber = 43;
UPDATE verses SET es = "Puertas<em> de plata</em> para sus casas y lechos <em>de plata</em> en los que se reclinasen," WHERE ayatNumber = 35 AND chapterNumber = 43;
UPDATE verses SET es = "Y <em>otros objetos</em> de ornamentación. Pero eso no es nada mas ue una provisión <em>temporal</em> de la vida presente. Y el <em>bienestar</em> del Más Allá con tu Señor es para los justos." WHERE ayatNumber = 36 AND chapterNumber = 43;
UPDATE verses SET es = "A quien se aparta del recuerdo del <em>Dios</em> Clemente, le asignamos un demonio, que se convierte en su compañero." WHERE ayatNumber = 37 AND chapterNumber = 43;
UPDATE verses SET es = "En verdad, les apartan del camino <em>de Dios,</em> pero piensan que están rectamente guiados;" WHERE ayatNumber = 38 AND chapterNumber = 43;
UPDATE verses SET es = "Hasta que, al llegar uno de ellos a Nosotros, dice <em> a su compañero:</em> “¡Ojalá que tú y yo estuviéramos distanciados en las antípodas!”. ¡Qué mal compañero es!" WHERE ayatNumber = 39 AND chapterNumber = 43;
UPDATE verses SET es = "“Habiendo transgredido como lo habéis hecho, vuestra unión en el castigo no os servirá de nada en este día ”." WHERE ayatNumber = 40 AND chapterNumber = 43;
UPDATE verses SET es = "¿Puedes, pues, hacer que el sordo oiga o guiar al ciego y a quien está en error evidente?" WHERE ayatNumber = 41 AND chapterNumber = 43;
UPDATE verses SET es = "Mas si te lleváramos, les exigiremos ciertamente su retribución," WHERE ayatNumber = 42 AND chapterNumber = 43;
UPDATE verses SET es = "O te mostraremos lo que les hemos prometido; pues en verdad tenemos poder completo sobre ellos." WHERE ayatNumber = 43 AND chapterNumber = 43;
UPDATE verses SET es = "Persevera, pues, en lo que te ha sido revelado; pues estás en el camino recto." WHERE ayatNumber = 44 AND chapterNumber = 43;
UPDATE verses SET es = "Pues, en verdad, es un recordatorio para ti y para tu pueblo; y seréis llamados a dar cuenta." WHERE ayatNumber = 45 AND chapterNumber = 43;
UPDATE verses SET es = "Pregunta a aquellos de Nuestros Mensajeros a quienes enviamos antes que a ti: “¿Nombramos acaso a deidad alguna aparte del<em> Dios </em> Clemente para ser adorada?”." WHERE ayatNumber = 46 AND chapterNumber = 43;
UPDATE verses SET es = "Y enviamos a Moisés con Nuestros Signos al Faraón y a sus jefes, y dijo: “En verdad soy Mensajero del Señor de los mundos”." WHERE ayatNumber = 47 AND chapterNumber = 43;
UPDATE verses SET es = "Pero cuando llegó a ellos con Nuestros Signos, ¡he aquí que se rieron de ellos!" WHERE ayatNumber = 48 AND chapterNumber = 43;
UPDATE verses SET es = "No les mostramos ningún Signo que no fuese mayor que el que lo<em>precedió, </em>y les enviamos el castigo, para que volvieran <em>a Nosotros.</em>" WHERE ayatNumber = 49 AND chapterNumber = 43;
UPDATE verses SET es = "Mas respondieron: “¡Mago! Ruega por nosotros, a tu Señor, según el pacto que ha establecido contigo; entonces ciertamente aceptaremos la guía”." WHERE ayatNumber = 50 AND chapterNumber = 43;
UPDATE verses SET es = "Pero cuando les retiramos el castigo ¡he aquí que rompieron su palabra!" WHERE ayatNumber = 51 AND chapterNumber = 43;
UPDATE verses SET es = "El Faraón publicó una proclama entre su pueblo, <em>diciendo</em>: “¡Pueblo mío! ¿Acaso no me pertenecen el reino de Egipto y estos ríos que corren a mis pies? ¿Es que no lo veis?" WHERE ayatNumber = 52 AND chapterNumber = 43;
UPDATE verses SET es = "“No. Soy mejor que este <em>ser</em> despreciable que apenas puede expresarse con claridad”." WHERE ayatNumber = 53 AND chapterNumber = 43;
UPDATE verses SET es = "“¿Por qué no se le han concedido pulseras de oro, o ángeles que lo acompañen en filas apretadas?”." WHERE ayatNumber = 54 AND chapterNumber = 43;
UPDATE verses SET es = "Así encendió a su pueblo y ellos le obedecieron. Ciertamente eran un pueblo malvado." WHERE ayatNumber = 55 AND chapterNumber = 43;
UPDATE verses SET es = "De ese modo, cuando despertaron Nuestra cólera, les impusimos su justa retribución y les ahogamos a todos." WHERE ayatNumber = 56 AND chapterNumber = 43;
UPDATE verses SET es = "Y los convertimos en precedente y ejemplo para las generaciones futuras." WHERE ayatNumber = 57 AND chapterNumber = 43;
UPDATE verses SET es = "Cuando el hijo de María es mencionado como parábola, he ahí que tu pueblo levanta un clamor por ello;" WHERE ayatNumber = 58 AND chapterNumber = 43;
UPDATE verses SET es = "Dicen: “¿Quiénes son mejores, nuestros dioses o él?”. No te mencionan este <em>tema</em> salvo para discutir. No, no son más que un pueblo pendenciero”." WHERE ayatNumber = 59 AND chapterNumber = 43;
UPDATE verses SET es = "Él no fue más que Nuestro siervo a quien otorgamos Nuestro favor, y le convertimos en ejemplo para los hijos de Israel." WHERE ayatNumber = 60 AND chapterNumber = 43;
UPDATE verses SET es = "Mas si <em>lo</em> hubiésemos querido, habríamos creado de entre vosotros ángeles en la tierra para ser los sucesores<em> en ella</em>." WHERE ayatNumber = 61 AND chapterNumber = 43;
UPDATE verses SET es = "Mas en verdad él es un signo de la Hora. No dudéis, pues, de ello, sino seguidme. Éste es el camino recto." WHERE ayatNumber = 62 AND chapterNumber = 43;
UPDATE verses SET es = "No dejéis que Satanás os estorbe. En verdad, él es vuestro enemigo declarado." WHERE ayatNumber = 63 AND chapterNumber = 43;
UPDATE verses SET es = "Y cuando Jesús vino con pruebas claras, dijo: “En verdad vengo a vosotros con la sabiduría y para aclararos algo de aquello sobre lo que disputáis. Temed, pues, a Al-lah y obedecedme." WHERE ayatNumber = 64 AND chapterNumber = 43;
UPDATE verses SET es = "“En verdad, Al-lah es mi Señor y vuestro Señor. Adoradle pues. Éste es el camino recto”." WHERE ayatNumber = 65 AND chapterNumber = 43;
UPDATE verses SET es = "Mas los grupos discutieron entre sí. ¡Ay de los inicuos por el castigo del día doloroso!" WHERE ayatNumber = 66 AND chapterNumber = 43;
UPDATE verses SET es = "Sólo esperan que la Hora llegue de improviso sobre ellos, cuando menos la perciban." WHERE ayatNumber = 67 AND chapterNumber = 43;
UPDATE verses SET es = "En ese día, los amigos se volverán enemigos, salvo los justos." WHERE ayatNumber = 68 AND chapterNumber = 43;
UPDATE verses SET es = "“¡Siervos míos! No habrá temor para vosotros en ese día, ni os afligiréis”;" WHERE ayatNumber = 69 AND chapterNumber = 43;
UPDATE verses SET es = "“Los que creísteis en Nuestros Signos y os sometisteis”." WHERE ayatNumber = 70 AND chapterNumber = 43;
UPDATE verses SET es = "“Entrad en el jardín, vosotros y vuestras esposas seréis enaltecidos y se os hará felices”." WHERE ayatNumber = 71 AND chapterNumber = 43;
UPDATE verses SET es = "Se harán pasar entre ellos fuentes y copas de oro, y allí habrá todo lo que desean las almas y <em>en lo</em> que se deleitan los ojos. “Y allí moraréis”." WHERE ayatNumber = 72 AND chapterNumber = 43;
UPDATE verses SET es = "“Éste es el Jardín del que habéis sido nombrados herederos por vuestras acciones”." WHERE ayatNumber = 73 AND chapterNumber = 43;
UPDATE verses SET es = "“En él hay para vosotros frutas en abundancia de las que comeréis”." WHERE ayatNumber = 74 AND chapterNumber = 43;
UPDATE verses SET es = "Los culpables morarán ciertamente en el castigo del Infierno." WHERE ayatNumber = 75 AND chapterNumber = 43;
UPDATE verses SET es = "No les será suavizado, y sobre ellos caerá la desesperación." WHERE ayatNumber = 76 AND chapterNumber = 43;
UPDATE verses SET es = "No fuimos Nosotros Quienes los perjudicamos, sino que ellos mismos fueron los inicuos." WHERE ayatNumber = 77 AND chapterNumber = 43;
UPDATE verses SET es = "Y gritarán; “¡Oh custodio del Infierno! Haz que tu Señor nos haga perecer”. Dirá: “Debéis permanecer”." WHERE ayatNumber = 78 AND chapterNumber = 43;
UPDATE verses SET es = "<em>Dios dirá:</em> “Ciertamente os trajimos la verdad; pero la mayoría de vosotros fuisteis adversos a la verdad”." WHERE ayatNumber = 79 AND chapterNumber = 43;
UPDATE verses SET es = "¿Se han decidido a tomar un camino? Pues Nosotros <em>también</em> lo hemos decidido." WHERE ayatNumber = 80 AND chapterNumber = 43;
UPDATE verses SET es = "¿Piensan acaso que no oímos sus secretos y conspiraciones? ¡Sí! Y Nuestros mensajeros permanecen con ellos registrándolo <em>todo.</em>" WHERE ayatNumber = 81 AND chapterNumber = 43;
UPDATE verses SET es = "Diles: “Si hubiese existido un hijo del <em>Dios</em> Clemente, yo habría sido el primero en adorarlo”." WHERE ayatNumber = 82 AND chapterNumber = 43;
UPDATE verses SET es = "Santo es <em>Al-lah</em>, el Señor de los cielos y de la tierra, el Señor del Trono, <em>pues Él es libre</em> de todo lo que <em>Le</em> atribuyen." WHERE ayatNumber = 83 AND chapterNumber = 43;
UPDATE verses SET es = "Dejadlos, pues, solos, dedicados a sus frívolas conversaciones y divirtiéndose hasta que se encuentren con ese Día suyo que les ha sido prometido." WHERE ayatNumber = 84 AND chapterNumber = 43;
UPDATE verses SET es = "Él es Quien es Dios en el cielo y Dios en la tierra; pues Él es el Sabio, el Omnisciente." WHERE ayatNumber = 85 AND chapterNumber = 43;
UPDATE verses SET es = "Bendito sea Aquel a Quien pertenece el reino de los cielos y de la tierra y todo cuanto hay entre ellos, pues en Él está el conocimiento de la Hora y a Él seréis devueltos." WHERE ayatNumber = 86 AND chapterNumber = 43;
UPDATE verses SET es = "Mas aquellos a quienes invocan aparte de Él no poseen poder de intercesión salvo el que es testigo de la verdad; y ellos <em>lo </em> conocen." WHERE ayatNumber = 87 AND chapterNumber = 43;
UPDATE verses SET es = "Si les preguntáis: “¿Quién los creó?”, responderán ciertamente: “Al-lah”. ¿Cómo es entonces que se están apartando?" WHERE ayatNumber = 88 AND chapterNumber = 43;
UPDATE verses SET es = "<em>Juro</em> por el clamor <em>repetido</em>, “¡Oh mi Señor!” que son un pueblo que no creerán." WHERE ayatNumber = 89 AND chapterNumber = 43;
UPDATE verses SET es = "Apártate, pues, de ellos y diles: “Paz”; pues pronto sabrán." WHERE ayatNumber = 90 AND chapterNumber = 43;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 44;
UPDATE verses SET es = "Ha Mim." WHERE ayatNumber = 2 AND chapterNumber = 44;
UPDATE verses SET es = "Por este Libro lúcido." WHERE ayatNumber = 3 AND chapterNumber = 44;
UPDATE verses SET es = "En verdad lo revelamos en una Noche bendita. Ciertamente hemos estado <em>siempre</em> advirtiendo <em>contra el mal.</em>" WHERE ayatNumber = 4 AND chapterNumber = 44;
UPDATE verses SET es = "En él se han aclarado todos los mandamientos sabios." WHERE ayatNumber = 5 AND chapterNumber = 44;
UPDATE verses SET es = "<em>Por</em> Nuestro <em>propio</em> mandato. En verdad, hemos estado siempre enviando <em>Mensajeros,</em>" WHERE ayatNumber = 6 AND chapterNumber = 44;
UPDATE verses SET es = "Como misericordia de tu Señor. Ciertamente, Él es Quien todo lo oye, el Omnisciente." WHERE ayatNumber = 7 AND chapterNumber = 44;
UPDATE verses SET es = "El Señor de los cielos y la tierra y de todo lo que hay entre ellos, si hubieseis creído." WHERE ayatNumber = 8 AND chapterNumber = 44;
UPDATE verses SET es = "No hay más Dios que Él. Él da la vida y causa la muerte. Él es vuestro Señor y el Señor de vuestros antepasados." WHERE ayatNumber = 9 AND chapterNumber = 44;
UPDATE verses SET es = "Sin embargo, juegan en la duda." WHERE ayatNumber = 10 AND chapterNumber = 44;
UPDATE verses SET es = "Mas tu vigila <em>esperando</em> el día en que el cielo arroje una humareda visible," WHERE ayatNumber = 11 AND chapterNumber = 44;
UPDATE verses SET es = "Que lanzará sombras sobre todos los hombres. Será un doloroso castigo." WHERE ayatNumber = 12 AND chapterNumber = 44;
UPDATE verses SET es = "<em>Entonces los hombres gritarán:</em> “Señor nuestro, retira de nosotros el tormento; en verdad, somos creyentes”." WHERE ayatNumber = 13 AND chapterNumber = 44;
UPDATE verses SET es = "<em>¿</em>Cómo podrán beneficiarse de la advertencia, si ya les ha llegado un Mensajero, que explica las cosas con claridad?," WHERE ayatNumber = 14 AND chapterNumber = 44;
UPDATE verses SET es = "Y sin embargo se apartaron de él diciendo: “Está aleccionado, ¿es un hombre poseído?”." WHERE ayatNumber = 15 AND chapterNumber = 44;
UPDATE verses SET es = "Nosotros retiraremos el castigo durante un breve plazo, <em>pero</em> volveréis ciertamente <em>a la incredulidad.</em>" WHERE ayatNumber = 16 AND chapterNumber = 44;
UPDATE verses SET es = "En el día en que caeremos <em>sobre vosotros</em> con poderosa determinación, entonces ciertamente exigiremos la retribución." WHERE ayatNumber = 17 AND chapterNumber = 44;
UPDATE verses SET es = "Antes de ellos sometimos a prueba al pueblo del Faraón, y les llegó un noble Mensajero." WHERE ayatNumber = 18 AND chapterNumber = 44;
UPDATE verses SET es = "<em>Diciendo</em>: “Entregadme a los siervos de Al-lah. En verdad soy un Mensajero para vosotros, sincero <em>con lo que se me</em> ha confiado”;" WHERE ayatNumber = 19 AND chapterNumber = 44;
UPDATE verses SET es = "<em>“</em>No os exaltéis desafiando a Al-lah. Ciertamente vengo a vosotros con una autoridad evidente”." WHERE ayatNumber = 20 AND chapterNumber = 44;
UPDATE verses SET es = "<em>“</em>Y busco refugio en mi Señor y vuestro Señor, no sea que me lapidéis”." WHERE ayatNumber = 21 AND chapterNumber = 44;
UPDATE verses SET es = "<em>“</em>Y si no me creéis, manteneos apartados de mí”." WHERE ayatNumber = 22 AND chapterNumber = 44;
UPDATE verses SET es = "Entonces suplicó a su Señor <em>diciendo</em>: “Son, en verdad, un pueblo pecador”." WHERE ayatNumber = 23 AND chapterNumber = 44;
UPDATE verses SET es = "<em>Dios dijo:</em> “Llévate a mis siervos por la noche; pues ciertamente seréis perseguidos”." WHERE ayatNumber = 24 AND chapterNumber = 44;
UPDATE verses SET es = "<em>“</em>Y abandona el mar <em>en un momento en el que esté bajo y</em> en calma. En verdad, es un ejército destinado a ser ahogado”." WHERE ayatNumber = 25 AND chapterNumber = 44;
UPDATE verses SET es = "¡Cuántos jardines y manantiales dejaron detrás!" WHERE ayatNumber = 26 AND chapterNumber = 44;
UPDATE verses SET es = "¡Los campos de trigo y los nobles lugares!" WHERE ayatNumber = 27 AND chapterNumber = 44;
UPDATE verses SET es = "¡Y las comodidades en las que se complacían!" WHERE ayatNumber = 28 AND chapterNumber = 44;
UPDATE verses SET es = "Así <em>estaba destinado a ser.</em> E hicimos que otro pueblo heredara estas cosas." WHERE ayatNumber = 29 AND chapterNumber = 44;
UPDATE verses SET es = "Los cielos y la tierra no lloraron por ellos, si se les concedió respiro." WHERE ayatNumber = 30 AND chapterNumber = 44;
UPDATE verses SET es = "Y libramos a los hijos de Israel del suplicio humillante" WHERE ayatNumber = 31 AND chapterNumber = 44;
UPDATE verses SET es = "<em>Infligido</em> por el Faraón; fue en verdad arrogante y de los extraviados." WHERE ayatNumber = 32 AND chapterNumber = 44;
UPDATE verses SET es = "Los elegimos conociendo sus méritos, por encima de otros pueblos <em> de su época.</em>" WHERE ayatNumber = 33 AND chapterNumber = 44;
UPDATE verses SET es = "Y les dimos algunos Signos en los que había una prueba clara." WHERE ayatNumber = 34 AND chapterNumber = 44;
UPDATE verses SET es = "Esta <em>gente</em> dice:" WHERE ayatNumber = 35 AND chapterNumber = 44;
UPDATE verses SET es = "“Es sólo nuestra primera <em>y única</em> muerte, <em>después de la cual no hay vida</em> y no seremos resucitados." WHERE ayatNumber = 36 AND chapterNumber = 44;
UPDATE verses SET es = "Devolvednos, pues, a nuestros padres, si decís la verdad”." WHERE ayatNumber = 37 AND chapterNumber = 44;
UPDATE verses SET es = "¿Acaso son mejores que el pueblo de Tubba y que los que les precedieron? Los destruimos porque eran pecadores." WHERE ayatNumber = 38 AND chapterNumber = 44;
UPDATE verses SET es = "No creamos los cielos y la tierra y todo lo que hay entre ellos por diversión." WHERE ayatNumber = 39 AND chapterNumber = 44;
UPDATE verses SET es = "No los creamos sino con los requisitos de la verdad <em> y la justicia</em>, pero la mayoría de ellos no lo entienden." WHERE ayatNumber = 40 AND chapterNumber = 44;
UPDATE verses SET es = "En verdad, el Día de la Decisión es el día fijado para todos ellos." WHERE ayatNumber = 41 AND chapterNumber = 44;
UPDATE verses SET es = "El Día en que un amigo no servirá en absoluto a su amigo, ni serán ayudados." WHERE ayatNumber = 42 AND chapterNumber = 44;
UPDATE verses SET es = "Excepto aquellos a quienes Al-lah muestre misericordia. En verdad, Él es el Poderoso, el Misericordioso." WHERE ayatNumber = 43 AND chapterNumber = 44;
UPDATE verses SET es = "Ciertamente, el árbol de Zaqqum." WHERE ayatNumber = 44 AND chapterNumber = 44;
UPDATE verses SET es = "Será el alimento del pecador," WHERE ayatNumber = 45 AND chapterNumber = 44;
UPDATE verses SET es = "Como cobre fundido, hervirá en <em>sus </em>entrañas," WHERE ayatNumber = 46 AND chapterNumber = 44;
UPDATE verses SET es = "Como el borboteo del agua hirviente." WHERE ayatNumber = 47 AND chapterNumber = 44;
UPDATE verses SET es = "<em>Ordenaremos a los ángeles</em>: “Agarradle y arrastradle al centro del Fuego llameante”;" WHERE ayatNumber = 48 AND chapterNumber = 44;
UPDATE verses SET es = "<em>“</em>Luego verted sobre su cabeza el tormento del agua hirviendo”." WHERE ayatNumber = 49 AND chapterNumber = 44;
UPDATE verses SET es = "<em>“</em>¡Pruébalo! Te<em> consideraste</em> poderoso, honorable." WHERE ayatNumber = 50 AND chapterNumber = 44;
UPDATE verses SET es = "<em>“</em>Sobre esto es en verdad sobre lo que dudabais”." WHERE ayatNumber = 51 AND chapterNumber = 44;
UPDATE verses SET es = "En verdad, los justos estarán en un lugar seguro," WHERE ayatNumber = 52 AND chapterNumber = 44;
UPDATE verses SET es = "Entre jardines y manantiales," WHERE ayatNumber = 53 AND chapterNumber = 44;
UPDATE verses SET es = "Vestidos de fina seda y grueso brocado, situados frente a frente." WHERE ayatNumber = 54 AND chapterNumber = 44;
UPDATE verses SET es = "Así <em>será. </em>Y los desposaremos con mujeres justas, de ojos grandes <em>y bellos.</em>" WHERE ayatNumber = 55 AND chapterNumber = 44;
UPDATE verses SET es = "Pedirán allí toda <em>clase de </em>fruta, en <em>paz y</em> seguridad." WHERE ayatNumber = 56 AND chapterNumber = 44;
UPDATE verses SET es = "No sufrirán la muerte, excepto la primera. Y Él los salvará del castigo del Fuego llameante." WHERE ayatNumber = 57 AND chapterNumber = 44;
UPDATE verses SET es = "<em>Como acto de</em> gracia de tu Señor. Ése es el triunfo supremo." WHERE ayatNumber = 58 AND chapterNumber = 44;
UPDATE verses SET es = "Y lo hemos hecho (al Corán) fácil en tu lengua para que reflexionen." WHERE ayatNumber = 59 AND chapterNumber = 44;
UPDATE verses SET es = "Espera, pues, tú; ellos <em>también</em> están esperando," WHERE ayatNumber = 60 AND chapterNumber = 44;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 45;
UPDATE verses SET es = "Ha Mim." WHERE ayatNumber = 2 AND chapterNumber = 45;
UPDATE verses SET es = "La revelación de este Libro procede de Al-lah, el Poderoso, el Sabio." WHERE ayatNumber = 3 AND chapterNumber = 45;
UPDATE verses SET es = "En verdad, en los cielos y en la tierra hay Signos para quienes creen." WHERE ayatNumber = 4 AND chapterNumber = 45;
UPDATE verses SET es = "Y en vuestra propia creación y <em>en</em> la de todas las criaturas que Él esparce <em>por la tierra</em> hay Signos para la gente que posee fe firme." WHERE ayatNumber = 5 AND chapterNumber = 45;
UPDATE verses SET es = "Y<em> en</em> la alternación de la noche y el día y en la provisión que Al-lah envía desde el cielo, por la que devuelve la vida a la tierra después su muerte, y <em>en </em>el cambio de los vientos, hay Signos para un pueblo que <em>intente</em> entender." WHERE ayatNumber = 6 AND chapterNumber = 45;
UPDATE verses SET es = "Estos son los Signos de Al-lah que te repetimos con verdad. ¿En qué palabra creerán, después de <em>rechazar la palabra de</em> Al-lah y Sus Signos?" WHERE ayatNumber = 7 AND chapterNumber = 45;
UPDATE verses SET es = "¡Ay de cada mentiroso pecador!" WHERE ayatNumber = 8 AND chapterNumber = 45;
UPDATE verses SET es = "Que escucha los Signos de Al-lah que se le recitan y luego persiste orgullosamente <em>en su incredulidad,</em> como si no los oyera. Dadle, pues, la noticia de un doloroso castigo." WHERE ayatNumber = 9 AND chapterNumber = 45;
UPDATE verses SET es = "Mas cuando oye algo de Nuestros Signos, se burla de ellos. Para éstos habrá un castigo afrentoso." WHERE ayatNumber = 10 AND chapterNumber = 45;
UPDATE verses SET es = "Ante ellos está el Infierno; y lo que han conseguido no les servirá de nada, como tampoco aquellos que ha tomado por protectores al margen de Al-lah. Y tendrán un serio castigo." WHERE ayatNumber = 11 AND chapterNumber = 45;
UPDATE verses SET es = "Ésta es la guía. Para aquellos que no creen en los Signos de su Señor está la tortura de un castigo doloroso." WHERE ayatNumber = 12 AND chapterNumber = 45;
UPDATE verses SET es = "Al-lah es Quien os ha sometido al mar para que los buques naveguen en él siguiendo Su orden, para que busquéis de Su magnanimidad y seáis agradecidos." WHERE ayatNumber = 13 AND chapterNumber = 45;
UPDATE verses SET es = "Él os ha sometido cuanto hay en los cielos y cuanto hay en la tierra; todo<em> esto procede</em> de Él. Aquí hay en verdad Signos para un pueblo que reflexiona." WHERE ayatNumber = 14 AND chapterNumber = 45;
UPDATE verses SET es = "Di a quienes creen, que ejerciten el perdón hacia aquellos que no esperan que los días prometidos por Al-lah <em>vayan a llegar</em>, para que Él pueda retribuir a los hombres según lo que merecen." WHERE ayatNumber = 15 AND chapterNumber = 45;
UPDATE verses SET es = "Quien actúa rectamente lo hace por su propia alma. Y quien practica el mal lo hace en su propio perjuicio. Luego seréis <em>todos</em> devueltos a vuestro Señor." WHERE ayatNumber = 16 AND chapterNumber = 45;
UPDATE verses SET es = "En verdad, entregamos a los hijos de Israel el Libro, la soberanía y la profecía; les proporcionamos cosas <em>buenas y</em> puras, y los exaltamos por encima de los pueblos <em>de su época.</em>" WHERE ayatNumber = 17 AND chapterNumber = 45;
UPDATE verses SET es = "Y les dimos instrucciones claras en relación con la Ley. Sólo iscutieron, por rivalidad mutua, después de que les llegara el <em>verdadero</em> conocimiento. Ciertamente, tu Señor juzgará entre ellos en el Día de la Resurrección sobre aquello en lo que divergían." WHERE ayatNumber = 18 AND chapterNumber = 45;
UPDATE verses SET es = "Luego te situamos en el camino claro de la Ley; síguelo, pues, y no sigas las inclinaciones perversas de quienes no saben." WHERE ayatNumber = 19 AND chapterNumber = 45;
UPDATE verses SET es = "En verdad no te servirán de nada contra Al-lah. Y en cuanto a los inicuos, algunos de ellos son amigos de otros. Pero Al-lah es el Amigo de los justos." WHERE ayatNumber = 20 AND chapterNumber = 45;
UPDATE verses SET es = "Este <em>Libro contiene</em> evidencias claras para la humanidad y es guía y misericordia para la gente que posee fe firme." WHERE ayatNumber = 21 AND chapterNumber = 45;
UPDATE verses SET es = "¿Creen acaso quienes cometen malas acciones que les haremos iguales a aquellos que creen y hacen buenas obras, de modo que su vida y su muerte sean iguales? Qué malo es <em>en verdad</em> lo que juzgan." WHERE ayatNumber = 22 AND chapterNumber = 45;
UPDATE verses SET es = "Al-lah ha creado los cielos y la tierra con verdad y para que se recompense a cada alma por lo que ha merecido; y no serán perjudicados." WHERE ayatNumber = 23 AND chapterNumber = 45;
UPDATE verses SET es = "¿Has visto a quien convierte a su deseo en su señor, a quien Al-lah ha juzgado intencionadamente como extraviado, cuyos oídos y cuyo corazón Él ha sellado, y sobre cuyos ojos ha puesto una venda? ¿Quién, pues, lo guiará después de que Al-lah <em>lo ha condenado?</em> ¿No caeréis en la cuenta?" WHERE ayatNumber = 24 AND chapterNumber = 45;
UPDATE verses SET es = "Mas dicen; “No hay más que esta nuestra vida presente; vivimos y morimos <em>aquí</em>; y nada nos destruye sino el Tiempo”. Pero no tienen conocimiento de ello; no hacen más que conjeturas." WHERE ayatNumber = 25 AND chapterNumber = 45;
UPDATE verses SET es = "Y cuando se les recitan Nuestros Signos claros, su único argumento consiste en decir: “Devolvednos a nuestros padres, si sois veraces”." WHERE ayatNumber = 26 AND chapterNumber = 45;
UPDATE verses SET es = "Diles: <em>“Es</em> Al-lah <em>Quien</em> os da la vida y luego os reunirá juntos en el Día de la Resurrección, sobre el que no hay duda alguna. Aunque la mayoría de los hombres no lo saben”." WHERE ayatNumber = 27 AND chapterNumber = 45;
UPDATE verses SET es = "A Al-lah pertenece el reino de los cielos y la tierra; y en el día en que llegue la Hora, en ese día sufrirán quienes rechazaron la verdad." WHERE ayatNumber = 28 AND chapterNumber = 45;
UPDATE verses SET es = "Verás a todos los hombres postrados sobre sus rodillas. Cada pueblo será amonestado según lo que hicieron <em>y se les dirá:</em> “En este día seréis correspondidos por lo que hicisteis”." WHERE ayatNumber = 29 AND chapterNumber = 45;
UPDATE verses SET es = "“Este es Nuestro Libro; habla contra vosotros con verdad. Hemos hecho que <em>todo</em> lo que hicisteis quede <em>plenamente</em> registrado”." WHERE ayatNumber = 30 AND chapterNumber = 45;
UPDATE verses SET es = "Ahora bien, a quienes creyeron e hicieron buenas obras, su Señor los admitirá a Su misericordia. Ése es el éxito manifiesto." WHERE ayatNumber = 31 AND chapterNumber = 45;
UPDATE verses SET es = "En cuanto a los que no creen: “¿No se os recitaron Mis Signos? Pero erais arrogantes y fuisteis un pueblo culpable”." WHERE ayatNumber = 32 AND chapterNumber = 45;
UPDATE verses SET es = "“Y cuando se dijo: “La promesa de Al-lah es ciertamente verdadera, y en cuanto a la Hora, no hay duda alguna sobre su <em>llegada”</em>, respondisteis: “No sabemos qué es la Hora; pensamos que no es más que una conjetura, y no estamos convencidos”." WHERE ayatNumber = 33 AND chapterNumber = 45;
UPDATE verses SET es = "Entonces se les harán evidentes las perversas <em>consecuencias</em> de sus acciones y se verán rodeados de aquello de lo que acostumbraban a burlarse." WHERE ayatNumber = 34 AND chapterNumber = 45;
UPDATE verses SET es = "Y se <em>les</em> dirá: “Hoy nos olvidaremos de vosotros, al igual que olvidasteis la reunión de este día vuestro. Vuestra morada es el Fuego, y no tendréis quien os ayude”." WHERE ayatNumber = 35 AND chapterNumber = 45;
UPDATE verses SET es = "“<em>Así es</em> porque hicisteis objeto de escarnio los Signos de Al-lah, y la vida del mundo os engañó”. Por tanto, en ese día no se les sacará de ese lugar ni les será permitido acceder al umbral." WHERE ayatNumber = 36 AND chapterNumber = 45;
UPDATE verses SET es = "Toda alabanza pertenece, pues, a Al-lah, Señor de los cielos y Señor de la tierra, y Señor de <em>todos</em> los mundos." WHERE ayatNumber = 37 AND chapterNumber = 45;
UPDATE verses SET es = "Suya es la majestad en los cielos y en la tierra; pues Él es el Poderoso, el Sabio." WHERE ayatNumber = 38 AND chapterNumber = 45;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente y Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 46;
UPDATE verses SET es = "Ha Mim." WHERE ayatNumber = 2 AND chapterNumber = 46;
UPDATE verses SET es = "La revelación de este Libro procede de Al-lah, el Poderoso, el Sabio." WHERE ayatNumber = 3 AND chapterNumber = 46;
UPDATE verses SET es = "No hemos creado los cielos y la tierra, y todo lo que hay entre ellos, sino con la verdad, y para un plazo fijado; mas los incrédulos se apartan de aquello de lo que han sido advertidos." WHERE ayatNumber = 4 AND chapterNumber = 46;
UPDATE verses SET es = "Diles: “¿Sabéis qué es lo que invocáis aparte de Al-lah? Mostradme o que han creado en la tierra. ¿Acaso han participado en <em> la creación de los</em> cielos? Traedme un Libro <em>revelado</em> ntes de éste o algún vestigio de conocimiento <em>en apoyo vuestro, </em>si<em> en verdad</em> decís la verdad”." WHERE ayatNumber = 5 AND chapterNumber = 46;
UPDATE verses SET es = "¿Quién está más extraviado que los que, en lugar de Al-lah, imploran a quienes no les responderán hasta el Día de la Resurrección, y ni siquiera prestan atención a su plegaria?" WHERE ayatNumber = 6 AND chapterNumber = 46;
UPDATE verses SET es = "Mas cuando los humanos sean reunidos juntos, se harán enemigos suyos, y negarán su adoración." WHERE ayatNumber = 7 AND chapterNumber = 46;
UPDATE verses SET es = "Cuando se les recitan Nuestros Signos evidentes, los incrédulos dicen de la verdad cuando les llega: “Es brujería manifiesta”." WHERE ayatNumber = 8 AND chapterNumber = 46;
UPDATE verses SET es = "¿Acaso dicen: “Él lo ha inventado?”. Diles: “Si yo lo hubiera inventado, no podríais servirme de nada contra Al-lah. Él conoce mejor que nadie las conversaciones <em>maliciosas</em> a que os dedicáis. Él basta como Testigo entre vosotros y yo. Pues Él es el Sumo Indulgente, el Misericordioso”." WHERE ayatNumber = 9 AND chapterNumber = 46;
UPDATE verses SET es = "Diles: “No soy ningún Mensajero nuevo, ni sé lo que se hará conmigo o con vosotros <em>en esta vida.</em> No hago más que seguir lo que me ha sido revelado, y no soy más que un simple Amonestador”." WHERE ayatNumber = 10 AND chapterNumber = 46;
UPDATE verses SET es = "Diles: “Decidme; si esto procede de Al-lah y no creéis en ello, y n testigo de entre los hijos de Israel da testimonio de <em>la aparición de</em> uno como él, y creyó, pero vosotros sois demasiado orgullosos, <em>¿qué será de vosotros?”</em>. En verdad, Al-lah no guía a los hombres que practican la injusticia." WHERE ayatNumber = 11 AND chapterNumber = 46;
UPDATE verses SET es = "Mas quienes no creen dicen de los creyentes: “Si fuese algo bueno, no se nos habrían adelantado para conseguirlo”. Y al no haber sido guiados por ello, dirán: “Esto es una mentira antigua”." WHERE ayatNumber = 12 AND chapterNumber = 46;
UPDATE verses SET es = "Y antes de él estuvo el Libro de Moisés, guía y misericordia; y ste es un Libro en idioma árabe que cumple <em>las profecías anteriores,</em> para amonestar a quienes obran mal; y como buena nueva para quienes hacen el bien." WHERE ayatNumber = 13 AND chapterNumber = 46;
UPDATE verses SET es = "En verdad, quienes dicen: “Nuestro Señor es Al-lah” <em>y</em> luego permanecen perseverantes; ningún temor <em>caerá</em> sobre ellos, ni serán afligidos." WHERE ayatNumber = 14 AND chapterNumber = 46;
UPDATE verses SET es = "Éstos son los moradores del Jardín. En él habitarán: una recompensa por lo que hicieron." WHERE ayatNumber = 15 AND chapterNumber = 46;
UPDATE verses SET es = "Hemos ordenado al hombre que sea bueno con sus padres. Su madre lo lleva con incomodidad y lo pare con dolor. Su parto y lactancia dura treinta meses hasta que, cuando alcanza la plena madurez, y cumple cuarenta años, dice: “Señor mío, concédeme <em>el poder</em> para ser agradecido por el favor que me has concedido a mí y a mis padres y para que practique el bien para complacerte. Haz que mi descendencia sea justa. Yo vuelvo ciertamente a Ti y, en verdad, soy de quienes se someten a <em>Tu voluntad.</em>" WHERE ayatNumber = 16 AND chapterNumber = 46;
UPDATE verses SET es = "Éstos son de quienes aceptamos sus buenas obras y perdonamos sus alas acciones. <em>Estarán</em> entre los moradores del Jardín, <em>en cumplimiento</em> de la verdadera promesa que se les hizo." WHERE ayatNumber = 17 AND chapterNumber = 46;
UPDATE verses SET es = "Pero hay quien dice a sus padres: “¡Vergüenza para vosotros! ¿Me amenazáis con que naceré <em>de nuevo</em>, cuando ya han desaparecido eneraciones antes de mí?”. Y ambos invocan a Al-lah pidiendo ayuda <em> y le dicen</em>: “¡Ay de ti! ¡Cree!, pues la promesa de Al-lah es verdadera”. Pero responde: “Esto no es más que fábulas de los antiguos”." WHERE ayatNumber = 18 AND chapterNumber = 46;
UPDATE verses SET es = "Son éstos contra quienes se dictó la sentencia <em>del castigo</em> , junto con los grupos de Yinn y de hombres que les precedieron. En verdad, fueron los perdedores." WHERE ayatNumber = 19 AND chapterNumber = 46;
UPDATE verses SET es = "Y para todos hay grados <em>de medida </em>según lo que hicieron, para que Al-lah les retribuya plenamente por sus acciones; y no serán perjudicados." WHERE ayatNumber = 20 AND chapterNumber = 46;
UPDATE verses SET es = "Mas en el día en que los incrédulos sean llevados ante el Fuego, <em>se les dirá:</em> “Agotasteis vuestras cosas buenas en la vida del mundo y disfrutasteis <em>plenamente</em> de ellas. Ahora, en este día, seréis retribuidos con un castigo vergonzoso, porque fuisteis arrogantes en la tierra sin justificación, y actuasteis con rebeldía”." WHERE ayatNumber = 21 AND chapterNumber = 46;
UPDATE verses SET es = "Mencionad al hermano de Ad, cuando amonestó a su pueblo entre las unas – aunque hubo amonestadores antes y después de él – <em>diciéndoles:</em> “No adoréis sino a Al-lah. Temo para vosotros el castigo de un gran día”." WHERE ayatNumber = 22 AND chapterNumber = 46;
UPDATE verses SET es = "Dijeron: “¿Has venido para apartarnos de nuestros dioses? Tráenos, pues, aquello con lo que nos amenazas, si <em>en verdad</em> eres de los veraces”." WHERE ayatNumber = 23 AND chapterNumber = 46;
UPDATE verses SET es = "Dijo: “<em>Su</em> conocimiento corresponde sólo a Al-lah. Yo os transmito aquello con lo que se me ha enviado, pero veo que sois un pueblo <em>muy</em> ignorante”." WHERE ayatNumber = 24 AND chapterNumber = 46;
UPDATE verses SET es = "Entonces, cuando lo vieron acercándose hacia sus valles como una nube, dijeron: “Es una nube que nos dará la lluvia”. “No, no es sino lo que queríais apresurar: un viento que lleva un severo castigo”." WHERE ayatNumber = 25 AND chapterNumber = 46;
UPDATE verses SET es = "“Que destruirá todo por mandato de su Señor”. Y así sucedió, hasta tal punto, que no quedó nada por ver, excepto sus viviendas. Así retribuimos a los culpables." WHERE ayatNumber = 26 AND chapterNumber = 46;
UPDATE verses SET es = "Los habíamos establecido en lo que no os establecimos a vosotros; les dimos oídos, ojos y corazones. Mas sus oídos, sus ojos y sus corazones no les sirvieron de nada puesto que negaron los Signos de Al-lah; y aquello de lo que solían burlarse les rodeó." WHERE ayatNumber = 27 AND chapterNumber = 46;
UPDATE verses SET es = "Destruimos las ciudades que os rodean; y variamos los Signos para que acudieran <em>a Nosotros.</em>" WHERE ayatNumber = 28 AND chapterNumber = 46;
UPDATE verses SET es = "¿Por qué, pues, no les ayudaron aquellos que habían tomado por dioses, buscando <em>Su</em> cercanía <em>a través suyo</em>? No, se les perdieron. Ese fue <em>el resultado de</em> su mentira y de lo que tramaron." WHERE ayatNumber = 29 AND chapterNumber = 46;
UPDATE verses SET es = "<em>Acuérdate</em> de cuando dirigimos hacia ti un grupo de los inn que deseaba oír el Corán y, cuando estaban presentes en su<em>recitación,</em> se dijeron<em> mutuamente:</em> “Estad en silencio <em> y escuchad”</em> y, cuando terminó, volvieron a sus gentes, advirtiéndo<em>les.</em>" WHERE ayatNumber = 30 AND chapterNumber = 46;
UPDATE verses SET es = "Dijeron: “¡Oh pueblo nuestro! Hemos oído un Libro que ha sido revelado después de Moisés, que cumple todo lo que le precede; guía a la verdad y al camino recto." WHERE ayatNumber = 31 AND chapterNumber = 46;
UPDATE verses SET es = "“¡Oh pueblo nuestro! Responded a quien os emplaza a Al-lah y creed en Él. Él os perdonará vuestros pecados y os protegerá de un doloroso castigo." WHERE ayatNumber = 32 AND chapterNumber = 46;
UPDATE verses SET es = "“Mas quien no responda al Invitador a Al-lah, no podrá frustrar Su designio en la tierra ni tener protector fuera de Él. Éstos están en un error evidente”." WHERE ayatNumber = 33 AND chapterNumber = 46;
UPDATE verses SET es = "¿No han visto acaso que Al-lah, que creó los cielos y la tierra y no se cansó con su creación, tiene el poder de dar vida a los muertos? Sí, en verdad, Él tiene poder sobre todas las cosas." WHERE ayatNumber = 34 AND chapterNumber = 46;
UPDATE verses SET es = "En el día en que los incrédulos sean llevados ante el Fuego, <em>se les dirá:</em> “¿No es ésta la verdad?”. Ellos responderán: “Sí, por nuestro Señor”. Él dirá: “Probad, pues, el castigo, por no haber creído”." WHERE ayatNumber = 35 AND chapterNumber = 46;
UPDATE verses SET es = "Ten, pues, paciencia, como lo tuvieron los Mensajeros dotados de fuerte decisión, y no te apresures respecto a ellos. El día en que vean aquello con lo que se les amenaza, les parecerá como si sólo hubiesen permanecido una hora de un día. <em>Esta advertencia</em> ha sido comunicada; y nadie salvo los hombres desobedientes serán destruidos." WHERE ayatNumber = 36 AND chapterNumber = 46;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 47;
UPDATE verses SET es = "A los que no creen y apartan <em>a los hombres</em> del camino de Al-lah: Él hará que sus trabajos sean vanos." WHERE ayatNumber = 2 AND chapterNumber = 47;
UPDATE verses SET es = "Mas a quienes crean y hagan buenas obras y crean en lo que ha sido revelado a Mohammad -que es la verdad de su Señor– Él les eliminará sus pecados y reformará su conducta." WHERE ayatNumber = 3 AND chapterNumber = 47;
UPDATE verses SET es = "Esto es porque los incrédulos siguen la falsedad, mientras que los creyentes siguen la verdad de su Señor. Así Al-lah propone a los hombres sus ejemplos." WHERE ayatNumber = 4 AND chapterNumber = 47;
UPDATE verses SET es = "Cuando os enfrentéis <em>en una batalla regular</em> contra los incrédulos, golpeadles en el cuello y, una vez que los hayáis vencido, tad fuertemente <em>a los vencidos</em> –más tarde, <em>liberadlos como</em> gracia o <em>recibiendo</em> un rescate– hasta que la guerra haya depuesto sus cargas. <em>Eso es lo ordenado.</em> Mas si <em>así </em>lo hubiese decidido Al-lah, <em>Él mismo </em>les abría exigido<em> la retribución,</em> pero <em>Él </em>somete a algunos de vosotros a prueba a manos de otros. Y quienes mueren por la causa de Al-lah <em>sepan</em> que Él no permitirá que sus obras queden en vano." WHERE ayatNumber = 5 AND chapterNumber = 47;
UPDATE verses SET es = "Él los guiará y reformará su conducta." WHERE ayatNumber = 6 AND chapterNumber = 47;
UPDATE verses SET es = "Y los admitirá en el Jardín que Él ha embellecido y distinguido para ellos." WHERE ayatNumber = 7 AND chapterNumber = 47;
UPDATE verses SET es = "¡Oh vosotros, los creyentes! Si ayudáis<em> a la causa de</em> Al-lah, Él os ayudará y hará firmes vuestros pasos." WHERE ayatNumber = 8 AND chapterNumber = 47;
UPDATE verses SET es = "Mas <em>en cuanto a </em>los incrédulos, su destino es la perdición; Él hará que sus trabajos sean inútiles." WHERE ayatNumber = 9 AND chapterNumber = 47;
UPDATE verses SET es = "Esto es porque odian lo que Al-lah ha revelado; por ello Él ha hecho que sus trabajos sean vanos." WHERE ayatNumber = 10 AND chapterNumber = 47;
UPDATE verses SET es = "¿Acaso no han viajado por la tierra y han visto cuál fue el fin de os que existieron antes que ellos? Al-lah los destruyó <em>totalmente</em>, y a los incrédulos les ocurrirá algo similar." WHERE ayatNumber = 11 AND chapterNumber = 47;
UPDATE verses SET es = "Esto es porque Al-lah es el Protector de los creyentes y, en cuanto a los incrédulos, para ellos no hay protector." WHERE ayatNumber = 12 AND chapterNumber = 47;
UPDATE verses SET es = "En verdad, Al-lah hará que los que creen y practican el bien entren en los Jardines bajo los cuales fluyen los ríos; mientras que los incrédulos disfrutan y comen, como come el ganado, mas el Fuego será su morada." WHERE ayatNumber = 13 AND chapterNumber = 47;
UPDATE verses SET es = "¡Cuántas ciudades más poderosas que tu ciudad, que te expulsó, hemos destruido, y no tuvieron quien les ayudara!" WHERE ayatNumber = 14 AND chapterNumber = 47;
UPDATE verses SET es = "¿Acaso quien se apoya en una prueba evidente de su Señor es como aquellos a los que se hace <em>que aparezca</em> bella la maldad de sus acciones y siguen sus perversas inclinaciones?" WHERE ayatNumber = 15 AND chapterNumber = 47;
UPDATE verses SET es = "<em>He aquí</em> una descripción del Jardín prometido a los justos: en él hay ríos de agua que no se corrompe; ríos de leche cuyo sabor no cambia; ríos de vino, una delicia para quienes lo beben, y ríos de miel clara. En él tendrán <em>toda clase de</em> frutas y el perdón de su Señor. ¿Acaso <em>quienes disfrutan esta felicidad</em> son iguales que quienes moran en el Fuego, a quienes se da agua hirviendo para beber, que deshace sus entrañas?" WHERE ayatNumber = 16 AND chapterNumber = 47;
UPDATE verses SET es = "Entre ellos hay algunos que te escuchan hasta que, cuando se van de tu presencia, dicen a quienes se ha dado conocimiento: “¿De qué ha estado hablando precisamente ahora?”. Son ellos cuyos corazones ha sellado Al-lah y que siguen sus propias inclinaciones malvadas." WHERE ayatNumber = 17 AND chapterNumber = 47;
UPDATE verses SET es = "Mas quienes siguen la guía, Él aumenta su guía y les concede piedad." WHERE ayatNumber = 18 AND chapterNumber = 47;
UPDATE verses SET es = "No esperan sino la Hora, que debe llegarles de improviso. Sus Signos ya han aparecido. Mas ¿de qué les servirá su advertencia si les ha llegado <em>realmente?</em>" WHERE ayatNumber = 19 AND chapterNumber = 47;
UPDATE verses SET es = "Sabe, pues, que no hay Dios distinto de Al-lah. Pide perdón por tus faltas y por los hombres y mujeres creyentes. Pues Al-lah sabe el lugar por el que os movéis y el lugar en el que estáis." WHERE ayatNumber = 20 AND chapterNumber = 47;
UPDATE verses SET es = "Los creyentes dicen: “¿Por qué no se revela un <em>Surah?”.</em> Mas cuando se revela un <em>Surah</em> decisivo, y en él se menciona la lucha, verás a los de corazón enfermo mirarte con la mirada de quien está aturdido ante la sombra de la muerte<em>.</em> ¡La ruina caerá sobre ellos!" WHERE ayatNumber = 21 AND chapterNumber = 47;
UPDATE verses SET es = "<em>Para ellos lo mejor es</em> la obediencia y una palabra amable. Mas cuando se decida el asunto, lo mejor para ellos es que hayan sido veraces con Al-lah." WHERE ayatNumber = 22 AND chapterNumber = 47;
UPDATE verses SET es = "¿Si ocuparais, pues, un puesto de autoridad, crearíais el desorden en el país y romperíais vuestros lazos de parentesco?" WHERE ayatNumber = 23 AND chapterNumber = 47;
UPDATE verses SET es = "A éstos es a quienes Al-lah maldice, haciéndoles sordos y cegando sus ojos." WHERE ayatNumber = 24 AND chapterNumber = 47;
UPDATE verses SET es = "¿No meditarán, pues, en el Corán, o es que ellos mismos han cerrado sus corazones por dentro?" WHERE ayatNumber = 25 AND chapterNumber = 47;
UPDATE verses SET es = "En verdad, quienes vuelven la espalda después de que se les ha manifestado la guía, es porque Satanás los ha seducido, y alienta en ellos falsas esperanzas." WHERE ayatNumber = 26 AND chapterNumber = 47;
UPDATE verses SET es = "Esto es porque dijeron a quienes odian lo que ha revelado Al-lah: “Os obedeceremos en algunos asuntos”, mas Al-lah conoce sus secretos." WHERE ayatNumber = 27 AND chapterNumber = 47;
UPDATE verses SET es = "Pero <em>¿qué harán</em> cuando los ángeles les hagan morir, golpeándoles en sus rostros y espaldas?" WHERE ayatNumber = 28 AND chapterNumber = 47;
UPDATE verses SET es = "Esto es por haber seguido lo que desagradaba a Al-lah y odiado lo que le agradaba. De este modo Él hizo inútiles sus trabajos." WHERE ayatNumber = 29 AND chapterNumber = 47;
UPDATE verses SET es = "¿Suponen acaso los que tienen el corazón enfermo que Al-lah no sacará a la luz su malicia?" WHERE ayatNumber = 30 AND chapterNumber = 47;
UPDATE verses SET es = "Mas si lo hubiésemos querido, te los habríamos mostrado para que los conocieras por sus señales. Ciertamente los reconocerás por el tono de <em>sus </em>palabras. Y Al-lah conoce vuestras acciones." WHERE ayatNumber = 31 AND chapterNumber = 47;
UPDATE verses SET es = "Ciertamente os pondremos a prueba hasta que distingamos a los que se esfuerzan verdaderamente <em>en la causa de Dios</em> y a los que son perseverantes. Y <em>mediante la prueba</em> verificaremos vuestra auténtica valía." WHERE ayatNumber = 32 AND chapterNumber = 47;
UPDATE verses SET es = "Los que no creen y apartan <em>a los hombres</em> del camino de Al-lah, oponiéndose al Mensajero después de que la guía se les ha hecho evidente, no perjudicarán a Al-lah en lo más mínimo y Él hará estériles sus obras." WHERE ayatNumber = 33 AND chapterNumber = 47;
UPDATE verses SET es = "¡Oh vosotros, creyentes! Obedeced a Al-lah y al Mensajero y no permitáis que vuestras obras queden en vano." WHERE ayatNumber = 34 AND chapterNumber = 47;
UPDATE verses SET es = "En verdad, a los que no creen y apartan <em>a los hombres</em> del camino de Al-lah, y mueren mientras están en la incredulidad: Al-lah ciertamente no los perdonará." WHERE ayatNumber = 35 AND chapterNumber = 47;
UPDATE verses SET es = "No flaqueéis, de manera que demandéis la paz, cuando estáis destinados a salir victoriosos. Al-lah está con vosotros y Él no os privará de <em>la recompensa de</em> vuestras acciones." WHERE ayatNumber = 36 AND chapterNumber = 47;
UPDATE verses SET es = "La vida<em> de este </em>mundo no es más que juego y diversión; si creéis y sois justos, Él os dará vuestras recompensas y no os pedirá vuestras posesiones." WHERE ayatNumber = 37 AND chapterNumber = 47;
UPDATE verses SET es = "Si os las pidiera y os las exigiera, os comportaríais mezquinamente y Él sacaría a la luz vuestra malicia." WHERE ayatNumber = 38 AND chapterNumber = 47;
UPDATE verses SET es = "He ahí que sois los invitados a gastar en la causa de Al-lah; pero hay algunos de vosotros que se comportan mezquinamente. Pero quien es mezquino lo es solamente contra su propia alma. Al-lah es Autosuficiente, y sois vosotros los necesitados. Mas si volvéis la espalda, colocará en vuestro lugar a un pueblo distinto a vosotros, que no será como vosotros." WHERE ayatNumber = 39 AND chapterNumber = 47;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 48;
UPDATE verses SET es = "En verdad, te hemos concedido una clara victoria." WHERE ayatNumber = 2 AND chapterNumber = 48;
UPDATE verses SET es = "Para que Al-lah corra un velo por tus faltas, pasadas y futuras, y complete Su favor sobre ti, guiándote por el camino recto;" WHERE ayatNumber = 3 AND chapterNumber = 48;
UPDATE verses SET es = "Para que Al-lah te ayude poderosamente." WHERE ayatNumber = 4 AND chapterNumber = 48;
UPDATE verses SET es = "Él es Quien infundió tranquilidad en los corazones de los creyentes para que añadieran fe a su fe -pues a Al-lah pertenecen las huestes de los cielos y la tierra, y Al-lah es Omnisciente, Sabio–" WHERE ayatNumber = 5 AND chapterNumber = 48;
UPDATE verses SET es = "Para hacer que los hombres y mujeres creyentes entren en Jardines por los que corren ríos, en los que habitarán y <em>para</em> retirarles sus males –y esto, a la vista de Al-lah, es el triunfo supremo–" WHERE ayatNumber = 6 AND chapterNumber = 48;
UPDATE verses SET es = "Y para castigar a los hombres hipócritas y a las mujeres hipócritas y a los hombres idólatras y a las mujeres idólatras, que abrigan malos pensamientos sobre Al-lah. Sobre ellos pasará la rueda del infortunio; y la cólera de Al-lah. Él los ha maldecido y les ha preparado el Infierno. ¡Qué mal destino es en verdad!" WHERE ayatNumber = 7 AND chapterNumber = 48;
UPDATE verses SET es = "Pues a Al-lah pertenecen las huestes de los cielos y la tierra; y Al-lah es Poderoso, Sabio." WHERE ayatNumber = 8 AND chapterNumber = 48;
UPDATE verses SET es = "Te hemos enviado como Testigo, portador de la buena nueva y Amonestador." WHERE ayatNumber = 9 AND chapterNumber = 48;
UPDATE verses SET es = "Para que creáis en Al-lah y en Su Mensajero y le ayudéis y le honréis, y <em>para</em> que glorifiquéis a Al-lah por la mañana y por la tarde." WHERE ayatNumber = 10 AND chapterNumber = 48;
UPDATE verses SET es = "En verdad, los que te juran lealtad a ti ciertamente juran lealtad a Al-lah. La mano de Al-lah está sobre sus manos. Por ello, quien incumple <em>su juramento, lo</em> incumple en su propio perjuicio; y a quien cumpla el pacto establecido con Al-lah, Él le concederá ciertamente una gran recompensa." WHERE ayatNumber = 11 AND chapterNumber = 48;
UPDATE verses SET es = "Los moradores del desierto, que quedaron atrás te dirán: “Nuestros bienes y nuestras familias nos mantuvieron ocupados; pide, pues, perdón por nosotros”. Dicen con sus lenguas lo que no está en sus corazones. Diles: “¿Quién puede serviros de algo contra Al-lah si piensa perjudicaros de algún modo o piensa beneficiaros? No, Al-lah es Perfectamente Consciente de lo que hacéis." WHERE ayatNumber = 12 AND chapterNumber = 48;
UPDATE verses SET es = "“¡No!, creísteis que el Mensajero y los creyentes nunca volverían a sus familias, y eso se hizo atractivo para vuestros corazones; mantuvisteis un pensamiento malvado y fuisteis un pueblo corrompido”." WHERE ayatNumber = 13 AND chapterNumber = 48;
UPDATE verses SET es = "<em>En cuanto </em>a quienes no creen en Al-lah y en Su Mensajero, <em>sepan que</em> ciertamente hemos preparado para los incrédulos un fuego llameante." WHERE ayatNumber = 14 AND chapterNumber = 48;
UPDATE verses SET es = "A Al-lah pertenece el reino de los cielos y la tierra. Él perdona a quien Le agrada y castiga a quien quiere. Pues Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 15 AND chapterNumber = 48;
UPDATE verses SET es = "Los que <em>se las arreglaron para</em> quedar atrás dirán, cuando os dirijáis para recoger el botín: “Permitidnos que os sigamos”. Intentan cambiar el decreto de Al-lah. Diles: “No nos seguiréis. Así lo dijo Al-lah previamente”. Entonces responderán: “¡No!, es porque nos envidiáis”. No es así, pero no entienden casi nada." WHERE ayatNumber = 16 AND chapterNumber = 48;
UPDATE verses SET es = "Di a los árabes del desierto que quedaron detrás: “Seréis llamados <em>a luchar</em> contra un pueblo de enorme valor; lucharéis contra ellos hasta que se rindan. Entonces, si obedecéis, Al-lah os dará una buena recompensa, pero si dais la espalda, como disteis antes la espalda, os castigará con un castigo doloroso”." WHERE ayatNumber = 17 AND chapterNumber = 48;
UPDATE verses SET es = "No tiene culpa el ciego, ni tiene culpa el cojo, ni tampoco el enfermo, <em>si no se dirigen a la lucha.</em> Mas a quien obedece a Al-lah y a Su Mensajero, Él lo hará entrar en los Jardines por los que corren ríos; pero a quien vuelva la espalda, Él lo castigará con un castigo severo." WHERE ayatNumber = 18 AND chapterNumber = 48;
UPDATE verses SET es = "En verdad, Al-lah se congratuló con los creyentes cuando te juraron fidelidad bajo el Árbol; Él sabía lo que había en sus corazones, y Él les infundió tranquilidad, recompensándolos con una victoria inmediata." WHERE ayatNumber = 19 AND chapterNumber = 48;
UPDATE verses SET es = "Y grandes trofeos que obtendrán, pues Al-lah es Poderoso, Sabio." WHERE ayatNumber = 20 AND chapterNumber = 48;
UPDATE verses SET es = "Al-lah os ha prometido grandes trofeos que alcanzaréis; os ha dado esto de antemano y ha apartado las manos de los hombres de vosotros, para que sirva de Signo para los creyentes y os guíe por el camino recto." WHERE ayatNumber = 21 AND chapterNumber = 48;
UPDATE verses SET es = "<em>Él os ha prometido</em> otra <em>victoria,</em> que no podréis conseguir aún, pero que Al-lah ciertamente la ha conseguido, porque Al-lah tiene poder sobre todas las cosas." WHERE ayatNumber = 22 AND chapterNumber = 48;
UPDATE verses SET es = "Mas si los incrédulos os combatieran, ciertamente volverían sus espaldas; entonces no encontrarían protector ni ayudante." WHERE ayatNumber = 23 AND chapterNumber = 48;
UPDATE verses SET es = "Así es la conducta dispuesta de Al-lah que ha estado <em>vigente</em> con anterioridad; y no encontrarás cambio alguno en la conducta instituida por Al-lah." WHERE ayatNumber = 24 AND chapterNumber = 48;
UPDATE verses SET es = "Él es Quien apartó sus manos de vosotros y vuestras manos de ellos en el valle de la Meca, después de concederos la victoria sobre ellos. Pues Al-lah ve todo lo que hacéis." WHERE ayatNumber = 25 AND chapterNumber = 48;
UPDATE verses SET es = "Son los incrédulos quienes os impidieron la entrada en la Mezquita Sagrada así como la ofrenda que no pudo alcanzar el lugar del sacrificio. Y de no haber sido por los hombres y mujeres creyentes a los que no conocíais y que hubierais podido exterminar, hasta el punto e que os hubierais perjudicado, sin saberlo, a causa de ellos, <em>Él os hubiera permitido luchar, pero no lo hizo</em> para poder admitir a Su Misericordia a quien quisiera. Si hubiesen estado separados <em>de los incrédulos</em>, en verdad habríamos castigado a los que no creían con un severo castigo." WHERE ayatNumber = 26 AND chapterNumber = 48;
UPDATE verses SET es = "Cuando los incrédulos forjaron en sus corazones una orgullosa indignación, la indignación <em>de los días</em> de Ignorancia, Al-lah infundió Su tranquilidad a Su Mensajero y a los creyentes y les hizo seguir la palabra de la justicia, porque tenían más derecho y eran más merecedores de ella. Pues Al-lah lo conoce todo perfectamente." WHERE ayatNumber = 27 AND chapterNumber = 48;
UPDATE verses SET es = "Ciertamente que en verdad Al-lah ha cumplido para Su Mensajero la Visión. Entraréis de cierto en la Mezquita Sagrada, si Al-lah lo quiere, con seguridad; <em>algunos</em> con <em>sus</em> cabezas rapadas y <em>otros</em> con el pelo corto; <em>y </em>no tendréis temor. Mas Él sabe lo que desconocéis. En efecto, ha ordenado para vosotros, además de ésta, una victoria casi inmediata." WHERE ayatNumber = 28 AND chapterNumber = 48;
UPDATE verses SET es = "Él es Quien ha enviado a Su Mensajero, con la guía y la Religión de la verdad, para hacerla prevalecer sobre <em>cada una de las demás</em> religiones. Pues Al-lah basta como Testigo." WHERE ayatNumber = 29 AND chapterNumber = 48;
UPDATE verses SET es = "Mohammad es el Mensajero de Al-lah. Quienes están con él son firmes ontra los incrédulos y compasivos entre si. Los verás inclinándose <em>y</em> postrándose <em>en Oración, </em>buscando la gracia de Al-lah y <em>Su</em> agrado. Marcas de sus <em>características</em> se hallan en sus rostros, que son las señales de las postraciones. Ésta es su descripción en la Torah. Y su descripción en el Evangelio es como la de una semilla que produce primero su brote y después se fortalece; más tarde se hace grande y permanece firme sobre su tronco, complaciendo a os sembradores, para que Él haga que los incrédulos ardan de furor <em>ante su vista.</em> Al-lah ha prometido a quienes creen y hacen buenas obras, el perdón y una gran recompensa. " WHERE ayatNumber = 30 AND chapterNumber = 48;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 49;
UPDATE verses SET es = "¡Oh vosotros, creyentes! No seáis audaces con respecto a Al-lah y a Su Mensajero, sino temed a Al-lah. En verdad, Al-lah es Quien Todo lo oye, el Omnisciente." WHERE ayatNumber = 2 AND chapterNumber = 49;
UPDATE verses SET es = "¡Oh vosotros, creyentes! No levantéis vuestras voces por encima de la voz del Profeta, ni os dirijáis a él en voz alta como os dirigís mutuamente, no sea que vuestras <em>buenas </em>obras se pierdan sin que os deis cuenta." WHERE ayatNumber = 3 AND chapterNumber = 49;
UPDATE verses SET es = "En verdad, quienes bajan su voz en presencia del Mensajero de Al-lah son aquellos cuyos corazones Al-lah ha hecho justos por medio de pruebas. Para ellos habrá el perdón y una gran recompensa." WHERE ayatNumber = 4 AND chapterNumber = 49;
UPDATE verses SET es = "En verdad <em>que</em> quienes comienzan a llamarte en voz alta cuando aún están a distancia de <em>tus</em> hogares: la mayoría de ellos carecen de entendimiento." WHERE ayatNumber = 5 AND chapterNumber = 49;
UPDATE verses SET es = "Mas si hubiesen esperado con paciencia hasta que salieras hacia ellos, habría sido mejor para ellos. Pero Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 6 AND chapterNumber = 49;
UPDATE verses SET es = "¡Oh vosotros, los creyentes! Si una persona injusta os trae alguna noticia, verificad a fondo <em>la exactitud del informe,</em> no sea que perjudiquéis a alguien por ignorancia, y os arrepintáis después de lo que habéis hecho." WHERE ayatNumber = 7 AND chapterNumber = 49;
UPDATE verses SET es = "Sabed que entre vosotros está el Mensajero de Al-lah; si tuviera que cumplir vuestros deseos en la mayoría de los asuntos, ciertamente os eríais en problemas; mas Al-lah os ha hecho atractiva la fe y ha hecho <em> que aparezca</em> bella para vuestros corazones, haciendo que la incredulidad, la maldad y la desobediencia os sean odiosas. Ésos son, en verdad, quienes siguen el camino recto," WHERE ayatNumber = 8 AND chapterNumber = 49;
UPDATE verses SET es = "A través de la gracia y el favor de Al-lah. Pues Al-lah es el Omnisciente, Sabio." WHERE ayatNumber = 9 AND chapterNumber = 49;
UPDATE verses SET es = "Mas si dos grupos de creyentes luchan <em>mutuamente</em>, estableced la paz entre ellos; si, <em>después de eso, </em>uno de ellos trasgrede contra el otro, combatid al grupo trasgresor hasta que vuelva al mandamiento de Al-lah. Luego, si retorna, estableced la paz entre ellos con equidad, y actuad con justicia. En verdad, Al-lah ama al justo." WHERE ayatNumber = 10 AND chapterNumber = 49;
UPDATE verses SET es = "En verdad, <em>todos </em>los creyentes son hermanos. Estableced, pues, la paz entre hermanos, y temed a Al-lah para que se os muestre misericordia." WHERE ayatNumber = 11 AND chapterNumber = 49;
UPDATE verses SET es = "¡Oh vosotros, los creyentes! No permitáis que un pueblo se burle<em>de otro,</em> que puede ser mejor que ellos, ni que las mujeres <em>se burlen de otras</em> mujeres, que pueden ser mejores que ellas. o calumniéis a vuestra propia gente, ni os burléis unos de otros mediante motes. Malo es, <em>en verdad</em>, ganar una mala reputación después de <em>profesar</em> la fe; y quienes no se arrepienten son los malvados." WHERE ayatNumber = 12 AND chapterNumber = 49;
UPDATE verses SET es = "¡Oh vosotros, los creyentes! Evitad al máximo caer en las sospechas; pues algunas sospechas son ciertamente un pecado. No espiéis ni murmuréis de los demás. ¿Le gustaría a alguno de vosotros comer la carne de su hermano difunto? Cierto que os repugnaría. Y temed a l-lah, pues en verdad Al-lah es el Remisorio <em>con compasión, y es</em> Misericordioso." WHERE ayatNumber = 13 AND chapterNumber = 49;
UPDATE verses SET es = "¡Oh, humanos! Os hemos creado de varón y hembra; y os hemos constituido en clanes y tribus para que os reconozcáis mutuamente. En verdad, el más honorable de entre vosotros, a la vista de Al-lah, es el más justo de vosotros. Ciertamente Al-lah es Omnisciente, Conocedor de todo." WHERE ayatNumber = 14 AND chapterNumber = 49;
UPDATE verses SET es = "Los árabes del desierto dicen: “Creemos”. Diles: “<em>Aún</em> no habéis creído; sino decid más bien: “Hemos aceptado el Islam; pues la fe <em>auténtica</em> no ha entrado aún en vuestros corazones”. Mas si obedecéis a Al-lah y a Su Mensajero, Él no restará mérito alguno a vuestras obras. En verdad, Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 15 AND chapterNumber = 49;
UPDATE verses SET es = "Sólo son creyentes quienes creen <em>sinceramente</em> en Al-lah y en Su Mensajero y después no dudan, sino que se esfuerzan con todo lo que poseen y con sus personas por la causa de Al-lah. Ellos son los veraces." WHERE ayatNumber = 16 AND chapterNumber = 49;
UPDATE verses SET es = "Diles: “¿Pretendéis enseñar a Al-lah con vuestra fe, cuando Al-lah conoce cuanto hay en los cielos y cuanto hay en la tierra, y conoce perfectamente todas las cosas?”." WHERE ayatNumber = 17 AND chapterNumber = 49;
UPDATE verses SET es = "Piensan que te han hecho un favor abrazando el Islam. Diles: “No hagáis alarde de vuestra aceptación del Islam como <em>si fuera</em> un favor para mí. Bien al contrario, sois vosotros a quienes Al-lah ha avorecido guiándoos a la fe verdadera<em>,</em> si sois veraces <em>en vuestra afirmación de ser creyentes</em>”." WHERE ayatNumber = 18 AND chapterNumber = 49;
UPDATE verses SET es = "En verdad, Al-lah conoce los secretos de los cielos y la tierra. Y Al-lah ve todo lo que hacéis." WHERE ayatNumber = 19 AND chapterNumber = 49;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 50;
UPDATE verses SET es = "Qaf. <em>Presentamos</em> al Glorioso Corán <em>como testigo de tu verdad</em>." WHERE ayatNumber = 2 AND chapterNumber = 50;
UPDATE verses SET es = "Se asombran de que les haya llegado un Amonestador de entre ellos. Y los incrédulos dicen: “¡Qué cosa más extraña!”." WHERE ayatNumber = 3 AND chapterNumber = 50;
UPDATE verses SET es = "“¡Cómo! <em>¿Seremos de nuevo resucitados</em> cuando hayamos muerto nos hayamos convertido en polvo? Es un retorno muy lejos <em>de lo posible”.</em>" WHERE ayatNumber = 4 AND chapterNumber = 50;
UPDATE verses SET es = "Sabemos lo que la tierra les arrebata, pues poseemos un Libro que lo preserva <em>todo.</em>" WHERE ayatNumber = 5 AND chapterNumber = 50;
UPDATE verses SET es = "¡No!, rechazaron la verdad cuando les llegó, por lo que están en un estado de confusión." WHERE ayatNumber = 6 AND chapterNumber = 50;
UPDATE verses SET es = "¿No han contemplado acaso el firmamento que está sobre ellos, cómo lo hicimos y lo adornamos, sin que hubiese grieta alguna en él?" WHERE ayatNumber = 7 AND chapterNumber = 50;
UPDATE verses SET es = "Y a la tierra, la hemos dilatado y colocado en ella montañas firmes; y hemos hecho crecer en ella toda <em>clase de </em>bellas especies," WHERE ayatNumber = 8 AND chapterNumber = 50;
UPDATE verses SET es = "Como <em>medio</em> <em>de</em> ilustración y recordatorio para cada siervo que vuelve<em> a Dios.</em>" WHERE ayatNumber = 9 AND chapterNumber = 50;
UPDATE verses SET es = "Hemos enviado el agua del cielo llena de bendiciones, produciendo con ella jardines y cosechas de grano," WHERE ayatNumber = 10 AND chapterNumber = 50;
UPDATE verses SET es = "Altas palmeras, con racimos apilados uno sobre otro," WHERE ayatNumber = 11 AND chapterNumber = 50;
UPDATE verses SET es = "Como sustento para <em>Nuestros</em> siervos; pues de ese modo vivificamos una tierra muerta. Así será la Resurrección." WHERE ayatNumber = 12 AND chapterNumber = 50;
UPDATE verses SET es = "El pueblo de Noé rechazó <em>la verdad</em> antes de ellos y <em>también lo hizo</em> el Pueblo del Pozo y los Zamud," WHERE ayatNumber = 13 AND chapterNumber = 50;
UPDATE verses SET es = "Igualmente Ad, el Faraón y los hermanos de Lot," WHERE ayatNumber = 14 AND chapterNumber = 50;
UPDATE verses SET es = "Los Habitantes del Bosque y el pueblo de Tubba. Todos <em>ellos</em> rechazaron a los Mensajeros con el resultado de que Mi castigo <em>previamente</em> advertido cayó sobre <em>ellos.</em>" WHERE ayatNumber = 15 AND chapterNumber = 50;
UPDATE verses SET es = "¿Acaso Nos cansamos con la primera creación? No, pero están confundidos sobre la nueva creación." WHERE ayatNumber = 16 AND chapterNumber = 50;
UPDATE verses SET es = "Pues en verdad, hemos creado al hombre y sabemos lo que su ego le susurra, y estamos más cerca de él <em>incluso que su </em>vena yugular." WHERE ayatNumber = 17 AND chapterNumber = 50;
UPDATE verses SET es = "Cuando los dos <em>ángeles</em> Registradores anoten<em>las acciones de los hombres, uno</em> sentado a la derecha y <em>el otro</em> a la izquierda," WHERE ayatNumber = 18 AND chapterNumber = 50;
UPDATE verses SET es = "No musita una sola palabra, sin que haya junto a él un <em>ángel de la </em>guarda preparado <em>para registrarla.</em>" WHERE ayatNumber = 19 AND chapterNumber = 50;
UPDATE verses SET es = "Pues el estupor de la muerte llega ciertamente. “De esto es de lo que intentabas escapar”." WHERE ayatNumber = 20 AND chapterNumber = 50;
UPDATE verses SET es = "Sonará la trompeta: “Éste es el Día de la Promesa”." WHERE ayatNumber = 21 AND chapterNumber = 50;
UPDATE verses SET es = "Cada alma se adelantará <em>y</em> junto con ella habrá un <em>ángel</em> para conducirla y <em>un</em> <em>ángel</em> para actuar de testigo." WHERE ayatNumber = 22 AND chapterNumber = 50;
UPDATE verses SET es = "“Te hallabas descuidado respecto a esto; mas ahora te hemos retirado el velo, y tu vista es penetrante en este día”." WHERE ayatNumber = 23 AND chapterNumber = 50;
UPDATE verses SET es = "Su compañero dirá: “Esto es lo que he preparado <em>del registro”.</em>" WHERE ayatNumber = 24 AND chapterNumber = 50;
UPDATE verses SET es = "“Precipitad ambos al Infierno, a todo ingrato enemigo <em>de la verdad”,</em>" WHERE ayatNumber = 25 AND chapterNumber = 50;
UPDATE verses SET es = "“Que impide el bien, trasgresor, escéptico”," WHERE ayatNumber = 26 AND chapterNumber = 50;
UPDATE verses SET es = "“Que instaura a otro Dios en lugar de Al-lah. Así pues, arrojadle ambos al espantoso tormento”." WHERE ayatNumber = 27 AND chapterNumber = 50;
UPDATE verses SET es = "Su acompañante dirá: “Oh, Señor nuestro, no fui yo quien le hizo rebelarse; sino que <em>él mismo</em> llegó demasiado lejos en el error”." WHERE ayatNumber = 28 AND chapterNumber = 50;
UPDATE verses SET es = "<em>Dios</em> dirá: “No disputéis en Mi presencia, pues previamente os hice la advertencia”." WHERE ayatNumber = 29 AND chapterNumber = 50;
UPDATE verses SET es = "“La declaración no <em>puede</em> ser cambiada ante Mi presencia, y Yo no soy en absoluto injusto con Mis siervos”." WHERE ayatNumber = 30 AND chapterNumber = 50;
UPDATE verses SET es = "En ese día diremos al Infierno: “¿Estás lleno?” y él responderá: “¿Hay más?”." WHERE ayatNumber = 31 AND chapterNumber = 50;
UPDATE verses SET es = "Y el Cielo será acercado al justo, y no estará ya lejano," WHERE ayatNumber = 32 AND chapterNumber = 50;
UPDATE verses SET es = "“Esto es lo que se os prometió: a quien ha vuelto siempre <em>a Dios</em> y vigilaba<em> sus acciones.</em>" WHERE ayatNumber = 33 AND chapterNumber = 50;
UPDATE verses SET es = "“Quien temía <em>al Dios</em> Clemente en privado y acudía <em>a Él</em> con corazón contrito”." WHERE ayatNumber = 34 AND chapterNumber = 50;
UPDATE verses SET es = "“Entrad allí en paz. Éste es el Día de la Eternidad”." WHERE ayatNumber = 35 AND chapterNumber = 50;
UPDATE verses SET es = "Allí tendrán todo lo que deseen, pues con Nosotros hay <em>mucho</em> más." WHERE ayatNumber = 36 AND chapterNumber = 50;
UPDATE verses SET es = "¡A cuántas generaciones más poderosas hemos destruido antes de ellos! <em>Pero cuando llegó el castigo,</em> se esparcieron por la tierra. ¿Hubo acaso algún lugar de refugio<em> para ellos?</em>" WHERE ayatNumber = 37 AND chapterNumber = 50;
UPDATE verses SET es = "En esto hay, en verdad, un aviso para quien tiene corazón, presta oídos y está atento." WHERE ayatNumber = 38 AND chapterNumber = 50;
UPDATE verses SET es = "Pues ciertamente creamos los cielos y la tierra y todo lo que hay entre ellos en seis períodos, y el cansancio no Nos afectó." WHERE ayatNumber = 39 AND chapterNumber = 50;
UPDATE verses SET es = "Soporta, pues, con paciencia lo que dicen y glorifica a tu Señor on <em>Sus</em> alabanzas, antes de la salida del sol y antes de <em>su </em>ocaso;" WHERE ayatNumber = 40 AND chapterNumber = 50;
UPDATE verses SET es = "Y glorifícalo <em>también</em> en <em>una parte de</em> la noche, así como después de las postraciones <em>prescritas.</em>" WHERE ayatNumber = 41 AND chapterNumber = 50;
UPDATE verses SET es = "¡Y escucha! El día en que el convocador haga surgir la llamada desde lugares cercanos." WHERE ayatNumber = 42 AND chapterNumber = 50;
UPDATE verses SET es = "El Día en que en verdad oigan el clamor; ese será el día de la salida <em>de las tumbas</em>." WHERE ayatNumber = 43 AND chapterNumber = 50;
UPDATE verses SET es = "En verdad, somos Nosotros quienes damos la vida y causamos la muerte, y hacia Nosotros es el retorno<em> final.</em>" WHERE ayatNumber = 44 AND chapterNumber = 50;
UPDATE verses SET es = "<em>En</em> el día en que la tierra se rompa en pedazos por encima de ellos <em>y salgan</em> apresuradamente: ésa será la reunión de todos, muy fácil para Nosotros." WHERE ayatNumber = 45 AND chapterNumber = 50;
UPDATE verses SET es = "Sabemos muy bien lo que dicen; mas tú no has sido <em>nombrado</em> para obligarles <em>de manera alguna.</em> Amonesta, pues, por medio del Corán a quien teme Mi advertencia." WHERE ayatNumber = 46 AND chapterNumber = 50;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 51;
