UPDATE verses SET es = "<em>Será</em> el día en que sean atormentados en el Fuego." WHERE ayatNumber = 14 AND chapterNumber = 51;
UPDATE verses SET es = "“Probad la consecuencia de vuestra perversidad. Esto es lo que intentabais apresurar”." WHERE ayatNumber = 15 AND chapterNumber = 51;
UPDATE verses SET es = "En verdad, el justo estará en medio de jardines y manantiales," WHERE ayatNumber = 16 AND chapterNumber = 51;
