UPDATE verses SET es = "Por el Señor del cielo y la tierra, esto es ciertamente la verdad, tan <em>cierto como que </em>habláis." WHERE ayatNumber = 24 AND chapterNumber = 51;
UPDATE verses SET es = "¿Te ha llegado la historia de los honorables huéspedes de Abraham?" WHERE ayatNumber = 25 AND chapterNumber = 51;
UPDATE verses SET es = "Cuando se presentaron a él y dijeron: “¡Paz!”. Él respondió: “¡Paz!”. <em>Eran</em> todos extraños." WHERE ayatNumber = 26 AND chapterNumber = 51;
UPDATE verses SET es = "Se fue silenciosamente a su hogar y trajo un ternero cebado," WHERE ayatNumber = 27 AND chapterNumber = 51;
UPDATE verses SET es = "Y lo colocó ante ellos. Dijo: “¿No coméis?”." WHERE ayatNumber = 28 AND chapterNumber = 51;
UPDATE verses SET es = "Mas sintió desconfianza de ellos. Le dijeron: “No temas”. Y le dieron la buena nueva <em>del nacimiento de</em> un hijo erudito." WHERE ayatNumber = 29 AND chapterNumber = 51;
UPDATE verses SET es = "Entonces se presentó su mujer gritando y abofeteándose el rostro y dijo: “¡Una anciana estéril!”." WHERE ayatNumber = 30 AND chapterNumber = 51;
UPDATE verses SET es = "Ellos dijeron: “Aún así lo ha dicho tu Señor. En verdad, Él es el sabio, el Omnisciente." WHERE ayatNumber = 31 AND chapterNumber = 51;
