UPDATE verses SET es = "Respondieron: “Hemos sido enviados a un pueblo pecador," WHERE ayatNumber = 33 AND chapterNumber = 51;
UPDATE verses SET es = "“Para lanzar sobre ellos piedras de barro," WHERE ayatNumber = 34 AND chapterNumber = 51;
UPDATE verses SET es = "“Marcadas, de parte de tu Señor, para los culpables de excesos”," WHERE ayatNumber = 35 AND chapterNumber = 51;
