UPDATE verses SET es = "Que no sólo no dejó nada de lo que azotó, sino que lo convirtió en algo similar a un hueso podrido." WHERE ayatNumber = 43 AND chapterNumber = 51;
UPDATE verses SET es = "Y <em>también hubo un Signo</em> en <em>la tribu de</em> Zamud, cuando se les dijo: “Disfrutad durante algún tiempo”." WHERE ayatNumber = 44 AND chapterNumber = 51;
UPDATE verses SET es = "Mas se rebelaron contra el mandamiento de su Señor. Por eso el rayo cayó sobre ellos mientras miraban;" WHERE ayatNumber = 45 AND chapterNumber = 51;
UPDATE verses SET es = "Y no pudieron levantarse ni socorrerse." WHERE ayatNumber = 46 AND chapterNumber = 51;
