UPDATE verses SET es = "No dicen: ¡<em>Es</em> un poeta!; ¡Esperamos alguna desgracia que el tiempo traerá sobre él!”." WHERE ayatNumber = 31 AND chapterNumber = 52;
UPDATE verses SET es = "Diles: “¡Esperad, pues! Yo estoy <em>también</em> con vosotros entre quienes esperan”." WHERE ayatNumber = 32 AND chapterNumber = 52;
UPDATE verses SET es = "¿Son inducidos por su intelecto <em>y su razón</em> o es que son un pueblo rebelde?" WHERE ayatNumber = 33 AND chapterNumber = 52;
UPDATE verses SET es = "¿Por ventura dicen: “¿Lo ha inventado?”. No, pero no creerán." WHERE ayatNumber = 34 AND chapterNumber = 52;
UPDATE verses SET es = "¡Que presenten, pues, una exposición como ésta, si son veraces!" WHERE ayatNumber = 35 AND chapterNumber = 52;
