UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 57;
UPDATE verses SET es = "Cuanto hay en los cielos y en la tierra glorifica a Al-lah; pues Él es el Poderoso, el Sabio." WHERE ayatNumber = 2 AND chapterNumber = 57;
