UPDATE verses SET es = "Dejadme, pues,<em> sólo</em> con quienes rechazan esta palabra<em>Nuestra.</em> Los llevaremos paso a paso <em>muy</em> <em>cerca de la destrucción,</em> desde donde menos sospechan." WHERE ayatNumber = 45 AND chapterNumber = 68;
UPDATE verses SET es = "Les doy un respiro; pues Mi plan es poderoso." WHERE ayatNumber = 46 AND chapterNumber = 68;
