UPDATE verses SET es = "“Para que sirváis a Al-lah, Le temáis y me obedezcáis”." WHERE ayatNumber = 4 AND chapterNumber = 71;
UPDATE verses SET es = "“Él os perdonará vuestros pecados y os concederá un respiro hasta el momento fijado. En verdad, el momento fijado por Al-lah no podrá retrasarse cuando llegue, ¡ojalá lo supierais!”" WHERE ayatNumber = 5 AND chapterNumber = 71;
