UPDATE verses SET es = "“Y han forjado un plan poderoso”." WHERE ayatNumber = 23 AND chapterNumber = 71;
UPDATE verses SET es = "“Diciéndose <em>entre sí</em>: “No abandonéis a vuestros dioses <em> en ninguna circunstancia.</em> No abandonéis a Wadd, Suwa, Yaghuz, Yauq ni Nasr”." WHERE ayatNumber = 24 AND chapterNumber = 71;
