UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 73;
UPDATE verses SET es = "Oh, tú, que te has envuelto en un manto," WHERE ayatNumber = 2 AND chapterNumber = 73;
