UPDATE verses SET es = "Soporta con paciencia todo lo que dicen; y aléjate de ellos con elegancia." WHERE ayatNumber = 11 AND chapterNumber = 73;
UPDATE verses SET es = "Y déjame sólo con quienes rechazan <em>la verdad,</em> poseedores de comodidades y confort; y dales un pequeño respiro." WHERE ayatNumber = 12 AND chapterNumber = 73;
